INSERT INTO `npc_text_localized` VALUES ('1262', 'ruRU', 'Ты $gпохож:похожа; на кузнеца. Под ногтями копоть, в костях сталь. Да, в твоих жилах течет кровь настоящих кузнецов… Стало быть, учиться $gпришел:пришла;? Ну что ж, $gпарень:девица;, бери молоток. Посмотрим, чему я сумею тебя выучить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
