INSERT INTO `npc_text_localized` VALUES ('4135', 'ruRU', '', 'Рад встрече. Я офицер Чистосердая, и я горжусь тем, что состою во всемирной организации под названием Серебряный Рассвет. Наша цель очень проста – быстро и безжалостно уничтожать любое возникшее в Азероте зло. Наша организация не политическая и никогда не будет ею. Мы считаем и Альянс, и Орду нашими друзьями и соратниками в борьбе со злом!$b$bЗа Рассвет, $gбрат:сестра;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4193', 'ruRU', 'Серебряный Рассвет приветствует тебя, |3-6($c)! Меня зовут Искра Света, и я полномочный интендант Рассвета по обмундированию и материалам.$B$BПока ты сражаешься со злом под нашим командованием, тем самым завоевывая наше доверие и уважение, офицер Чистосердая будет вручать тебе знаки доблести. Эти знаки служат двум целям: используются для улучшения своего положения в наших рядах или для покупки особых предметов.$B$BЧем больше тебя ценят в Серебряном Рассвете, тем больше станет доступных тебе предметов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4213', 'ruRU', '', 'Здравствуйте, |3-6($c). Я Миранда Бричлок, одна из немногих официальных поставщиков экипировки и оружия для Серебряного рассвета.$B$BСражаясь со злом в наших рядах, вы будете получать знаки доблести у герцога Зверенхоффа. Эти знаки можно использовать для продвижения по службе или обменивать на ценные вещи.$B$BЧем выше ваше звание в рядах Рассвета, тем больше разных вещей вы можете приобрести.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4233', 'ruRU', 'Здравствуй, |3-6($c).$B$BСтражи позволили мне остаться в лагере, если я буду сидеть тихо и никому не мешать. Ну, ты же понимаешь, дипломатия – наше все.$B$BЯ провожу исследования возмущений земли, а им это на руку, так что мне разрешили тут быть под соусом \"укрепления шаткого союза между расами Азерота\". Хочешь мне помочь? Я только рад. Только прошу, сиди тихо и никому не мешай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
