INSERT INTO `npc_text_localized` VALUES ('10790', 'ruRU', '', 'Я не пойму, зачем ты хочешь научиться чему-то еще… но, полагаю, что смогу указать тебе нужное направление. Что за обучение тебе нужно, конкретнее?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10791', 'ruRU', 'Опасайся порождений Эфириума, окруживших нас со всех сторон.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10792', 'ruRU', 'Пойдем, и я покажу тебе, как ездить верхом на лучших животных, которых тебе доведется увидеть. У тебя, конечно, хватит золота, чтобы заплатить за это?', 'Пойдем, и я покажу тебе, как ездить верхом на лучших животных, которых тебе доведется увидеть. У тебя, конечно, хватит золота, чтобы заплатить за это?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10793', 'ruRU', '', 'Добро пожаловать в Вечную рощу, |3-6($c). Времион и я уже некоторое время изучаем араккоа в Острогорье, и мы обнаружили несколько тревожных различий между ними и их родственниками в Тероккаре.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10797', 'ruRU', 'Грок искать помощь. $gМаленький:Маленькая:r; |3-6($r), идти в горы и помогать ограм Кровавого Молота!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10798', 'ruRU', 'Ты идешь смотреть на Могдорна?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10800', 'ruRU', 'Сыны Груула должны быть побеждены, если мы хотим сбросить наши оковы и вновь жить как свободные огры!$B$BМы должны работать бесшумно, но быстро, если хотим, чтобы наше подпольное движение сопротивления получило шанс на успех.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10808', 'ruRU', '', 'Ты можешь знать пару-другую вещей о том, как обращаться с животными, но ты когда-нибудь $gвзмывал:взмывала; к небесам? Мы, Громовые Молоты, знаем, как обгонять ветер верхом на наших лучших питомцах. За небольшую плату, если у тебя кишка не тонка, я научу тебя этому, как надо.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10809', 'ruRU', 'Что ты представляешь себе, когда вспоминаешь Громовой Молот, а? Уж наверное, всадников на грифонах! Долина Призрачной Луны – не Заоблачный пик, но и наши грифоны по-своему хороши. Взгляни на моих красавцев – и увидишь, как один из них поймает твой взгляд!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13662', 'ruRU', '', 'Рада встрече, сестра по оружию. Я могу поставить в стойло твоих питомцев или помочь тебе вернуть павших товарищей.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10817', 'ruRU', 'Похоже, ты знаешь, как обращаться с животными, летающими над землей, но если ты хочешь стать хорошим пилотом, тебе придется самоотверженно тренироваться. И платить деньги. У тебя ведь есть деньги, да?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10818', 'ruRU', '', 'Прекрасные животные, правда? Взращенные нами виверны обучены справляться с самыми жесткими и странными погодными условиями, что только бывают в Дреноре. Ты убедишься в надежности и отваге этих животных.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10819', 'ruRU', 'Мне все равно, кто ты и что делаешь вне Стражи Небес, но пока ты здесь, ты будешь делать то, что тебе говорят!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10821', 'ruRU', 'Когда я вырасту, я хочу стать кожевником!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10842', 'ruRU', 'Предсказателя Джавада ищите на восточной стороне Великой Кузни, это чуть к северу по восточной стороне. Все ясно?', 'Предсказателя Джавада ищите на восточной стороне Великой Кузни, это чуть к северу по восточной стороне. Все ясно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10843', 'ruRU', 'Наставника Огненора можно найти среди советников Тралла в крепости Громмаш, в Аллее Мудрости.', 'Наставника Огненора можно найти среди советников Тралла в крепости Громмаш, в Аллее Мудрости.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10846', 'ruRU', 'Я могу обучить вас ювелирному делу. Не угодно ли?', 'Я могу обучить вас ювелирному делу. Не угодно ли?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10851', 'ruRU', '*Ксири окружает мерцающий синий свет. Наару приветствует вас как $gсоюзника:союзницу; на поле боя.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10864', 'ruRU', 'У меня нет ничего нового для тебя, $gпарень:девушка;. Ты уже слишком $gумен:умна; для физического труда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10872', 'ruRU', '', 'Давайте к делу, $n. Я скажу вам, что знаю, но не болтайте об этом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10884', 'ruRU', '', 'У меня есть для тебя предложение, |3-6($r). Пожалуйста, подумай хорошенько – это решение непросто будет отменить.$B$BНастало время официально оформить твою преданность нашей организации. Мне надо знать, в каком качестве ты хочешь служить нам, чтобы мы могли вознаградить тебя как следует.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10886', 'ruRU', 'Добро пожаловать в \"Почтенное ателье Килена\". Я думаю, мой магазин тебе подойдет. А если хочешь у меня поучиться – только скажи, я с радостью тебя обучу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10892', 'ruRU', 'Ты выглядишь $gзнакомым:знакомой;…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10894', 'ruRU', 'Если тебе нужны реагенты, ты $gпришел:пришла; по адресу. У меня лучший выбор во всем Шаттрате.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10896', 'ruRU', '*Грязевик полирует свои ботинки.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10903', 'ruRU', 'Проклинаю тебя тысячу раз, |3-6($r)! Эта тюрьма не задержит меня надолго!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10906', 'ruRU', 'Что, нравится моя повязка на глазу? Что ж, позаботься о том, чтобы $gсамому:самой; не начать носить такую же, $gмальчонка:девчонка;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10907', 'ruRU', '', 'А? Что? Я тут немного занята, дружище!$B$BКонечно, если ты здесь, чтобы помочь, у меня полно времени.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10923', 'ruRU', 'Гахк новый в Огри\'ле – как и $gмаленький:маленькая:r; |3-6($r). Это как небеса, о которых всегда говорят огры внизу. Но головы Гахка болят от кристалла, который делает Гахк умнее.$B$B*Обе головы Гахка кивают друг другу.*$B$BНаша учиться делать кристалл с темными рунами. Когда наша это уметь, мы разбить порталы демонов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10926', 'ruRU', 'Добро пожаловать в Огри\'лу, |3-6($c).$B$BЗдешние огры смогли обрести большую остроту мышления благодаря магическим эманациям здешних кристаллов. Хотя мы не вполне понимаем, как они работают, мы стали ценить наше новое место обитания и хотим лишь жить спокойно.$B$BЖаль лишь, что демоны, духи Астрала и в особенности черные драконы не дают нам этого самого покоя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10929', 'ruRU', 'Нет. Ты еще недостаточно $gсилен:сильна;, чтобы участвовать в соревнованиях. Но ты можешь найти распорядителя арены и поучаствовать в тренировочных схватках. Никогда не рано начать тренироваться.', 'Нет. Ты еще недостаточно $gсилен:сильна;, чтобы участвовать в соревнованиях. Но ты можешь найти распорядителя арены и поучаствовать в тренировочных схватках. Никогда не рано начать тренироваться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10936', 'ruRU', 'Кенарийская экспедиция спасла меня от верной смерти в копях. Я обязан им жизнью! Я предлагаю услуги по ремонту и продаже реагентов союзникам Кенарийской экспедиции.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10937', 'ruRU', 'Ты найдешь чем заняться здесь, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10939', 'ruRU', '', 'Мы должны вернуть контроль над этими шахтами!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10957', 'ruRU', 'Забудь ты о Викстоне. Хочешь отличный товар? Поговори с Крикселем. У меня есть все, что тебе нужно, если, конечно, у тебя репутация подходящая. Ну, на арене. Товар-то мой не для трусов разных, $gпарень:девушка;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10967', 'ruRU', 'Чем мы можем тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10976', 'ruRU', 'Какие яства ты желаешь отведать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10980', 'ruRU', 'Доброго тебе дня. Когда ты станешь чуть повыше чином у Стражи Небес Ша\'тар, я смогу предложить тебе полет без пересадок к заставе Стражи Небес, нашу базу на вершине Острогорья.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11000', 'ruRU', 'Что ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11017', 'ruRU', 'У тебя есть сталь, нуждающаяся в ковке?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11029', 'ruRU', 'Вам нравится у нас в Огри\'ле?$B$BНаши огры и днем и ночью делают всякие интересности, и все самое лучшее мы сохраняем для почетных гостей – таких как вы!$B$BТак что не забывайте иногда отдыхать от спасения мира и заглядывайте к нам – посмотреть на товары!$B$BУдачи, $gпарнишка:девчушка;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11042', 'ruRU', 'Уйди. Для таких, как ты, у меня ничего нет.', 'Уйди. Для таких, как ты, у меня ничего нет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11045', 'ruRU', 'Какие тайны нашего ремесла открыть тебе сегодня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11056', 'ruRU', 'Мы готовим штурм Лагеря Баш\'ира, чтобы изучить их Хрустальную кузницу. Помоги нам, если можешь! Наш эфир-тех Стражи Небес отправится примерно через $4581d.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11059', 'ruRU', 'Нужны припасы для похода в поле?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11064', 'ruRU', 'Приветствую, $gпутешественник:путешественница;.', 'Приветствую, $gпутешественник:путешественница;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11070', 'ruRU', 'Разучиться шитью из тенеткани не так-то просто. Если решишь забыть этот навык, то забудешь и все выкройки, для которых он нужен!$b$bТы абсолютно $gуверен:уверена;?', 'Разучиться шитью из тенеткани не так-то просто. Если решишь забыть этот навык, то забудешь и все выкройки, для которых он нужен!$b$bТы абсолютно $gуверен:уверена;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11078', 'ruRU', 'Лакеи Иллидана все еще стерегут эти залы, $N. Когда они падут, мои силы позволят мне перенести тебя дальше в храм.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11081', 'ruRU', 'Я чувствую, что ты $gрасчистил:расчистила; тропинку к моим собратьям. Моя связь с ними и храмом все еще сильна. Ты хочешь зайти глубже?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11082', 'ruRU', 'Мои собратья недавно были здесь. Моя связь с ними стала непрочной. Во дворе – источник большой силы, и он препятствует моему продвижению.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11083', 'ruRU', 'Мои товары доступны лишь самым настойчивым преследователям приспешников Иллидана.$B$BВы станете $gтаким:такой; только тогда, когда вас будут превозносить Ша\'тар, Кенарийская экспедиция и Алдоры.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11084', 'ruRU', 'Мои товары доступны лишь самым настойчивым преследователям приспешников Иллидана.$B$BВы станете $gтаким:такой; только тогда, когда вас будут превозносить Ша\'тар, Кенарийская экспедиция и Провидцы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11085', 'ruRU', 'Добро пожаловать, $N. Если вы хотите купить у меня что-нибудь, то нужно будет принести мне знаки отличия самых могущественных прислужников Иллидана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11089', 'ruRU', 'Невидимые араккоа… они вокруг нас… следят за нами… ждут!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11126', 'ruRU', 'Мы обязаны раскрыть это преступление. Учитывая местоположение таверны и количество жертв, дело очень щекотливое. Исследуй место преступления как можно осторожнее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11128', 'ruRU', 'Я приму вашу помощь в этом расследовании, |3-6($c). Но все ваши мотивы и открытия будут проверяться самым строжайшим образом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11136', 'ruRU', 'Мне кажется, что вы очень $gотзывчивый:отзывчивая:c; |3-6($c). Вы помните славные дни Терамора – еще до того, как Джайна заставила нас прекратить войну с Ордой? Вы тоже устали держать свой клинок в ножнах, пока орки продолжают провоцировать нас?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11165', 'ruRU', 'Дирижабль должен как раз долететь до Оргриммара.', 'Дирижабль должен как раз долететь до Оргриммара.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11167', 'ruRU', 'Дирижабль, должно быть, только что долетел до Гром\'гола…', 'Дирижабль, должно быть, только что долетел до Гром\'гола…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11170', 'ruRU', 'Дирижабль должен как раз отбыть из Оргриммара…', 'Дирижабль должен как раз отбыть из Оргриммара…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11172', 'ruRU', 'Дирижабль должен как раз отбыть из Гром\'гола…', 'Дирижабль должен как раз отбыть из Гром\'гола…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11173', 'ruRU', 'Дирижабль, должно быть, только что прибыл в Подгород…', 'Дирижабль, должно быть, только что прибыл в Подгород…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11174', 'ruRU', 'Дирижабль должен как раз отбыть из Оргриммара…', 'Дирижабль должен как раз отбыть из Оргриммара…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11175', 'ruRU', 'Дирижабль должен как раз отбыть из Подгорода.', 'Дирижабль должен как раз отбыть из Подгорода.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11181', 'ruRU', 'Дирижабль должен как раз отбыть из Гром\'гола…', 'Дирижабль должен как раз отбыть из Гром\'гола…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11189', 'ruRU', 'Я день-деньской собираю части разбитого дирижабля, а Фрезза даже не установит мне почасовую плату! Но вы же здесь не за этим, да? Вам ведь нужен дирижабль. Ну что ж, на ваше счастье, у меня есть специальное устройство, отслеживающее их!', 'Я день-деньской собираю части разбитого дирижабля, а Фрезза даже не установит мне почасовую плату! Но вы же здесь не за этим, да? Вам ведь нужен дирижабль. Ну что ж, на ваше счастье, у меня есть специальное устройство, отслеживающее их!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11209', 'ruRU', 'Лучшего пива ты не найдешь во всем Азероте.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11210', 'ruRU', 'Мы, Громовары, гордимся тем, что наша пивоварня – лучшая на свете.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11211', 'ruRU', 'Ух ты! Ты вправду хочешь что-то купить у нас?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11215', 'ruRU', 'Ты что, не видишь, что мне надо кое-куда сходить и кое с кем повидаться? Отойди, хам!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11217', 'ruRU', 'Я полагаю, ты знаешь, кто я такой. И все же стремишься меня побеспокоить?$B$BЧто у тебя такого, что непременно требует моего внимания?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11224', 'ruRU', '', 'Рада встрече, |3-6($c). Я учусь у Балдрика обращению с грифонами, и, надеюсь, когда-нибудь у меня будет своя ручная стая.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11226', 'ruRU', '', 'Привет, $gкрасавчик:красотка;! Чего ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11230', 'ruRU', '', 'Чего ты хочешь, $gмалыш:крошка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11231', 'ruRU', '', 'Я – стряпуха? Пожалуй, так. Официантке приходится время от времени работать на кухне, и за прошедшие годы я освоила несколько рецептов. Ничего особенного, но достаточно питательно, чтоб набить солидное брюхо Грока.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11234', 'ruRU', 'Мой брат Крикс думает, что нет ничего хуже работы в Оргриммаре. А тут такая вонь, что с ног сшибает! Но, по крайней мере, начальник приплачивает мне за вредность.\r\n\r\nЧто? Вы ищете дирижабль? Здорово, у меня как раз есть устройство для их отслеживания.', 'Мой брат Крикс думает, что нет ничего хуже работы в Оргриммаре. А тут такая вонь, что с ног сшибает! Но, по крайней мере, начальник приплачивает мне за вредность.\r\n\r\nЧто? Вы ищете дирижабль? Здорово, у меня как раз есть устройство для их отслеживания.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11243', 'ruRU', 'Будь настороже. Мы – первая линия обороны Терамора от множества врагов с этого болота.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11245', 'ruRU', 'Теперь, когда дезертиры захватили Разрушенную башню, самой далекой заставой Терамора осталась Северная башня.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11256', 'ruRU', 'Чего $gтакой:такая:r; |3-6($r), как вы, желает в такое время? И чем может помочь такой парень, как я?', '', 'Я чем-нить могу помочь-то, |3-6($c)?', '', 'Если хочешь помародерствовать – становись в очередь!$b$bЯ еще нескоро управлюсь…', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11257', 'ruRU', 'Да без понятия. Я в команде новичок.$b$bХотя, знаешь, молотить троллей да распивать грог мне уже надоело. Глянуть бы на сокровище, о котором Бадд соловьем разливается…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11258', 'ruRU', 'Команда Бадда, что ль? Да, деликатностью-то он не отличается. Но возьмет любого, кто работы не боится.$b$bНу, кроме вот нежити. Не любит их Бадд. Боится, стало быть.', '', 'Ага, никогда не знаешь, кто твой напарник и кому черед погибать.$b$bХочешь совет? Не заводи ни с кем дружбы.', '', 'Согласен. Бадд собрал всякий сброд со всех уголков мира.$b$bОно и понятно: когда речь идет о бесплатной выпивке, все мигом забывают о разногласиях.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11259', 'ruRU', 'Принюхивается и морщится$b$bВот он, запах приключений, |3-6($c)!', '', 'ик! Слушай, |3-6($r)! Ты $gсам:сама; пару дней поборись с дикими троллями, и я посмотрю, как от тебя будет пахнуть!', '', 'А-а, это. Видишь ли, в лагере сейчас осталось только орочье пойло.$b$bА его моя душа не принимает… понимаешь, да?', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11264', 'ruRU', 'Добро пожаловать на Сторожевой пост, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11279', 'ruRU', 'Слушай!$B$BЯ расскажу тебе все, что знаю, но сначала спаси меня от этих дикарей и их медведей!$B$BЛадно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11281', 'ruRU', 'Брогг рад, что гоблины Шестермути позволили ему остаться здесь, но иногда ему кажется, что они над ним смеются.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11283', 'ruRU', 'Ур-ра! Танзар свободен, киса!$B$BУдача! И даже клык не подрезан.$B$BПрекрасно, киса. Вот что я такое. Отрицать нет смысла.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11289', 'ruRU', 'Ладно, $Gприятель:мадам;, раз уж договорились…$b$bЭто было ночью, когда мы тайком пробрались в Зул\'Аман, чтобы там осмотреться. $b$bВсе было тихо, пока мы не услышали шорох – будто ветер прошелестел в ветвях. И не успели мы опомниться, как нас окружили эти дикари со своими зверюгами! На следующий день я очнулся в клетке.$b$bПленников убивали одного за другим, днем и ночью. И с каждой нашей смертью этот тролль-медведь набирался сил.$b$bВот все, что мне известно об этом месте. И больше я и знать не хочу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11295', 'ruRU', 'Этот тип – просто дурак!$b$bБогатство, слава? Тьфу! Это место – просто смертельная ловушка, да!$b$bЗа свою жизнь я повидал немало троллей, но эти, они не такие: незаметные, сильные, хитрые.$b$bМне показалось, что они были готовы к его приходу….', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11299', 'ruRU', 'Заметив твое приближение, Бадд закатывает глаза.$B$BА, да, будущая знаменитость $n! Чем обязан удовольствию находиться в вашем обществе?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11302', 'ruRU', 'Ну, ну, $N, расслабься, дыши глубже! Научись, наконец, доверять мне!$b$bТы не знаешь, как вести себя в таких ситуациях, поэтому паникуешь и преувеличиваешь. На самом деле ничего особо страшного тут нет.$b$bА теперь, пожалуйста, успокойся. Так ты всех остальных перепугаешь…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11304', 'ruRU', 'Что за глупый вопрос! Я явно тебя переоценил, $N. $B$BОчевидно же, что кто-то должен следить за общей картиной. Поскольку, увы, я единственный, кто хоть как-то для этого подходит, эта ноша досталась мне. $B$BПо правде говоря, |3-6($c), я тебе завидую. Я предпочел бы оказаться в Зул\'Амане и гнать этих дикарей. Можешь считать, что тебе повезло…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11309', 'ruRU', 'Если хочешь поговорить с учеником Гарионом, отправляйся к дому Табеты. Там он хотя бы не утащит все реагенты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11311', 'ruRU', 'Эх, сюда бы жреца или друида. С такими ранами мне учебу не продолжить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11313', 'ruRU', 'Погоди, не говори… $n, верно?$b$bЧем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11365', 'ruRU', 'На этой доске представители Хитрой Шестеренки в Шестермути размещают объявления о премиях и вакансиях.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11370', 'ruRU', 'И в самом деле! Я сделал для них все, что мог. Даже представить не могу, что там пошло не так.$b$bВот, |3-6($c), возьми. Я тут кое-что дополнил и улучшил, так что смотри, возвращайся с победой!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11376', 'ruRU', 'Если хочешь – можешь отойти. Потрошить рыбу – грязная работа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11403', 'ruRU', '', 'Все куда хуже, чем я думала! Мой дирижабль превратился в груду покореженного железа и горящего топлива!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11406', 'ruRU', '', 'Добро пожаловать в Терамор, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11428', 'ruRU', 'Вы пришли, чтобы меня сменить? Сейчас самое время. В общем, если что, дайте мне знать. Я бы сейчас с удовольствием пропустил пару стаканчиков.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11424', 'ruRU', 'Мне некогда говорить – разве что это срочно… Я отвечаю за врата Терамора.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11426', 'ruRU', 'Лейтенант Аден всегда вызывается добровольцем на сторожевой пост у ворот. Я не возражаю.$b$bПотому что с моего поста видно сразу все стратегические объекты: и стражу, и дочерей местных лавочников.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11432', 'ruRU', 'Слава Свету, что в Братстве Справедливости нырять совершенно не умеют!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11457', 'ruRU', 'Добро пожаловать в Терамор.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11458', 'ruRU', 'Какие чары интересуют тебя сегодня, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11459', 'ruRU', '', 'Может, показать тебе сегодня парочку выкроек?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11469', 'ruRU', 'Приятно познакомиться, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11492', 'ruRU', 'Добро пожаловать в Терамор, |3-6($c).', 'Добро пожаловать в Терамор, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11567', 'ruRU', 'Я никогда не назову Деревню Гиблотопь своим домом. Это значит – отказаться от надежды вернуть наши земли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11578', 'ruRU', 'Выходит, я тебя отблагодарить должен, |3-6($r).$b$bЯ-то думал, последняя кружка – и все, но тут ты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11580', 'ruRU', 'Грустная история, да и ясно-то в ней не все.$b$bНарод мой, знаешь ли, любит эль. Да и я не исключение. Но выпил я, видно, больше, чем следует.$b$bВстречаю я, значит, старого Бадда, а он мне и предлагает сколько угодно грога, если я к нему в команду пойду, – ох и долго же я потом об этом жалел.$b$bКак-то ночью, когда я высосал столько грогу, что и кодо бы с ног свалился, меня уболтали двинуть вместе с Баддовой бандой неудачников в Зул\'Аман.$b$bА потом я ничего не помню, очнулся уже в клетке.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11797', 'ruRU', 'Есть хочешь? Можешь здесь и поспать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11851', 'ruRU', 'Большое тебе спасибо, |3-6($c).$b$bЕсли когда чего где понадобится – ты только свистни!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11945', 'ruRU', 'Здорово, $gприятель:подруга;! Да, я лучший наездник на баранах, но это не значит, что я не стану помогать пивоварам. Ты тоже можешь помочь, если хочешь.$b$bДа, вот еще что!$b$bЕсли поможешь на Хмельном фестивале, то получишь возможность оседлать самых быстрых баранов на свете!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11947', 'ruRU', 'Ха-ха-ха, видишь? Жуль вернулся!!! Я снова в своей лавке, полной товаров, и готов заключать новые сделки!$b$bХочешь стать лучше, сильнее и быстрее? Любой из этих амулетов гарантированно наделит тебя небывалыми способностями. Подходи, выбирай и покупай! За эти сокровища и двойную цену заплатить не жалко!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12044', 'ruRU', 'Моих клиентов интересуют редкие и ценные вещи.$b$bВы могли бы их добыть, |3-6($c), а взамен получить то, что вам нужно.$b$bПоговорим о том, что мне нужно сегодня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12046', 'ruRU', 'Ветра Пустоты шепчут, и Ма\'дуун слышит их приказ.$b$bВетра шепчут, что к Ма\'дууну придет $gмужчина, которого:женщина, которую; нужно будет убить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12076', 'ruRU', 'У нас на фестивале пиво льется рекой, и бочонков много не бывает!$b$bМожешь забрать одного из моих баранов.$b$bЗа каждый принесенный мне бочонок ты получишь несколько купонов и возможность подольше ездить на баране.\r\n\r\nНо учти! Возьмешься за задание – и до завтрашнего дня больше ничего не поручу!\r\n\r\nНу что, решишься?', 'У нас на фестивале пиво льется рекой, и бочонков много не бывает!$b$bМожешь забрать одного из моих баранов.$b$bЗа каждый принесенный мне бочонок ты получишь несколько купонов и возможность подольше ездить на баране.\r\n\r\nНо учти! Возьмешься за задание – и до завтрашнего дня больше ничего не поручу!\r\n\r\nНу что, решишься?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12080', 'ruRU', 'Я понимаю, что многие пытаются закрутить роман в тавернах и на таких вот праздниках, однако алкоголь притупляет все чувства, особенно чувство опасности.$b$bНо у меня есть одно решение! После многих испытаний и неудач мне удалось разработать устройство, позволяющее наслаждаться всеми прелестями алкоголя без каких-либо дурных последствий! В этих очках КТО УГОДНО будет выглядеть красиво!$b$bМмм, даже ты! Для тебя я, пожалуй, сделаю скидку!', 'Я понимаю, что многие пытаются закрутить роман в тавернах и на таких вот праздниках, однако алкоголь притупляет все чувства, особенно чувство опасности.$b$bНо у меня есть одно решение! После многих испытаний и неудач мне удалось разработать устройство, позволяющее наслаждаться всеми прелестями алкоголя без каких-либо дурных последствий! В этих очках КТО УГОДНО будет выглядеть красиво!$b$bМмм, даже ты! Для тебя я, пожалуй, сделаю скидку!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12083', 'ruRU', 'Привет! Хочешь, расскажу о премудростях гонок на баранах?', 'Привет! Хочешь, расскажу о премудростях гонок на баранах?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12084', 'ruRU', 'Если возьмешься за какое-нибудь из этих заданий, мы одолжим тебе барана. Именно одолжим, потом его надо будет вернуть. Сначала баран идет медленно, но если ослабить поводья, он поскачет быстрее.$b$bНаши бараны любят яблоки. Мы расставили бочонки с яблоками вдоль дороги, так что не забывай у них останавливаться. Поев яблок, твой баран забудет об усталости.', 'Если возьмешься за какое-нибудь из этих заданий, мы одолжим тебе барана. Именно одолжим, потом его надо будет вернуть. Сначала баран идет медленно, но если ослабить поводья, он поскачет быстрее.$b$bНаши бараны любят яблоки. Мы расставили бочонки с яблоками вдоль дороги, так что не забывай у них останавливаться. Поев яблок, твой баран забудет об усталости.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12085', 'ruRU', 'Вместе с бараном ты получаешь поводья. И это тебе не дедулины подтяжки, ими надо уметь пользоваться!$b$bЧтобы баран прибавил шагу, ослабляй их раз за разом. Будешь ослаблять поводья дальше, и... оп! Баран мчится галопом!$b$bНо чем быстрей несется баран, тем скорее он устанет. Поэтому соблюдай меру.', 'Вместе с бараном ты получаешь поводья. И это тебе не дедулины подтяжки, ими надо уметь пользоваться!$b$bЧтобы баран прибавил шагу, ослабляй их раз за разом. Будешь ослаблять поводья дальше, и... оп! Баран мчится галопом!$b$bНо чем быстрей несется баран, тем скорее он устанет. Поэтому соблюдай меру.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12086', 'ruRU', 'Чтобы начать движение, ослабь поводья барана. Начинает он с шага. Ослабь поводья еще немного, и он перейдет на рысь. Ослабь поводья у рысящего барана, и он припустит легким галопом, а затем перейдет в галоп.', 'Чтобы начать движение, ослабь поводья барана. Начинает он с шага. Ослабь поводья еще немного, и он перейдет на рысь. Ослабь поводья у рысящего барана, и он припустит легким галопом, а затем перейдет в галоп.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12087', 'ruRU', 'Если слишком долго вести барана галопом или легким галопом, он выдохнется. Чтобы дать животному отдохнуть, пусти его шагом или рысью. Увидишь кадки с яблоками – подведи барана к ним: он сможет поесть и восстановить силы.', 'Если слишком долго вести барана галопом или легким галопом, он выдохнется. Чтобы дать животному отдохнуть, пусти его шагом или рысью. Увидишь кадки с яблоками – подведи барана к ним: он сможет поесть и восстановить силы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12088', 'ruRU', 'Бараньи скачки – это легче, чем может показаться, но они требуют умения управлять бараном. Просто рули с помощью поводьев, ешь яблоки, если можешь, и не изнуряй своего барана.', 'Бараньи скачки – это легче, чем может показаться, но они требуют умения управлять бараном. Просто рули с помощью поводьев, ешь яблоки, если можешь, и не изнуряй своего барана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12098', 'ruRU', 'Эй, $gприятель:подруга;, у меня тут такая вещица завалялась, единственная в своем роде! И я ее продаю, да совсем недорого!$b$bВидишь ли, надоело мне тратить деньги в тавернах и разыскивать идеальную спутницу жизни. И тогда я смастерил эти очки – сквозь них все просто красавицы! Вот увидишь!$b$bНу что, может, купишь, поможешь мне карманы-то набить слегка? Я тебе за красоту даже скидку сделаю!', 'Эй, $gприятель:подруга;, у меня тут такая вещица завалялась, единственная в своем роде! И я ее продаю, да совсем недорого!$b$bВидишь ли, надоело мне тратить деньги в тавернах и разыскивать идеального спутника жизни. И тогда я смастерила эти очки – сквозь них все просто красавцы! Вот увидишь!$b$bНу что, может, купишь, поможешь мне карманы-то набить слегка? Я тебе за красоту даже скидку сделаю!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12100', 'ruRU', 'А я тебя давно приметил, все ждал, когда же ты зайдешь! Итак, что ищешь, $gкрасавчик:милашка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12104', 'ruRU', 'Лучшей выпивки ты во всем Азероте не сыщешь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12105', 'ruRU', 'Эй, $gприятель:подруга;, не хочешь пропустить кружку лучшего пива в мире?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12113', 'ruRU', 'Авария между Колючим Холмом и этим местом сорвала поставки пива! Да еще и дворфы Черного Железа нападают каждый час или что-то около того!$B$BТак что, если хочешь помочь, вот мое предложение. Я одолжу тебе поводья и одного из моих баранов.$b$bКаждый раз, когда ты будешь привозить мне бочонок, я буду выдавать тебе несколько жетонов в награду за работу. Я даже позволю тебе подольше поездить на баране за каждый привезенный тобой бочонок!$b$bНО! Как только ты начнешь, ты не сможешь вновь этим заняться до завтрашнего дня.$b$bТы $gуверен:уверена;, что $gготов:готова; начать?', 'Авария между Колючим Холмом и этим местом сорвала поставки пива! Да еще и дворфы Черного Железа нападают каждый час или что-то около того!$B$BТак что, если хочешь помочь, вот мое предложение. Я одолжу тебе поводья и одного из моих баранов.$b$bКаждый раз, когда ты будешь привозить мне бочонок, я буду выдавать тебе несколько жетонов в награду за работу. Я даже позволю тебе подольше поездить на баране за каждый привезенный тобой бочонок!$b$bНО! Как только ты начнешь, ты не сможешь вновь этим заняться до завтрашнего дня.$b$bТы $gуверен:уверена;, что $gготов:готова; начать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12114', 'ruRU', 'Этих баранов мы купили в соответствии со всеми законами. Хочешь такого же? Хочешь окунуться в мир бараньих бегов? Что ж, никто не запрещает, но сначала придется поработать.$b$bВпрочем, работа больше на игру похожа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12134', 'ruRU', 'Ни одно празднество не сравнится с веселым разгулом Хмельного фестиваля!', 'Ни одно празднество не сравнится с веселым разгулом Хмельного фестиваля!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12135', 'ruRU', 'Вот тебе мудрый совет: отправляйся туда, откуда $gпришел:пришла;! Тут тебе не место для искателей веселых приключений, тут тебя сожрут и не подавятся.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12152', 'ruRU', 'Добро пожаловать на территорию Хмельного фестиваля! Развлечений тут с избытком, а главное – традиционный дух соперничества между пивоварнями. Если есть вопросы – задавай!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12154', 'ruRU', 'Хмельной фестиваль – это настоящий праздник для желудка! Мы подаем только лучшее пиво домашнего приготовления и самую вкусную еду. Ты только взгляни, сколько у нас всего: праздничные колбаски, крендельки, ароматные сыры. Лучшего не найдешь нигде, а как все дешево!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12155', 'ruRU', 'А когда наешься и напьешься вдоволь, переходи к следующей забаве – сувенирам! У Белби Быстросмен есть все и даже больше – от традиционных праздничных нарядов до мини-бочонков с отменным пивом. Но убедись сначала, что у тебя достаточно призовых жетонов Хмельного фестиваля – иначе сувениров не купишь! Получить призовые жетоны можно, помогая ребятам на территории Хмельного фестиваля.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12156', 'ruRU', 'Скачки на баранах – одна из давних традиций Фестиваля пива. Если хочешь узнать о них побольше, поговори с Полом Янтарленом, что стоит у бараньего загона. Веселья на скачках с избытком, а еще можно разжиться выигрышными жетонами Фестиваля пива!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12157', 'ruRU', 'Здравствуй и добро пожаловать на Хмельной фестиваль! Здесь ты точно не соскучишься, а захочешь о чем-то спросить – обращайся. Ну а если все и так понятно, то иди купи себе пива с крендельками!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12158', 'ruRU', 'На Хмельном фестивале все только и делают, что пьют да едят, ведь лучшей выпивки и яств во всем мире не сыщешь. Не веришь? Да только посмотри, сколько у нас пива, колбасок, крендельков да сыра. Здесь все самое вкусное – и почти задаром!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12159', 'ruRU', 'Лихие скачки на баранах – визитная карточка Хмельного фестиваля. Хочешь узнать побольше? Поговори с Дризом Неуклюгой, что стоит у бараньего загона. На скачках можно не только повеселиться от души, но и заработать призовые жетоны Хмельного фестиваля!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12160', 'ruRU', 'Эй-эй, кто ж уходит с Хмельного фестиваля без сувениров? У нас есть все и даже больше – от традиционных праздничных нарядов до мини-бочонков с отменным пивом. Поговори с Бликсом Чиништуксом, но убедись сначала, что у тебя достаточно призовых жетонов Хмельного фестиваля. Без них сувениров тебе не видать! Получить призовые жетоны можно, помогая ребятам на территории Хмельного фестиваля.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12162', 'ruRU', '', 'Здравствуй, и да пребудет с тобой Свет Элуны.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12165', 'ruRU', 'Говорю тебе, настоящая охота – только под хмельком!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12181', 'ruRU', 'Нет ничего плохого в том, чтобы всегда иметь повод выпить!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12182', 'ruRU', 'Что за муха укусила этих дворфов Черного Железа?! Они приходят каждые полчаса, ломают все бочки и убегают!$B$BЕсли ты не понимаешь, что тебе за дело до них, то я скажу!$B$BВ бочках, которые они ломают, находится чудесное пиво! Да-да, именно пиво!$B$BТак что когда эти дворфы опять придут, защищай бочки во что бы то ни стало!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12183', 'ruRU', 'Хмельной фестиваль – отличный праздник! Все бы хорошо, если бы не дворфы Черного Железа! Они нападают на нас каждые полчаса!$b$bКаждый раз они целятся в бочки с БЕСПЛАТНЫМ пивом! И это не какое-то там пойло, а лучшее пиво, предоставленное организаторами фестиваля!$b$bСлушай, если ты будешь поблизости во время очередного нападения, помоги нам и защити БЕСПЛАТНОЕ пиво!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12195', 'ruRU', 'Дважды в день, в 6 часов утра и 6 часов вечера, главный механик Меггакрут лично выходит на территорию Хмельного фестиваля для церемонии открытия нового бочонка. Не пропусти!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12196', 'ruRU', 'Дважды в день, в шесть утра и в шесть вечера, сам Ловец Духов Вол\'джин выходит на территорию Хмельного фестиваля ради церемонии открытия нового бочонка. Я бы на твоем месте не пропустил такое событие.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12198', 'ruRU', 'Этот бочонок эля охраняет Ярвен Громовар. Пока он в подвале, бочонки нельзя трогать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12213', 'ruRU', 'Нас немного осталось, $N. Возвращение нашего старого врага очень нас беспокоит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12226', 'ruRU', 'Если мы хотим создать портал, ведущий к острову Кель\'Данас и Солнечному Колодцу, как нам поручено, нам необходимо заполучить источник энергии, достаточно мощный, чтобы включить портал.$B$BМы на $3269w |4процент:процента:процентов; выполнили задачу. Ты поможешь нам, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12227', 'ruRU', 'Этот портал не похож на другие, ведущие обратно на Азерот. Чтоб сохранить его, нам потребуется еще больше контейнеров с маной.$B$BБез этого мы рискуем потерять доступ на остров Кель\'Данас и к Солнечному Колодцу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12237', 'ruRU', '', 'Здравствуйте, |3-6($r).$b$bМоя лавка наконец-то открыта. Надеюсь, вам тут все понравится!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12239', 'ruRU', '', 'Вот врата, через которые войска Келя отступили к Солнечному Колодцу. Некоторые из его прихвостней остались здесь. Легион вознаградил их очень… необычным способом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12241', 'ruRU', '', 'Наша попытка захватить Святилище увенчалась успехом. Мы можем продолжать обращать стражей и использовать их в качестве стражников в других стратегически важных территориях.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14667', 'ruRU', 'Мы отобрали самых лучших чудовищ. Таким и проиграть не стыдно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14668', 'ruRU', 'Превосходно! Дайте знать, когда будете готовы к следующей попытке.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12251', 'ruRU', 'Да благословит тебя наару, друг мой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12252', 'ruRU', 'На моей работе не так часто видишь сражения. Служить рядом с передовой в зоне военных действий – неплохая смена обстановки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12255', 'ruRU', 'Захват святилища Солнечного Предела был первым пунктом нашего плана. Следующим шагом нам приказано завладеть оружейной.$B$BМы на $3233w $lпроцент:процента:процентов; достигли нашей цели.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12256', 'ruRU', 'Мы захватили арсенал, но наша работа на острове еще не закончена.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12257', 'ruRU', 'Мне приказано вызвать смятение на Тропе Мертвых, чтобы наши войска могли взять оружейную Солнечного Предела. Может, это и не так эффектно, как сражение с демонами лицом к лицу, но если это поможет выиграть битву, я обеими руками за.$B$BМы на $3233w $lпроцент:процента:процентов; добились нашей цели.', 'Мне приказано вызвать смятение на Тропе Мертвых, чтобы наши войска могли взять оружейную Солнечного Предела. Может, это и не так эффектно, как сражение с демонами лицом к лицу, но если это поможет выиграть битву, я обеими руками за.$B$BМы на $3233w $lпроцент:процента:процентов; добились нашей цели.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12258', 'ruRU', '', 'Мы захватили оружейную, $N. Однако, не стоит почивать на лаврах. Легион точно не даст нам передышки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14272', 'ruRU', 'Знаю, знаю… \"Удить рыбу в сточных водах?!\" Но ты ведь и не подозреваешь, что тут можно выловить. Слыхал я и истории об акулах, да ты все равно не поверишь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12259', 'ruRU', 'Наши старания вознаграждены, $N. Мы захватили святилище Солнечного Края. Иди взгляни, нет ли там кого-нибудь, нуждающегося в твоей помощи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12286', 'ruRU', '', 'Привет, $n.$b$bТеперь, когда я закончила работу над своей наковальней, я могу заняться экипировкой наших войск. Это важный шаг, но сделать нужно еще очень много.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10570', 'ruRU', 'Сила и честь, |3-6($c). В Альтеракской долине нужны такие солдаты, как ты. Мы должны защитить территорию клана Северного Волка! Вернешься ли ты в Азерот и поможешь ли нам?', 'Сила и честь, |3-6($c). В Альтеракской долине нужны такие солдаты, как ты. Мы должны защитить территорию клана Северного Волка! Вернешься ли ты в Азерот и поможешь ли нам?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12287', 'ruRU', '', 'Мы взяли бухту, а этот корабль – отличная награда. Жаль, что он еще не годится для плавания по морю.$B$BПока что мне велено присматривать за нагами в заливе Зеленожабрых.$B$BТы не $gмог:могла; бы мне помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12309', 'ruRU', 'Если Кил\'джеден вернется через Солнечный Колодец, наш мир будет ввергнут в такую войну, какой не видели уже десять тысяч лет!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12320', 'ruRU', 'Готово, $N. Теперь, когда Солнечный Край наш, мы как никогда близки к полной победе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12323', 'ruRU', '', 'Правда, этот памятник прекрасен? Очень важно помнить те моменты нашей истории, когда были принесены великие жертвы, и не забывать цену войн – даже выигранных.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12334', 'ruRU', 'Заходи, отряхни с одежды грязь и пепел. Передохни с пути.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12335', 'ruRU', '\"Вожди тауренов\" гостят в Луносвете, пока я устраиваю их следующее турне. Они часто торчат на балконе надо мной, когда не репетируют. Можешь помахать им… они любят внимание.\r\n\r\nСейчас мы никого не нанимаем ни для шоу, ни в команду, но ты можешь зайти попозже. Мало ли что появится…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12338', 'ruRU', '', 'Нельзя выходить на бой с врагом, не подготовившись, $N. Я могу обеспечить тебя очень мощным оружием… если, конечно, ты докажешь нам свою преданность.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14384', 'ruRU', '$GУверен:Уверена;, что $Gготов:готова; к бою с искателем славы?', '$GУверен:Уверена;, что $Gготов:готова; к бою с искателем славы?', 'Глупо надеяться, что бой c искателем славы будет простым. $GУверен:Уверена;, что $Gготов:готова;?', 'Глупо надеяться, что бой c искателем славы будет простым. $GУверен:Уверена;, что $Gготов:готова;?', '$GУверен:Уверена;, что $Gготов:готова; к бою со мной?', '$GУверен:Уверена;, что $Gготов:готова; к бою со мной?', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12340', 'ruRU', 'Солнечный Край – наш. Псы Келя бегут… пора добить их!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12341', 'ruRU', 'Я руковожу рабочими, обслуживающими \"Вождей тауренов\".\r\n\r\nНу, знаете – погрузить, перевезти, разгрузить фургоны, поставить и разобрать сцену, позаботиться о пиротехнике, и всякое такое.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12347', 'ruRU', 'Лучшая защита – хорошее нападение. Это тебе всякий скажет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12348', 'ruRU', '', 'В мощном оружии нет никакого смысла, если ты умрешь раньше, чем начнешь им пользоваться, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13456', 'ruRU', '', 'Только магам дарована способность создавать порталы, ведущие сюда. Я могу обучить тебя этому таинству, если пожелаешь.$B$BИ помни: ты всегда можешь воспользоваться кристаллом, находящимся за мной, чтобы спуститься вниз, в руины, что находятся под Далараном в лесу Хрустальной Песни.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12370', 'ruRU', 'Проклятие Кель\'тасу и его сброду! Все, над чем мы трудились, погублено, но напрасно они думают, что мы не станем сражаться. Леди Лиадрин уже ищет новый источник силы для нашего ордена. Нас так легко не уничтожишь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12371', 'ruRU', '*Дракондор, похоже, встревожен из-за суматохи вокруг.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12494', 'ruRU', 'Так, $N, что вас интересует?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12495', 'ruRU', 'А! Странности и редкости! Мы специализируемся на невиданных, уникальных предметах Азерота, интересующих искателей приключений.$B$BРаз ты ко мне $gобратился:обратилась;, ты наверняка уже имеешь представление о том, что тебе нужно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12497', 'ruRU', '', 'Мы это сделали, $N. Мар\'на научилась получать весьма необычные камни, а я – их обрабатывать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14678', 'ruRU', 'Молодцы! Вы доблестно сражались с этими чудовищами! Правда, я слышал, что следующее испытание будет еще суровее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12498', 'ruRU', 'Не бойся, |3-6($r). У нас одна цель.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12500', 'ruRU', 'Я Калесгос, один из Синих драконов.$b$bЯ некоторое время наблюдал за нарождающимися энергиями Солнечного Колодца, но теперь я вижу, что его ужасающая мощь может уничтожить этот мир!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12502', 'ruRU', 'Победа над Кель\'тасом – достойнейшая цель, но она потребует всех твоих знаний и сил.$b$bКроме того, если тебе удастся собрать армию, то знаю, что на плато Солнечного Колодца тебя ждет еще много дел…$b$bВозможно, нам еще суждено встретиться там.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12507', 'ruRU', '', 'Мир изменился незаметно для наших глаз, Даже время теперь течет по-другому.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11419', 'ruRU', '', 'Хочешь отдохнуть, дорогуша? Или, может быть, перекусить?$B$BСудя по твоему виду, небольшой отдых тебе не помешает.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12596', 'ruRU', '', 'Хорошо, что ты здесь, |3-6($c). Обстановка на плато Солнечного Колодца становится напряженной.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12597', 'ruRU', 'Капитан Селана просила меня помочь тебе здесь, на плато Солнечного Колодца.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12598', 'ruRU', '', 'Армия Расколотого Солнца добилась больших успехов на внешних рубежах острова Кель\'Данас. Силы Кил\'джедена были оттеснены и в образовавшейся суматохе мне удалось провести небольшой отряд разведчиков сюда, на плато Солнечного Колодца. Моя группа пересекла Площадь Паргелия и обнаружила небольшой отряд эльфов крови Солнечного Клинка. А потом завязалась битва, переросшая в побоище.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12599', 'ruRU', '', 'Защитники Кил\'джедена здесь на Плато Солнечного Колодца сильнее, чем кто бы то ни было, с кем мы сталкивались на этом Острове. Легион к тому же смог извратить нашего Волшебного Стража, превратив его во что-то куда более жестокое. Только один такой Заступник клана Солнечного Клинка практически вырезал мой отряд, и я приказала отступить. Я ожидала, что моя команда будет полностью вытеснена с плато, но к моему удивлению они не стали нас преследовать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12600', 'ruRU', '', 'Не знаю, но я рада, что они сделали это. Отсрочка позволила Анахорету Илбадону спасти многих из моей команды. Я послала несколько своих самых хитрых разведчиков вперед, и они сообщили очень интересные сведения. Почти все Заступники клана Солнечного Клинка деактивированы! Войска Кил\'джедена, кажется, берегут силы и довольствуются тем, что удерживают нас на месте одним действующим Заступником. Если вы и ваши соратники хотите пробиться туда, позвольте мне изложить свой план.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12601', 'ruRU', '', 'Воспользуемся тем, что они деактивировали заступников клана Солнечного Клинка. Но если кто-то из их разведчиков засечет вас, я уверена, он попытается включить ближайшего заступника. Убейте его как можно скорее, чтобы предотвратить это. После того, как вы разберетесь с эльфами крови, заступники перестанут представлять для вас какую-либо опасность. Если, конечно, не подходить к ним в упор.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12603', 'ruRU', 'Агамат, Первые врата, и Рохендор, Вторые врата, уничтожены. Остались лишь Третьи врата – Архониус.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12604', 'ruRU', 'Все преграды, не позволявшие приблизиться у Солнечному Колодцу, уничтожены. Иди же: не дай Кил\'джедену вступить в этот мир!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12623', 'ruRU', 'Все трое врат на плато Солнечного Колодца разрушены, $N.$B$BТеперь тебе нужно собраться с силами и предотвратить появление Кил\'джедена в Азероте, иначе все погибло!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12709', 'ruRU', 'Этот пульт стоит перед глыбой, из которой валит пар. Снизу доносится гул механизмов.', 'Этот пульт стоит перед глыбой, из которой валит пар. Снизу доносится гул механизмов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12744', 'ruRU', 'С.С.М. – это не просто мишень для метания кружек, это еще и ценное оборудование для тренировок. Если ты сумеешь попасть в С.С.М., то попадешь во что угодно.$b$bПостарайся только в меня не угодить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12819', 'ruRU', 'Раз в два года Духи Соперничества впадают в неистовство… И сейчас такое время настало! Мы надеваем эту гербовую накидку, выходя на поле боя, чтобы почтить духов, дабы они послали нам победу, а нашим соперникам – позорное поражение.', 'Раз в два года Духи Соперничества впадают в неистовство… И сейчас такое время настало! Мы надеваем эту гербовую накидку, выходя на поле боя, чтобы почтить духов, дабы они послали нам победу, а нашим соперникам – поражение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12820', 'ruRU', 'Ты просто $gдолжен:должна; сойтись с соперниками лицом к лицу на поле боя. Неважно, победишь ты или проиграешь: достаточно продержаться все время боя, и ты получишь гербовую накидку участника соревнований.', 'Ты просто $gдолжен:должна; сойтись с соперниками лицом к лицу на поле боя. Неважно, победишь ты или проиграешь: достаточно продержаться все время боя, и ты получишь гербовую накидку участника соревнований.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12821', 'ruRU', 'Ты $gдолжен:должна; победить наших противников на поле боя. С каждой победой шансы на то, что ты получишь Золотой медальон, растут…', 'Ты $gдолжен:должна; победить наших противников на поле боя. С каждой победой шансы на то, что ты получишь Золотой медальон, растут…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12822', 'ruRU', 'Тебе известны рассказы о Духах Соперничества, так ведь?$B$BРаз в два года они приближаются к смертным землям, и в такое время воины Альянса и Орды облачаются в ритуальные гербовые накидки и выходят на поле боя, дабы почтить духов. Воины сражаются и побеждают противников, надеясь заслужить благоволение Духов Соперничества.$B$BЯ храню некоторые вещицы на память о таких временах. Если ты узнаешь нужное слово, быть может, я расстанусь с одной из них…', 'Тебе известны рассказы о Духах Соперничества, так ведь?$B$BРаз в два года они приближаются к смертным землям, и в такое время воины Альянса и Орды облачаются в ритуальные гербовые накидки и выходят на поле боя, дабы почтить духов. Воины сражаются и побеждают противников, надеясь заслужить благоволение Духов Соперничества.$B$BЯ храню некоторые вещицы на память о таких временах. Если ты узнаешь нужное слово, быть может, я расстанусь с одной из них…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12828', 'ruRU', 'Ты $gдолжен:должна; победить наших противников на поле боя. С каждой победой шансы на то, что ты получишь Золотой медальон, растут…', 'Ты $gдолжен:должна; победить наших противников на поле боя. С каждой победой шансы на то, что ты получишь Золотой медальон, растут…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12829', 'ruRU', 'Ты просто $gдолжен:должна; сойтись с соперниками лицом к лицу на поле боя. Неважно, победишь ты или проиграешь: достаточно продержаться все время боя, и ты получишь гербовую накидку участника соревнований.', 'Ты просто $gдолжен:должна; сойтись с соперниками лицом к лицу на поле боя. Неважно, победишь ты или проиграешь: достаточно продержаться все время боя, и ты получишь гербовую накидку участника соревнов', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12831', 'ruRU', 'Раз в два года Духи Соперничества впадают в неистовство… И сейчас такое время настало! Выходя на поле боя, воины Альянса и Орды надевают такую гербовую накидку, чтобы почтить духов, и каждый воин просит о победе себе и поражении своему противнику.', 'Раз в два года Духи Соперничества впадают в неистовство… И сейчас такое время настало! Выходя на поле боя, воины Альянса и Орды надевают такую гербовую накидку, чтобы почтить духов, и каждый воин просит о победе себе и поражении своему противнику.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12869', 'ruRU', 'Клево быть членом клуба \"Пиво месяца\"!', 'Клево быть членом клуба \"Пиво месяца\"!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12873', 'ruRU', 'Если у тебя достаточно жетонов, ты можешь вступить в клуб \"Пиво месяца\". Став членом нашего клуба, ты сможешь праздновать Хмельной фестиваль круглый год!$b$bКаждый месяц мы будем посылать тебе образец нашего нового пива на пробу. Если оно тебе понравится, наши представители будут ждать тебя в таверне \"Горючий камень\" в Стальгорне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12876', 'ruRU', 'Если у тебя достаточно жетонов, ты можешь вступить в клуб \"Пиво месяца\". Став членом нашего клуба, ты сможешь праздновать Хмельной фестиваль круглый год!$b$bКаждый месяц мы будем посылать тебе образец нашего нового пива на пробу. Если оно тебе понравится, наши представители будут ждать тебя в Пристанище Темнотерна, которое находится на Аллее Духов в Оргриммаре.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12913', 'ruRU', 'С.С.М. – это не просто мишень для метания кружек, это еще и ценное оборудование для тренировок. Если ты сумеешь попасть в С.С.М., то попадешь во что угодно.$b$bПостарайся только в меня не угодить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12926', 'ruRU', 'Большая часть моих братьев следует варварским обычаям Орды, и когда-то я тоже был среди них. Но когда меня впервые призвали в это чудное, мокрое и… ароматное место, я проклял свою прошлую жизнь.$b$bВо время учебы я научился ценить то, что меня окружает. Согласись, в этом месте есть своя привлекательность!$b$bАх, конечно – мои учения были направлены на то, чтобы позволить другим магам возвращаться сюда с помощью заклинания. Если хочешь, я поделюсь с тобой этим знанием.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12929', 'ruRU', 'Что? А, прошу прощения, я немного задумался. Я беспокоюсь за ребят из Северной стражи. Надо было послать парочку моих студентов к ним на помощь. Кстати, чем я могу помочь тебе?', 'Что? А, прошу прощения, я немного задумалась. Я беспокоюсь за ребят из Северной стражи. Надо было послать парочку моих студентов к ним на помощь. Кстати, чем я могу помочь тебе?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12932', 'ruRU', '', 'Мои учения может усвоить только маг. Боюсь, я не могу помочь тебе, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12933', 'ruRU', '', 'Шину а\'манорэ. Если ты хочешь узнать, как открывать портал в Терамор, я могу тебя научить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12934', 'ruRU', 'Хмельной фестиваль слегка изменился за прошедшие годы. Теперь мы выдаем жетоны фестиваля вместо купонов. Если у тебя остались старые купоны, я с удовольствием их обменяю.', 'Хмельной фестиваль слегка изменился за прошедшие годы. Теперь мы выдаем жетоны фестиваля вместо купонов. Если у тебя остались старые купоны, я с удовольствием их обменяю.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12963', 'ruRU', '', 'Дорогой, за эти туфельки можно отдать всю жизнь! Нет, правда! Кого ты убил, чтобы раздобыть их для меня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13044', 'ruRU', 'Жуткий капитан ДеМеза захватила Пиратскую бухту и объявила празднование Дня пирата!$B$BОна приглашает всех смельчаков выпить вместе с ней и ее командой.', 'Жуткий капитан ДеМеза захватила Пиратскую бухту и объявила празднование Дня пирата!$B$BОна приглашает всех смельчаков выпить вместе с ней и ее командой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13060', 'ruRU', 'Ой… что-то у меня голова закружилась…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13064', 'ruRU', 'Отличное пиво! Правда, слегка слабовато, на мой вкус. Я бы добавил немного чесночного душка…', '', 'Хорошо получилось! Но мне кажется, оно немного плоское. Давай-ка закруглим его с помощью нескольких мягких глаз василиска…', '', 'Неплохо, неплохо… Правда, не хватает перчинки! Может быть, добавить яда скорпида? Точно! Получится то, что надо!', '', 'Ага, уже лучше! Но все равно, чего-то не хватает… О, точно! Давай постираем в котле носки недельной давности… Ммм, вкуснятина!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13079', 'ruRU', 'Не обращай на меня внимания. Я всего лишь старик, ждущий своего старого… друга.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13093', 'ruRU', '', 'Слушай, скажи Швайцеру и Ройтау, что у нас тут все под контролем. Пусть даже не… что?$B$BОни тебя не посылали ко мне? Тогда зачем ты тратишь мое драгоценное время?$B$BУ меня дел по горло!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13094', 'ruRU', 'Эй, не видишь разве – я занят делом!$B$BХочешь попасть на концерт? Приходи вечером, в восемь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13155', 'ruRU', 'Прошу прощения, но мы разливаем наше пиво только для членов клуба \"Пиво месяца\".', 'Прошу прощения, но мы разливаем наше пиво только для членов клуба \"Пиво месяца\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13302', 'ruRU', 'Как только эту стену снесут, мы построим новую дорогу в Бухту Штормграда... если, конечно, этот сумасшедший дворф не сровняет нас всех с землей!$B$BНе знаю, о чем только думал мастер Алекстон, когда нанимал эту бешеную, злобную и упрямую ослицу с тонной пороха в багаже!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13441', 'ruRU', 'Приветствую тебя, |3-6($c). Полагаю, ты недавно $gпосетил:посетила; мероприятие Worldwide Invitational? В таком случае, тебе должны были сказать тайный код для получения подарка – Эфеса меча Тираэля.$B$BШепни мне его на ушко, когда захочешь получить свой сувенир.', 'Приветствую тебя, |3-6($c). Полагаю, ты недавно $gпосетил:посетила; мероприятие Worldwide Invitational? В таком случае, тебе должны были сказать тайный код для получения подарка – Эфеса меча Тираэля.$B$BШепни мне его на ушко, когда захочешь получить свой сувенир.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10504', 'ruRU', '*Волшебный Страж молча ждет вас, чтобы продолжить экскурсию.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10430', 'ruRU', '*Волшебный Страж вроде бы и смотрит на вас, но ничего не говорит.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10919', 'ruRU', 'Вы прибыли в Огри\'лу совсем недавно, поэтому еще не подверглись излучению кристаллов в достаточной мере, чтобы использовать их максимально эффективно. Да и голова у вас одна.$B$BНо мы провели тут очень много времени, поэтому, как можно догадаться, успели узнать гораздо больше вас.$B$BНедавно мы заинтересовались демонами из Лагерей Легиона и даже придумали способ, как навсегда избавиться от них.$B$BПо крайней мере, теория у нас есть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11026', 'ruRU', 'Мы пришли помочь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11094', 'ruRU', 'Кристальное средоточие? Разумеется, мы объясним, потому что вас, как видно, умом обделили.$B$BРазряженное кристальное средоточие можно скомбинировать с десятью апекситововыми осколками и в результате простого процесса преобразовать в заряженное кристальное средоточие. Заряженные кристальные средоточия обладают некими целебными свойствами, но будет гораздо правильнее, если их использует подконтрольный демон.$B$BЭти кристаллы могут наделить демона некими особыми умениями, и через некоторое время Гахк даст вам задание, касающееся именно этого.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11180', 'ruRU', 'Дирижабль должен как раз прибыть в Гром\'гол!', 'Дирижабль должен как раз прибыть в Гром\'гол!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11182', 'ruRU', 'Дирижабль должен как раз отбыть из Подгорода.', 'Дирижабль должен как раз отбыть из Подгорода.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11472', 'ruRU', '', 'Существуют колоды карт Новолуния различных видов: Звери, Благословения, Элементали, Ярости, Безумие, Порталы, Ураганы, Полководцы и другие. В каждой колоде по восемь карт, от туза до восьмерки. Если сумеете собрать колоду одного вида, то профессор Таддеус Палео даст вам полезный аксессуар в обмен на нее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12306', 'ruRU', '$N, Рохендор уничтожен, но на плато Солнечного Колодца остался еще один магический барьер, Архониус.$B$BНам сейчас пригодится любая твоя помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13474', 'ruRU', 'Питомцы бывают трех типов. Агрессивные питомцы – например, кошки – практически не умеют защищаться, но зато могут нанести значительный урон вашему врагу. Осторожные питомцы вроде черепах более выносливы и могут долгое время отвлекать внимание противника. А коварные питомцы – такие, как пауки – могут с одинаковым успехом нападать и защищаться. Поэтому я рекомендую именно их для сражений на поле боя и Арене.$b$bНабираясь опыта, твой питомец сможет обучаться новым способностям. Если захочешь научить своего любимца чему-то новому – обращайся ко мне!', 'Питомцы бывают трех типов. Агрессивные питомцы – например, кошки – практически не умеют защищаться, но зато могут нанести значительный урон вашему врагу. Осторожные питомцы вроде черепах более выносливы и могут долгое время отвлекать внимание противника. А коварные питомцы – такие, как пауки – могут с одинаковым успехом нападать и защищаться. Поэтому я рекомендую именно их для сражений на поле боя и Арене.$b$bНабираясь опыта, твой питомец сможет обучаться новым способностям. Если захочешь научить своего любимца чему-то новому – обращайся ко мне!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1516', 'ruRU', 'Нет времени болтать! Мы должны позаботиться о тех троллях!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2113', 'ruRU', 'Спасибо, что $gспас:спасла; меня!$B$BЯ повар и алхимик из Штормграда. Я здесь искал местные травы и собирал рецепты для дяди Ангуса, когда меня похитили эти иглогривы. Если бы не ты, я бы, несомненно, окончил жизнь в качестве их ужина.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2173', 'ruRU', 'Ты пока не слишком хорошо готовишь. Подучись еще немного, а потом приходи ко мне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4862', 'ruRU', 'Взгляни на кодо, $gдруг мой:уважаемая;! Из этих зверей, приручаемых с малых лет и выпасаемых на золотистых равнинах Мулгора, получатся превосходные скакуны! Прошу, осмотри моих животных, и $gсам:сама; поймешь, почему мы, таурены, так полагаемся на них!', 'Взгляни на кодо, $gдруг мой:уважаемая;! Из этих зверей, приручаемых с малых лет и выпасаемых на золотистых равнинах Мулгора, получатся превосходные скакуны! Прошу, осмотри моих животных, и $gсам:сама; поймешь, почему мы, таурены, так полагаемся на них!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4868', 'ruRU', 'Страсть к охоте присуща всем тауренам.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4891', 'ruRU', 'Эй! Ты когда-нибудь нырял в океан, а? Я расскажу тебе один секрет: под водой есть такие отверстия с пузырьками, и там можно дышать! Только никому не говори – мне не нужны конкуренты по ловле моллюсков… Понятно?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4959', 'ruRU', '', 'Мне нужно кого-нибудь нанять. Я уже устала сидеть тут целыми днями и продавать рыболовные снасти. Лучше было бы отправиться на рыбалку, а потом продать улов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6533', 'ruRU', 'Рад встрече, |3-6($c). Я Рокаро.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7334', 'ruRU', 'Приветствую вас, друг мой! Добро пожаловать на величайшее представление во всем Азероте!$B$BПожалуйста, проходите и посмотрите, что у нас есть! Взгляните на диковинки ярмарки Новолуния, собранные со всего света! Наши развлечения понравятся детям всех возрастов – от 7 до 70!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7336', 'ruRU', 'Не всегда все решают деньги! Пока мы получаем щедрые пожертвования, нашей труппой движет исключительно стремление нести искусство в этот истощенный войной мир. Не волнуйся о нас, лучше позволь нам развлечь тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7354', 'ruRU', 'Не видишь, я здесь работаю!', 'Не видишь, я здесь работаю!', 'Скоро будет ярмарка!', 'Скоро будет ярмарка!', 'Все верно! Ярмарка Новолуния будет здесь – как только я разгружу эти огромные повозки, поставлю палатки, открою зоопарк и наполню кружки. Возвращайся обязательно!', 'Все верно! Ярмарка Новолуния будет здесь – как только я разгружу эти огромные повозки, поставлю палатки, открою зоопарк и наполню кружки. Возвращайся обязательно!', 'Ярмарка Новолуния вот-вот откроется! Зайдите чуть позже, наверняка все уже будет готово.', 'Ярмарка Новолуния вот-вот откроется! Зайдите чуть позже, наверняка все уже будет готово.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7357', 'ruRU', 'Если ты соберешь колоду pверей, я дам тебе в награду редкую карту Новолуния с синим драконом. Она очень пригодится для восстановления магической энергии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7366', 'ruRU', 'Скоро в городе будет ярмарка Новолуния! Не пропусти!', '', 'Когда в городе откроется ярмарка Новолуния, обязательно сходи туда. Там столько всего интересного!', '', 'Ярмарка Новолуния приближается! Веселье для детей от 7 до 70! Приходите все!', '', 'Подходите! Сейчас вы услышите о самом необычном зрелище на всем Азероте – о ярмарке Новолуния! Ура!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7374', 'ruRU', 'Мы уже здесь! Ярмарка Новолуния открылась! Не пропустите самое увлекательное событие Азерота!', '', 'В городе открылась ярмарка Новолуния! Не пропустите! Такого вы еще не видели!', '', 'Ярмарка Новолуния приехала ненадолго, так что поторопитесь, обязательно посетите ее как можно скорее!', '', 'Девочки, мальчики, а так же все те, кто в душе остались детьми! Ярмарка Новолуния уже началась! Ур-р-а-а-а!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7377', 'ruRU', 'Ярмарка Новолуния сейчас проходит на золотистых равнинах Мулгора, к юго-западу от Громового Утеса. Не пропусти!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7395', 'ruRU', 'Приветствую тебя. Я – Хронос, помнящий все. Моя память безупречна и точна до малейшей детали.$B$BДоверься мне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7400', 'ruRU', '', 'Никак не могу найти свою лягушку, Жабжаб. Она любит эль Черного Железа – только его и пьет, а его бывает так трудно достать! Мои запасы кончились, и Жабжаб, должно быть, убежала искать эль!$B$BХочу, чтобы она вернулась…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7677', 'ruRU', 'Эй-эй-эй! Если вы считаете, что умеете ловить рыбу, приходите к нам! В это воскресенье в Тернистой долине пройдет состязание рыбаков!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7730', 'ruRU', '', 'Ох, у меня опасно мало сырья! Где этого проклятого гоблина носит столько времени?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7753', 'ruRU', 'Кто ты? Где я? Я чувствую себя таким… слабым…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7789', 'ruRU', 'Добро пожаловать! Проходите, не стесняйтесь. Вот это – моя радость и гордость: \"Ультрапушка 5000\" Покрышкинс! Разве не прелесть? Вы для нее, пожалуй, немного крупноваты, но ничего, поместитесь. Летать – это так здорово! Хотите научиться?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7790', 'ruRU', 'О, это просто! Используешь, как и все прочее, что можно активировать. Не волнуйся, запал я подожгу. Оказавшись в воздухе, ты почувствуешь, что как будто паришь, но долго это не продлится. Твоя цель – на озере, что далеко впереди. Не забудь при начале снижения снять свои \"крылья\" вовремя, чтобы приземлиться на воду, а не то… в общем, жизнь я не страхую.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7793', 'ruRU', 'Отличный вопрос! У нас, на ярмарке, продается много чего. Если тебя интересуют еда и питье, отправляйся в палатку Топа и Силаннии. Я лично отдаю предпочтение особому Новолуния.$B$B$BЕсли же ты хочешь чего-то необычного, возможно, тебе приглянутся товары Лары. Она торгует редкостями со всего мира.$B$B$BА еще тут где-то шныряет Флик. Если отловишь его, сможешь купить мячи для швыряния и дрессированных лягушек.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7795', 'ruRU', '', 'О происхождении карт Новолуния достоверно ничего не известно, как, впрочем, и об их истинном предназначении. Но могу сказать, что их можно найти в самых разных уголках Азерота – как правило, у всяких злодеев, которые пытаются разгадать их тайну.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7799', 'ruRU', '', 'Я настоятельно рекомендую вам обратиться к Воржею за предсказанием. Да, да, он гнолл. Но этот гнолл особенный, он и правда может предсказать ваше будущее!$B$BА еще на нашей ярмарке постоянно появляются новые аттракционы!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8347', 'ruRU', '', 'Наш новейший аттракцион – и совершенно бесплатный! Для управления этими миниатюрными паровыми танками можно использовать любую из панелей управления, расставленных по всей ярмарке.$B$BОт вас требуется только включить ближайшую панель. Каждый танк оборудован комплектом стандартных приспособлений и одной случайно выбранной особой возможностью!$B$BВедите эпические битвы с друзьями, катайтесь по окрестностям или просто устраивайте веселую суматоху… Выбор за вами!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8349', 'ruRU', 'Ультрапушка 5000 Покрышкинс – великолепное оружие, управляемое одним стрелком. Эта пушка, разгоняющая груз до околодраконьей скорости – гордость семейства Покрышкинс, известного во всем мире своими воздухоплавательными и стрелковыми достижения!$B$BЕсли хочешь, чтобы тобой выстрелили в небеса, где ты сможешь парить, подобно птице, разыщи Максиму Покрышкинс на южном краю центральной возвышенности Громового Утеса. Она нацелит тебя далеко на юг, на озеро Каменного Быка!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8549', 'ruRU', 'Разве ты не видишь, что я пытаюсь поговорить с этой женщиной? Впрочем, она в любом случае задумалась.$B$BЯ торгую только лучшими напитками – местными, заморскими и даже старинными. Не хочешь взглянуть?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9106', 'ruRU', 'Если наши враги увидят, что мы хотим жить с ними в мире, может быть, нам не придется атаковать их, чтобы выжить самим?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9108', 'ruRU', 'Когда новые враги стучатся в дверь, мы не должны позволить нашим древним противникам одержать верх.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9111', 'ruRU', 'Грязные грибные великаны! Что ж нам с ними делать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9201', 'ruRU', 'Я пока не знаю, что думать о таких, как ты, |3-6($r). Некоторые из вас могут быть жестокими и убивать спорлингов, как только их увидят.$B$BТы выглядишь $gдружелюбным:дружелюбной;… хотя и немного $gстранным:странной;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9248', 'ruRU', 'Добро пожаловать в Забра\'джин, |3-6($c). Я темный охотник Дэнжай, смотритель этого аванпоста и командир сил Черного Копья в Зангартопи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9249', 'ruRU', 'И не думай даже пробовать удрать от меня с моими мурлочьими младенцами! У меня глаза ястреба и уши… уши… В общем, я отлично слышу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9259', 'ruRU', 'Когда я впервые увидел грибы выше деревьев, я не был уверен, что смогу назвать такое место своим домом. А потом просто перестал замечать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9268', 'ruRU', 'Если ты когда-нибудь встретишь знахаря Тор\'геша, передай ему, что его зелья и амулеты не работают! Я сделал все так, как он мне сказал, но Магаша до сих пор заглядывается на Зурая.$B$BНе верь ни единому слову этого знахаря!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9269', 'ruRU', '', 'Реавиж грациозен, словно кодо! Забавно то, что он и сам не понимает, как неуклюже прячется. Я же вижу, как он якобы незаметно смотрит на меня.$B$BОн меня пугает. Хорошо бы темный охотник Дэнжай его с нами не посылал. На заставе Болотной Крысы было бы очень удобно от него сбежать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9380', 'ruRU', '', 'Отлично сделано, $gискатель:искательница; приключений. Смерть Лютого Хлада дала нам небольшую передышку между нападениями врага. Используйте это время с умом. Исцелите своих раненых и приготовьтесь защищаться, поскольку скоро Архимонд направит против нас еще более смертоносные войска. Мои немногочисленные вспомогательные отряды помогут вам, когда вновь начнется битва.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9381', 'ruRU', 'Тогда позвольте Архимонду сделать самое худшее, что он может.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9451', 'ruRU', '', 'Моя работа – заботиться об этих несчастных душах. Я готова принять помощь от любого, кто хочет помочь, будь то Алдоры или Провидцы.$B$BНам так не хватает рук! Я бы даже огру не отказала, умей он накладывать жгут. Здешняя политика меня не касается.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9486', 'ruRU', 'Я наблюдал за тем, как Алдоры работали над стенами. Знаешь, они ведь весь город отстроят заново.$B$BРаботают они куда быстрее, чем это делал штейгер Ослоу у себя в Приозерье. У него то инструменты падали в озеро, то гноллы крали материал.$B$BНадо бы попросить Алдоров, пусть они ему помогут. С ними мост в два счета будет починен.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9618', 'ruRU', 'Ты коль отдохнуть хочешь – спрашивай-то!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9997', 'ruRU', 'Давай побыстрей, |3-6($c) – у нас тут война, если непонятно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10373', 'ruRU', 'Спасибо за то, что выпустили меня отсюда! Я ваш должник!', '', 'Отсюда я теперь смогу выбраться. Спасибо!', '', 'Умопомрачительное местечко! Я бы мог годами изучать всю эту технику!', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4874', 'ruRU', 'Я могу научить тебя новым приемам езды на кодо, если, конечно, у тебя хватит золота заплатить за урок!', 'Я могу научить тебя новым приемам езды на кодо, если, конечно, у тебя хватит золота заплатить за урок!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9224', 'ruRU', '$GНезнакомец:Незнакомка;, скорее поговори с леди Джайной Праудмур. Ее маленький отряд, верный Альянсу, храбро противостоит вторжению Пылающего Легиона и не допускает появления Архимонда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13584', 'ruRU', 'Здравствуй, |3-6($c)! Я могу помочь тебе в розыске пропавших питомцев.', 'Здравствуй, |3-6($c)! Я могу помочь тебе в розыске пропавших питомцев.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13439', 'ruRU', 'Хочешь попасть в Аубердин или в Крепость Отваги? Тогда тебе надо в порт Штормграда. Иди прямо через Каналы, гавань расположена между Храмовым кварталом и парком.', 'Хочешь попасть в Аубердин или в Крепость Отваги? Тогда тебе надо в порт Штормграда. Иди прямо через Каналы, гавань расположена между Храмовым кварталом и парком.', 'До Аубердина или крепости Отваги быстрее всего можно добраться через штормградский порт. Порт находится между храмовым кварталом и парком.', 'До Аубердина или крепости Отваги быстрее всего можно добраться через штормградский порт. Порт находится между храмовым кварталом и парком.', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13882', 'ruRU', 'Парикмахерская Джелиника находится в северной части Торгового квартала.', 'Парикмахерская Джелиника находится в северной части Торгового квартала.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13459', 'ruRU', '$gДруг мой:Красавица;, добро пожаловать в парикмахерскую! Хочешь сделать стрижку или изменить цвет волос? А может быть, внести какие-то другие изменения в свой образ?$B$BМы можем подровнять бороду и усы, сделать пирсинг, нанести новые татуировки ночным эльфам и даже изменить форму рогов и щупалец у дренеев!$B$BПрисаживайся, чувствуй себя как дома. От нового облика тебя отделяют всего несколько монет!..', '$gДруг мой:Красавица;, добро пожаловать в парикмахерскую! Хочешь сделать стрижку или изменить цвет волос? А может быть, внести какие-то другие изменения в свой образ?$B$BМы можем подровнять бороду и усы, сделать пирсинг, нанести новые татуировки ночным эльфам и даже изменить форму рогов и щупалец у дренеев!$B$BПрисаживайся, чувствуй себя как дома. От нового облика тебя отделяют всего несколько монет!..', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13593', 'ruRU', 'Слишком долго я блуждал по всему Азероту в погоне за прошлым и тщетных попытках вернуть свой трон. Но, учитывая все те испытания, через которые пришлось пройти Альянсу в мое отсутствие, я не могу не гордиться подобными тебе героями, которые бесстрашно стоят на стороне нашего союза.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13832', 'ruRU', 'Ты слишком $gслаб:слаба;, чтобы сражаться на Береге Древних. Возвращайся, когда наберешься опыта.', 'Ты слишком $gслаб:слаба;, чтобы сражаться на Береге Древних. Возвращайся, когда наберешься опыта.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13310', 'ruRU', 'Возле Оргриммара есть две башни дирижаблей. Восточная башня обслуживает Тирисфальские леса и Тернистую долину, а западная – Нордскол.', 'Возле Оргриммара есть две башни дирижаблей. Восточная башня обслуживает Тирисфальские леса и Тернистую долину, а западная – Нордскол.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13311', 'ruRU', 'Причальная башня дирижабля находится на юго-восточной окраине города.', 'Причальная башня дирижабля находится на юго-восточной окраине города.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13889', 'ruRU', 'Парикмахер обитает в Расселине Теней.', 'Парикмахер обитает в Расселине Теней.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13888', 'ruRU', 'Джо\'ма находится на втором этаже Волока.', 'Джо\'ма находится на втором этаже Волока.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14174', 'ruRU', 'Словарь Силы необходим для совершенствования ваших заклинаний и способностей при помощи символов. Это бесценный ресурс, в котором нуждается каждый любознательный исследователь мира. Древние тома словарей всегда можно найти рядом с учителями, обучающими начертанию.', 'Словарь Силы необходим для совершенствования ваших заклинаний и способностей при помощи символов. Это бесценный ресурс, в котором нуждается каждый любознательный исследователь мира. Древние тома словарей всегда можно найти рядом с учителями, обучающими начертанию.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13557', 'ruRU', 'Здравствуй, |3-6($c)! Я могу предоставить стойло для твоих питомцев и помочь в розыске пропавших любимцев.', 'Здравствуй, |3-6($c)! Я могу предоставить стойло для твоих питомцев и помочь в розыске пропавших любимцев.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13842', 'ruRU', 'Что привело тебя ко мне?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12373', 'ruRU', 'Разрушение Оргриммара было направлено не только против орков, это был вызов всей Орде. Атака против самого нашего существования! $B$BКороль-лич думал посеять панику и страх.$B$BЭто…$B$B<Гаррош указывает пальцем на укрепленные стены, окружающие его.>$B$BЭто результат кампании Артаса.$B$BОрда не боится ничего и никого…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12417', 'ruRU', 'Мы потеряли довольно много ветрокрылов во время разведывательных миссий на западе. Силы Плети безжалостны к потенциальным шпионам.$B$BК счастью, я нашел решение, при котором риску подвергается только всадник. Выживают 30% из тех, кого провозят через равнины Назама. Это ж на целых 30% лучше, чем при использовании ветрокрылов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12424', 'ruRU', 'То, что ты видишь на берегу – это не фокусы Плети. На самом деле, мы думаем, что приспешники Плети тоже боятся тумана, и поэтому, может быть, они сюда и не приходят.$B$BВидишь ли, этот туман живой, друг. Внутри этих клубов воздуха находится смерть… $B$BЕсли ты вступишь в одно из его призрачных щупальцев, ты исчезнешь. Туман заберет тебя!$B$BМы никогда не видели, чтобы кто-то вернулся живым из тумана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12422', 'ruRU', 'И лагерь Гарроша, и Пал\'ия были стерты с лица земли этим туманом. Мы единственные сумасшедшие, кто остался здесь, дружище!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12427', 'ruRU', '', 'Я… А ты…$B$B<Муту сотрясает дрожь>$B$BТы хочешь меня спасти?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12426', 'ruRU', 'После высадки здесь я видел ужасы, которые ранее посещали меня лишь в самых жутких кошмарах, но ничто не могло меня подготовить к тому, что лежит за туманом.$B$B<Муту кивает.>$B$BЗа этим туманом не далее, чем в нескольких ярдах от нас, стоят кровожадные существа, известные как квалдиры. Никто не вернулся из тумана, и мало из тех, кто был на месте высадки, когда накатил туман, уцелел. Эта местность была позднее проклята Адским Криком. Входи на свой страх и риск…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12756', 'ruRU', 'Небо небезопасно, друг. Будь бдителен!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12626', 'ruRU', '', 'Берег Терзающего Бича никогда не вернет себе прежней славы, но, клянусь, эти мерзкие твари на своих жалких лодчонках сполна заплатят за свои деяния!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12477', 'ruRU', 'Закон клыкарров обязывает меня отомстить за своих сородичей… эти убийцы сравняли Пал\'иа с землей!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12472', 'ruRU', 'Действия этих дезертиров позорят нас всех. Позорят столь сильно, что это могут понять лишь те, кто знает, что это такое. Вот почему Уг\'тор здесь… Пареньку нужно знать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12485', 'ruRU', 'Твои помыслы чисты, а на сердце светло? Тогда добро пожаловать в обитель друидов, ратующих за этичное и гуманное отношение к животным. До тех пор, пока ты не станешь на темный путь греха и не причинишь вред животным, позволившим нам сосуществовать с ними в их мире, Д.Э.Г.О.Ж. будет считать тебя своим союзником.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12440', 'ruRU', '', 'Говори помедленнее, дитя, я еще не привыкла к твоему странному говору.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12441', 'ruRU', '', 'Пожалуйста, говори потише, |3-6($r). Хамат стара, и ей нужен отдых.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12475', 'ruRU', '', 'Будь поосторожнее тут, |3-6($r). Местные обитатели совершенно безжалостны.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12780', 'ruRU', '', 'У нас очень ограниченные запасы еды и питья. Боюсь, мне нечего тебе предложить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14215', 'ruRU', '', 'Быстрее, |3-6($r)! Я устала, и у нас нет времени на реверансы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11796', 'ruRU', 'Всю свою жизнь я провел на поле боя. Я сражался под разными знаменами, но всегда во славу других.$B$BТеперь же все иначе. У каждого солдата Плети отныне есть лишь один долг – убить Артаса.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12646', 'ruRU', 'Хай! Хочешь попасть обратно, в Старый свет? \" Мой \"Поцелуй небес\" доставит тебя в Тирисфальские леса в мгновение ока!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11607', 'ruRU', 'Заходи сюда с холода и дай немного отдохнуть своим утомленным косточкам. Воспользуйся нашим гостеприимством!', 'Заходи сюда с холода и дай немного отдохнуть своим утомленным косточкам. Воспользуйся нашим гостеприимством!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12556', 'ruRU', '', 'Сейчас я очень занята, |3-6($r). Прошу тебя, оставь меня в покое.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12627', 'ruRU', '', 'Наша обязанность – защитить этих таунка и предоставить им хотя бы те скудные удобства, которыми располагаем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12730', 'ruRU', 'Почему ты меня беспокоишь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13426', 'ruRU', 'Чем могу помочь, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12991', 'ruRU', 'Прошу прощения, |3-6($r), но я здесь по официальному делу и меня нельзя беспокоить.$b$bПрошу простить меня.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12770', 'ruRU', '', 'Отчаянное время требует отчаянных мер, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12740', 'ruRU', 'Приветствую! Вам что-нибудь нужно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12566', 'ruRU', 'Соберись, $gсмертный:смертная;. Здесь проходит линия фронта войны, не сравнимой с любой другой, которую тебе доводилось видеть.$b$bТебе понадобится полный контроль над собой, чтобы принести нам пользу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12577', 'ruRU', '', 'Ох…$b$bГде я?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12591', 'ruRU', 'Мы продвигаемся, |3-6($r), но слишком медленно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12576', 'ruRU', '', 'Видеть тебя снова, $n, – истинное удовольствие.', '', '$n! А я переживала, как ты там. Что задержало тебя?', '', 'Я надеюсь, ты $gпринял:приняла; все меры предосторожности…$b$bЭто опасное место, и я не прощу себе, если с тобой что-то случится.', '', 'Мы должны спешить, $n. Разрушительные планы Малигоса скоро будет невозможно остановить.', '', 'В такие времена настоящие друзья просто бесценны.$b$bБереги себя, $n. Насколько это возможно в таком месте, как это…', '', 'Если бы ты не $gспас:спасла; меня из этой тюрьмы, сейчас я была бы уже мертва.$b$bЯ этого не забыла…', '', 'Когда мы покончим с нашими делами, я найду способ отблагодарить тебя за свое спасение.', '', 'Моя сила возвращается, но медленно…$b$bНадеюсь, скоро я смогу продемонстрировать тебе все великолепие моей истинной формы.');
INSERT INTO `npc_text_localized` VALUES ('12592', 'ruRU', 'Мы должны победить! На кону – судьба Азерота…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12781', 'ruRU', '', 'Спасибо за помощь, $N. Эта снежевика в буквальном смысле слова – плод твоего труда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12382', 'ruRU', 'Нам необходимо подготовить этот аванпост к маршу на Низину Шолазар.$B$BДа не стой столбом, |3-6($c). Выметайся и покажи всем, что значит быть Ордой: купаться в лучах славы и реках крови!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12387', 'ruRU', '', 'Духи разговаривают рядом с нами. Ты слушаешь их, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12732', 'ruRU', 'Привет. Если тебе что-нибудь потребуется, дай мне знать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12395', 'ruRU', 'Духи воздуха встревожены. Берегись… они не позволят тебе пройти $gневредимым:невредимой;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12419', 'ruRU', 'Я опозорен. Мне следовало умереть в Магмоте вместе с предсказателем!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12357', 'ruRU', 'Я недавно прибыл сюда для наблюдения за мурлоками из клана Зимних Плавников. Загримировавшись под одного из них, я держался на расстоянии и делал заметки об их повседневной жизни.$B$BОднако вскоре, после моего прибытия, все изменилось.$B$BЗемлетрясение в Хладарре вызвало оползень, обнаживший близлежащие пещеры. Из них выбрался странный макрура, который утащил головастиков Зимних Плавников. Когда мурлоки отправились в пещеры, чтобы спасти своих детенышей, они вернулись светящимися и попавшими под влияние макруры.$B$BТеперь незатронутые всем этим отступили сюда, а мне поневоле пришлось взять на себя командование ими.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12349', 'ruRU', 'Гавургггл! Блргл-бргл гргл!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12352', 'ruRU', 'Я не слепой, знаешь. Я прекрасно вижу, что здесь происходит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12344', 'ruRU', '<Ахлурглгр удивлен тем, что может вас понимать.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12350', 'ruRU', 'Ты $gнаучился:научилась; говорить с нами, |3-6($r), но я не впечатлен.$B$BТам, на другом берегу, живут предатели Зимние Плавники, которые должны быть уничтожены.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13135', 'ruRU', 'Выпусти меня отсюда, |3-6($r)!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12361', 'ruRU', 'Мои братья и сестры сошли с ума! Как считаешь, не $gмог:могла; бы ты мне помочь убраться отсюда?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12363', 'ruRU', 'Некогда говорить – мне надо работать!', '', 'Ты понимаешь меня? Я-то тебя понимаю!', '', '$R, почему это я должен захотеть говорить с тобой? Твой народ, скорее всего, сам заварил эту кашу!', '', 'Тут есть моллюски, которых можно собирать, и косатки, которых можно добывать. Помогай или не путайся под ногами!', '', 'Блурглгл мурбргл… шучу-шучу, я могу тебя понимать.', '', 'Ты уже $gразобрался:разобралась;, почему наши братья и сестры по всей деревне сошли с ума?', '', 'Я знал, что нам не стоило идти в эти пещеры. Это противоестественно!', '', 'Тут что-то нечисто с этим новым королем Мргл-Мрглом, но я не могу понять, что.', '');
INSERT INTO `npc_text_localized` VALUES ('12413', 'ruRU', 'Только ты можешь спасти меня от Каганишу, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12394', 'ruRU', 'Я обесчещен. Мне лучше было умереть вместе с предсказателем в Магмоте.$B$BОставь меня наедине с моими мыслями.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12384', 'ruRU', '', 'Мне нужно тут убрать весь этот беспорядок, но на это я жаловаться не стану.$B$BА ты поможешь мне, если у тебя есть хоть капля гордости настоящего ордынца!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12388', 'ruRU', 'Здесь лежит тело одного из охранников каравана Стальной Челюсти.', 'Здесь лежит тело одного из охранников каравана Стальной Челюсти.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12403', 'ruRU', 'Мне больно видеть, как моих предков беспокоят в их могилах.$B$BМы должны сразить проклятый дух вашего командира соединения Стальная Челюсть, по глупости проведшего свой караван через эти священные места.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13572', 'ruRU', 'Эй, |3-6($c), если у тебя потерялся ключ – я могу помочь найти его!', 'Эй, |3-6($c), если у тебя потерялся ключ – я могу помочь найти его!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8436', 'ruRU', 'Этот сектант погружен в глубокий транс…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9373', 'ruRU', 'Желающие подать заявку должны переговорить со стражем-смотрителем Быккроком, прежде чем опубликовать какое-либо задание. $b$bТе, кого застанут за публикацией несанкционированных объявлений, будут наказаны.$b$bСчета на доску не помещать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9729', 'ruRU', 'Я нигде не видал такой отличной охоты, как на диких равнинах Награнда!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8526', 'ruRU', 'Чаша весов склоняется в нашу сторону. Силы Плети, кажется, истощены. Я уверен, что изгнание Плети – лишь дело времени. Я помогу тебе в этой борьбе своей магией.', 'Чаша весов склоняется в нашу сторону. Силы Плети, кажется, истощены. Я уверена, что изгнание Плети – лишь дело времени. Я помогу тебе в этой борьбе своей магией.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12291', 'ruRU', 'Не спрашивай…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12290', 'ruRU', '', 'Любишь острые ощущения? Тогда тебе у нас понравится! Я свою работу обожаю!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12289', 'ruRU', '*Ворчит* Эти горы как из ниоткуда появились… И так пять раз!', '', '*Ворчит* Рухнуть в океан, конечно, не каждому дано. Но чтобы четыре раза!', '', '*Ворчливо* Хотелось бы мне сказать, что нам удавалось избежать аварийных посадок на озеро. Хотелось бы мне сказать, что нам довелось плюхнуться в озеро всего один раз… но мы умудрились свалиться четырежды, в четыре разных озера, включая озеро Лордамер!', '', 'Я, конечно, рад, что сосновые ветви не особо повредили наш корабль.$b$bНо это слабое утешение, учитывая, что мы три раза врезались в деревья.', '', 'Я никогда не думал, что увижу Тельдрассил… И уж точно мне и в голову не приходило, что мы грохнемся туда.', '', 'Нам удалось пролететь сквозь Громовой Утес уже дважды!', '', '*Ворчит* Я все в толк не возьму – как мы смогли пробраться через Тысячу Игл без единой царапины, а потом рухнуть на Мерцающей равнине!', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12288', 'ruRU', 'Добро пожаловать на борт \"Призывателя грома\"!$b$bНе обращайте внимания на тех, кто жалуется на взрывы и все такое. Ну, то есть – у всех бывают ошибки. Надо быть снисходительнее к людям.$b$bЯ понимаю, если капитан разбил корабль 22-23 раза… Но если меньше – тут и говорить не о чем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8655', 'ruRU', 'Тебе, видать, награда за твой подвиг нужна? Намекаешь на мою жизнь? Пфу. Бери, что у меня есть, и уходи.', 'Тебе, видать, награда за твой подвиг нужна? Намекаешь на мою жизнь? Пфу. Бери, что у меня есть, и уходи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3194', 'ruRU', 'Леди Сильвана приказала исследовать некоторые естественные явления Азерота. Она считает, что можно узнать что-то важное из его полного секретов прошлого.$B$BЯ не знаю, откуда она знает такие вещи, но полностью доверяю ей, так что сделаю все, что смогу.$B$BСейчас я исследую слизнюков и тому подобную живность. Существа кажутся схожими по составу, но сильно восприимчивыми к своему окружению.$B$BЯ не отказался бы от твоей помощи в сборе образцов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3273', 'ruRU', 'При вашем приближении все жидкости и смеси начинают кипеть и булькать. Рядом с оборудованием лежат огромные клещи, пригодные для взятия проб у слизнюков.$B$BПоблизости есть и защитное снаряжение, но оно выглядит пыльным и неухоженным.$B$BВам кажется, что самым разумным было бы не трогать здесь ничего.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5099', 'ruRU', 'Мы с коллегами собираем все известные яды и испытываем их на наших врагах. Как только мы закончим исследования, я создам такую чуму, какой этот мир еще не видел.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5046', 'ruRU', 'Верно, раньше я был человеком. Но сейчас, $N, это не изъян, а преимущество – ведь я близко знаком с человеческими слабостями.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5089', 'ruRU', 'Благодаря Алжернону я могу работать, не отвлекаясь на каждую травку. Если мне и суждено распрощаться с чудесами природы, это будет нескоро.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10041', 'ruRU', '', 'Оставь меня, |3-6($c). Я должна исполнять свои обязанности по отношению к послу Печали Солнца, какими бы нудными они ни были.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1734', 'ruRU', 'Добро пожаловать в \"Тысячу мелочей Синей Луны\", |3-6($c). Если у вас есть вопросы, обращайтесь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5193', 'ruRU', 'У меня не так много времени, чтобы тратить его на всяких чужаков. Излагай свое дело или убирайся!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5228', 'ruRU', '', 'У меня нечасто бывают посетители. Ты, никак, ищешь что-то особенное?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5294', 'ruRU', 'Когда я выбрался из могилы, то думал, что родственники встретят меня с радостью. Но они с криками выгнали меня из деревни, и я больше не понимал их языка.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5355', 'ruRU', 'До моей смерти я был библиотекарем, я не имел ничего общего с войнами и чумой. И никогда не хотел стать этим. С другой стороны, теперь, когда мой разум вновь принадлежит мне, я не сдамся без боя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5162', 'ruRU', 'Мне некогда языком чесать, я профессионал!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5918', 'ruRU', '', 'Между светом и тьмой есть тень… И эти тени обладают силой, которую ты еще не видишь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5885', 'ruRU', 'Мне кажется, ваших умственных способностей недостаточно для постижения тонкой магии порталов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1619', 'ruRU', 'Хотите стать кузнецом? Хотите ковать клинки, отсекающие головы врагам? Молоты, дробящие черепа? О, да. Я могу помочь, я с удовольствием помогу вам, если это повлечет за собой кровопролитие.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1617', 'ruRU', 'Созданная тобою броня может выдержать удар даже самого острого топора! Я впечатлен, $N, такое увидишь не часто. Разыщи орка по имени Шайс Холодная Ярость. Лучшие мастера, чем она, есть только в проклятом логове дворфов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1616', 'ruRU', 'Ступайте в Оргриммар. Вы найдете ее и ее потомство в Аллее Чести.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1621', 'ruRU', 'Ты делаешь хорошие мечи, $N. Они режут камень и остаются достаточно острыми, чтобы перерезать горло. Тем не менее я видел кое-что получше. Орк по имени Кергул Кровавый Топор делает превосходные мечи. Иди и научись у него всему, что он умеет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1620', 'ruRU', 'Отправляйтесь в столицу орков, Оргриммар, там вы найдете его в Аллее Чести у магазина \"Легендарное оружие\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3421', 'ruRU', 'Я мечтаю научиться делать оружие и доспехи из костей моих врагов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13461', 'ruRU', '$gДруг мой:Красавица;, добро пожаловать в парикмахерскую! Хочешь сделать стрижку или изменить цвет волос? А может быть, внести какие-то другие изменения в свой образ?$B$BМы можем подровнять бороду и усы, сделать пирсинг, нанести новые татуировки ночным эльфам и даже изменить форму рогов и щупалец у дренеев!$B$BПрисаживайся, чувствуй себя как дома. От нового облика тебя отделяют всего несколько монет!..', '$gДруг мой:Красавица;, добро пожаловать в парикмахерскую! Хочешь сделать стрижку или изменить цвет волос? А может быть, внести какие-то другие изменения в свой образ?$B$BМы можем подровнять бороду и усы, сделать пирсинг, нанести новые татуировки ночным эльфам и даже изменить форму рогов и щупалец у дренеев!$B$BПрисаживайся, чувствуй себя как дома. От нового облика тебя отделяют всего несколько монет!..', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3653', 'ruRU', 'Я – городской чиновник, работаю над переписью населения Подгорода. Со времени нашего освобождения от Плети мы стараемся тщательно отслеживать численность населения города. Благодаря системе чар и усиленной работе над собой я способен осуществлять доступ к этим данным. Если у вас связанное с этим дело – изложите его, пожалуйста. Во всяком случае, вам следовало бы это знать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6159', 'ruRU', 'Добро пожаловать в подгородское бюро регистрации гильдий. Чего желаете?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13583', 'ruRU', 'Ищешь гербовую накидку?', 'Ищешь гербовую накидку?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8434', 'ruRU', 'Орде и Альянсу пора обратить свои взоры к Нордсколу и вспомнить о вторжении Плети. В мгновение ока войска Плети атаковали Калимдор и Восточные Королевства. Достанет ли тебе мужества не дать им опустошить наши земли?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8471', 'ruRU', 'Война снова захлестнула нас. С холодного севера к нам вторглись летающие некрополи Короля-лича. Он разослал их по всему миру.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8486', 'ruRU', 'Плеть создает небольшие опорные пункты в атакуемых районах, а для связи и снабжения использует летающие некрополи. Судя по тому, что нам известно, избавиться от них можно только одним способом: уничтожив наземные войска под ними.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8573', 'ruRU', 'Основные силы Плети сосредоточены не у главных городов Азерота, а в следующих областях:$B$BАзшара, Выжженные земли, пустыня Танарис, Пылающие степи, Восточные Чумные земли и Зимние Ключи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8481', 'ruRU', 'Пока что на этой территории нет войск Плети. Но, боюсь, их возвращение – только вопрос времени.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8483', 'ruRU', 'Плеть продолжает наступать на Выжженные земли. Нам необходим каждый крепкий воин, способный помочь остановить их.$B$BСейчас там $2280W |4некрополь:некрополя:некрополей;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8484', 'ruRU', 'Если немедленно не отправить дополнительную помощь в Пылающие степи, то, боюсь, Плеть полностью захватит эти земли. Если можешь, отправляйся туда и помоги обороняющимся.$B$BЧисленность некрополей на данный момент – $2281W.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8594', 'ruRU', 'Плеть продолжает наступление в Восточных Чумных землях. Нам необходимы все боеспособные воины.$B$BЧисленность некрополей на данный момент – $2282W.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8555', 'ruRU', 'Мы взяли верх над войсками Плети уже $2219W |4раз:раза:раз;! Держись, $N! Мы одержим верх!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13915', 'ruRU', 'Серебряный Рассвет организовал штаб сопротивления Плети в часовне Последней Надежды, что в Восточных Чумных землях. Указания ты можешь получить там.', 'Серебряный Рассвет организовал штаб сопротивления Плети в часовне Последней Надежды, что в Восточных Чумных землях. Указания ты можешь получить там.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8454', 'ruRU', 'У меня много дел и мало времени, |3-6($r)! Пожалуйста, не мешай.', 'У меня много дел и мало времени, |3-6($r)! Пожалуйста, не мешай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4814', 'ruRU', 'Еще $gодин простак, решивший:одна наивная особа, решившая; послужить Темной Госпоже…$B$B<Натанос вздыхает.>$B$BВсех ваших предшественников я отправил ей обратно… по частям. Почему бы сейчас не поступить так же?$B$BВпрочем, на этот раз я не буду задаваться вопросом, а просто выполню просьбу Госпожи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8452', 'ruRU', 'Наши караваны уходят два раза в день, но будь я проклят, если Плеть не уберется с наших путей, чтобы остановить Дидье и его мулов при попытке доставить подкрепление на фронт. Самая сумасшедшая вещь, что я когда-либо видел. Если бы только у нас были героические искатели приключений, чтобы помочь защитить караван…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8357', 'ruRU', 'Я ожи…$B$B*Римблат кашляет.*$B$BИзвини, старая привычка. Я Римблат из Служителей Земли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12649', 'ruRU', '', 'Тебя ждет Нордскол! Великолепный дирижабль \"Поцелуй небес\" перенесет тебя через море прямо в Лагерь Возмездия, что в Ревущем фьорде.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12332', 'ruRU', 'Сейчас я занят.', 'Сейчас я занята.', 'Тебе нужно что-то починить? Воспользуйся \"Торг-о-троном\"!', 'Тебе нужно что-то починить? Воспользуйся \"Торг-о-троном\"!', 'У меня сейчас нет времени говорить.', 'У меня сейчас нет времени говорить.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12327', 'ruRU', '', 'Добро пожаловать на борт, |3-6($c). Размещайте свои вещи и наслаждайтесь путешествием. Мы хотим, чтобы на борту \"Лиловой принцессы\" вы отдохнули как следует. Внизу у нас стоят торговые автоматы – на тот случай, если вам что-нибудь понадобится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12333', 'ruRU', '', 'Полеты из Гром\'гола в Подгород красивы, хоть и проходят без особых происшествий. Но это не повод спать на работе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12331', 'ruRU', 'Ну, дайте ж поспать пять минут…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12328', 'ruRU', 'Отдых – залог успеха. Если ребята хорошо отдохнули, дело у них спорится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12330', 'ruRU', 'Пожалуйста, дайте передохнуть несколько секунд.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6414', 'ruRU', 'Рад видеть тебя снова, доктор $N. Я слежу за твоей работой и позволю себе заметить, что тебе пора изучить новые приемы перевязки ран.$B$BВозвращайся ко мне время от времени – и я научу тебя всему, что знаю.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6595', 'ruRU', 'Могу ли я показать вам свои товары?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5575', 'ruRU', 'Здесь сквозняк… но мне-то что? Я мертвый.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3666', 'ruRU', 'Хочешь купить таракана?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7903', 'ruRU', 'Мы идем разными путями. Ступай учиться в другое место, |3-6($c).', 'Мы идем разными путями. Ступай учиться в другое место, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9185', 'ruRU', 'Прошу прощения, |3-6($c). Мне показалось, ты из достойных, но это ошибка. Уходи. Я делюсь знаниями только с посвященными.', 'Прошу прощения, |3-6($c). Мне показалось, ты из достойных, но это ошибка. Уходи. Я делюсь знаниями только с посвященными.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4475', 'ruRU', 'Прошу, никогда не стесняйся приходить ко мне, $N. Ты хорошо $gпослужил:послужила; Магате. Уверен, в будущем она еще призовет тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7878', 'ruRU', '', 'Здравствуй, $gюный:юная:c; |3-6($c). Ты хочешь принести мне немного мироцвета? Мы собираем его, чтобы делать зелья и эликсиры для воинов в Ан\'Кираже. Конечно, то, что мы вынуждены участвовать в войне, ужасно, но, если не подготовиться должным образом, все будет куда хуже.$B$BОрда поручила мне собрать побольше мироцвета, $2020w, а пока нам удалось раздобыть лишь $2021w. Если тебя это не затруднит, пожалуйста, собирай их на своем пути и приноси мне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7913', 'ruRU', '', 'Ты $gпринес:принесла; более прочную кожу? Жаль, но ты $gопоздал:опоздала;. У нас уже есть все, что нужно. Но ты поспрашивай других – может, им пригодится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7915', 'ruRU', 'Жалость какая! Готов поспорить, ты специально $gнаточил:наточила; нож, чтобы обеспечить меня свежими шкурами. Увы, |3-6($c), у нас уже достаточно прочной кожи. Ты лучше расспроси других собирателей, вдруг им тоже нужна помощь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7910', 'ruRU', '', 'Ты очень $gлюбезен:любезна; но у нас уже достаточно тяжелой кожи. Но твой нож остер. Может быть, ты пустишь его в ход, чтобы помочь другим собирателям? Удачи!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7924', 'ruRU', '', 'Очень мило с вашей стороны заботиться о нас, |3-6($c). Но, оказывается, у нас уже достаточно бинтов из рунической ткани для этой войны. Не будете ли вы любезны расспросить других собирателей и предложить им свою помощь? Да хранят вас предки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7921', 'ruRU', '', 'Вот неудача! Кажется, у нас уже достаточно бинтов из магической ткани. Может быть, ты сможешь продать их на аукционе? Но прежде, чем уйти, побудь $gхорошим мальчиком:хорошей девочкой; и загляни к остальным собирателям. Возможно, у них будет для тебя поручение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7919', 'ruRU', 'Спасибо, что спросили, но я уже собрал достаточно шерстяных бинтов. Вы поспрашивайте вокруг, наверняка кому-то еще нужна ваша помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7908', 'ruRU', '', 'Знаешь, |3-6($c), сейчас нам больше не нужен лиловый лотос. Спроси кого-нибудь еще, нужна ли твоя помощь, и не мешай моим занятиям. Я не могу попусту тратить время!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7906', 'ruRU', 'Пелекейки знает, что это невероятно, но Орда собрала достаточно огнецвета для войны. Если тебе не лень, отстань от Пелекейки и расспроси других собирателей. Им наверняка нужна твоя помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8092', 'ruRU', 'Тром\'ка, |3-6($c) Орды! Будь $gгордым:гордой;! Я полководец Горчук, командир военной кампании Орды. Нас ждут нелегкие времена, но мы победим, как всегда.$B$BЕсли у тебя есть вопросы – задавай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8096', 'ruRU', 'Ситуация такова: медные слитки – $2018w из $1998w, оловянные – $2005w из $2006w, мифриловые – $2008w из $2009w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8097', 'ruRU', 'Мне только что принесли отчет. Сейчас у нас имеются следующие припасы: мироцвет – $2020w из $2021w, огнецвет – $2050w из $2051w, лиловый лотос – $2054w из $2055w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8098', 'ruRU', 'Одну минутку… столько цифр – не упомнишь… Итак, по последним данным заготовлены следующие припасы: толстая кожа – $2066w из $2067w, плотная кожа – $2070w из $2071w, грубая кожа – $2073w из $2074w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8099', 'ruRU', 'Бинты? Что, надеешься выжить? Ха! Ладно, |3-6($r), я расскажу о текущих запасах. Шерстяные бинты – $2079w из $2080w, бинты из магической ткани – $2085w из $2086w, бинты из рунической ткани – $2089w из $2090w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8100', 'ruRU', 'Еда! Я могу говорить на эту тему бесконечно! Итак, постный стейк из волчатины – $2095w из $2096w, пятнистый желтохвост – $2102w из $2103w, запеченный лосось – $2105w из $2106w!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7949', 'ruRU', 'Приветствую вас, |3-6($r). Я – командир Мощный Молот, полномочный представитель Альянса. Если у вас, $gмолодой человек:девушка;, есть вопросы об успехах Альянса в подготовке к войне, не стесняйтесь их задавать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7951', 'ruRU', 'Отличный вопрос, |3-6($c). Последнее, что я слышал, – это то, что Альянсу удалось выплавить $1997w из $1998w медных слитков, $2002w из $2003w слитков стали и $2011w из $2012w слитков тория, нужных для фронта', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7952', 'ruRU', 'Посмотрим, посмотрим… Где-то у меня тут было записано. Ага, вот же. Так, удавник – $2047w из $2048w, лиловый лотос – $2053w из $2055w… И, наконец, слезы Артаса – $2057w из $2058w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7953', 'ruRU', 'Забавно, что вы спросили именно сейчас… я как раз только что получил свежую сводку. К настоящему моменту в Альянсе собрали $2060w из $2061w кусков тонкой кожи, $2063w из $2064w кусков жесткой кожи и $2069w из $2071w кусков плотной кожи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7954', 'ruRU', 'Бинты, говоришь? Да, ну, если мне не изменяет память, по последним сведениям, Альянс сделал: льняных бинтов – $2076w из $2077w, шелковых бинтов -$2082w из $2083w и бинтов из рунической ткани – $2088w из $2090w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7955', 'ruRU', 'Это мне напомнило, что мне надо вскоре найти что-нибудь поесть. Насчет того, что готовят в Альянсе, мне сказали, что уже упаковано $2092w из $2093w радужного тунца. Кроме того, $2098w из $2099w жареного ящера и $2101w из $2103w пятнистого желтохвоста подготовлено и сейчас хранится в холодильнике, ожидая отправки в Крепость Кенария, в Силитусе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7867', 'ruRU', 'Согласно последним подсчетам, у нас уже достаточно медных слитков. Расспросите других собирателей, вдруг им что-нибудь нужно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8073', 'ruRU', 'Рекомендательный жетон Орды присуждается $gгерою, добившемуся:героине, добившейся; выдающихся достижений на службе Орде. Сотрудники отдела Рекомендаций различных фракций Орды есть во всех крупных городах, поговори с теми из них, кто представляет фракцию, доброе отношение которой ты хочешь заслужить, и добудь им нужное количество жетонов!$B$BКстати, если во время войны ты захочешь обменять жетоны на предметы, разыщи полководца Горчука.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7871', 'ruRU', 'Больше никакого олова! У меня уже руки устали складывать эти слитки. Да и собрали мы уже достаточно. Пойди расспроси остальных собирателей – может, кому-нибудь из них нужна твоя помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7877', 'ruRU', '', 'Знаешь, а мы уже собрали достаточно мифриловых слитков для этой войны. Но с другими припасами все не столь хорошо. Расспроси остальных сборщиков. И спасибо тебе, $gдружище:подруга;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8074', 'ruRU', '', 'Рекомендательные жетоны Орды выдаются за особые заслуги перед нашими народами. Во всех крупных городах есть отделы рекомендаций, принадлежащие различным фракциям Орды. Если вы хотите улучшить отношения с какой-либо фракцией, поговорите с ее представителем и отдайте ему нужное количество жетонов.$B$BАх, да! Во время войны жетоны можно обменивать не только на репутацию, но и на материальные ценности. По этому вопросу обращайтесь к вождю Горчуку.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7926', 'ruRU', '', 'Похоже, у нас уже достаточно постных стейков из волчатины. Мне сказали, чтобы я все упаковала и приготовила для отправки в Силитус, в Крепость Кенария. Но, возможно, кто-то еще не успел собрать припасы, и ему требуется помощь. Поспрашивайте.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7930', 'ruRU', 'Спасибо за беспокойство, |3-6($c), но у нас уже достаточно запеченного лосося. Будьте добры, расспросите остальных собирателей. Наверняка кому-нибудь из них нужна помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7928', 'ruRU', 'Привет, |3-6($c)! Похоже, пятнистого желтохвоста у нас уже достаточно. Может, расспросишь других сборщиков – вдруг им нужна твоя помощь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8593', 'ruRU', 'Плеть продолжает наступление в Азшаре. Нам необходимы все боеспособные воины.$B$BЧисленность некрополей на данный момент – $2279W.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8480', 'ruRU', 'Действительно, Зимние Ключи пытаются противостоять новым нападениям Плети. Твоя помощь была бы неоценима.$B$BСейчас там $2284W |4некрополь:некрополя:некрополей;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12292', 'ruRU', 'Я ни в чем не виноват… Я даже не вижу, куда мы летим!$b$bХотя, возможно, это как раз значит, что я виноват…$b$bНет! Погоди! Я вполне могу приземлить этот корабль… Наверное, это все приказы нашего капитана! Видимо, он захотел проверить пределы возможностей экипажа, испытать наши навыки выживания и инженерную смекалку.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5002', 'ruRU', 'Не трать мое время.', 'Не трать мое время.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8809', 'ruRU', '', 'Здорово мы Заблудших провели, а? Теперь им не избежать проклятья! Еще немного, и можно будет заняться самими дренеями.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12927', 'ruRU', 'Большая часть моих братьев следует варварским обычаям Орды, и когда-то я тоже был среди них. Но когда меня впервые призвали в это чудное, мокрое и… ароматное место, я проклял свою прошлую жизнь.$b$bВо время учебы я научился ценить то, что меня окружает. Согласись, в этом месте есть своя привлекательность!$b$bЖаль, что ты не маг… тебе вряд ли будет интересно то, чем я занимаюсь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1820', 'ruRU', 'Если Рит как-то связан с поджогом \"Последнего привала\", его надо найти и допросить.$b$bЯ отправил по его следу Огрона. Они ушли на юго-восток, в сторону главной дороги через топи. Повстречайся с Огроном, а затем найди Рита.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3298', 'ruRU', '', 'Кар Аман, $N. Ты, как вижу, $gсильный:сильная:c; |3-6($c), но не забывай держать себя в рамках приличия. Тот, кто трогает вещи, в которых ничего не понимает, или выказывает неуважение к старшим, заслуживает наказания.$B$BМожешь не уважать меня или моих спутников, но, если тебе не достанет ума, научишься уважать нашу силу.$B$BНо довольно любезностей. Могу я тебе чем-нибудь помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3587', 'ruRU', '', 'Не все Отрекшиеся подчиняются Сильване, $N. Некоторых из нас ведет истинная сила – сила, которую заслужили мы сами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3586', 'ruRU', '', 'Слушай старших, $N. Если ты $gсилен:сильна; духом и $gпредан:предана; своей цели, любые силы поддадутся тебе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10877', 'ruRU', 'Не трепись о том, что ты видишь и слышишь в этих местах, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8469', 'ruRU', 'Здесь находятся разнообразные объявления, в том числе, о наградах.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9643', 'ruRU', '', 'Ну, как тебе тут, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9645', 'ruRU', '', 'Осмотритесь и скажите, заинтересовало ли вас что-нибудь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8659', 'ruRU', 'Несмотря на то что мы прибыли в землю обетованную, нам еще многое предстоит сделать, $N. Наш народ не достигнет Кель\'таса, если мы будем бродить по Запределью, как потерянные.$B$BДолжны быть найдены безопасные пути, ресурсы должны быть сохранены, а наших врагов мы должны обойти или уничтожить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8681', 'ruRU', '', 'Моего мужа нет уже несколько дней. Мы отправились в паломничество вместе, и завершить его должны были вместе.$B$BЯ так волнуюсь, что слишком часто теперь прикладываюсь к кристаллам маны.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10022', 'ruRU', '', 'Несколько дней назад эти люди прибыли из Шаттрата, и с тех пор не прекращают спорить.$b$bХорошо, что вас это не смущает, а то я уж было испугалась, что они отвадят от меня всех покупателей.', '', 'Надеюсь, эти чудаки из Шаттрата скоро прекратят драку. Они начинают действовать мне на нервы.$B$BЧем я могу вам помочь?', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10365', 'ruRU', 'С помощью своего брата Запа из Круговзора я соорудил пространственную подрывочную машину прямо здесь, в зоне 52. Кому нужны ракеты, если можно сразу же попасть по назначению благодаря гоблинскому инженерному делу?! А ты достаточно разбираешься в гоблинском инженерном деле, чтобы научиться собирать маячок проходчика своими руками?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12052', 'ruRU', '', 'Хе, думаешь, у тебя силенок много, а? Тогда иди-ка, продержись на арене, а потом я кое-что дам тебе в награду. Не пожалеешь, точно тебе говорю!$b$bАх да, и желаю удачи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10031', 'ruRU', 'Если вы были в Зангартопи, то, может быть, вам встречался некто Дэниэлс. Знаете, в таком странном головном уборе, и откликается только на обращение \"Специалист\". $b$bКонечно, это я научил его всему, что он знает. $b$bПусть эта красивая одежка вас не обманывает. В душе я по-прежнему разбойник. Мы с Дэниэлсом провели немало времени в окопах, но это было еще до того, как я ушел на покой. Насколько я знаю, он все еще там, задает им жару.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9980', 'ruRU', 'Рад видеть друга Алдоров в этих отдаленных местах. Боюсь, я больше не в состоянии слушать высокомерную болтовню этих эльфов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10042', 'ruRU', '', 'Пока Ша\'тар сосредоточены на Долине Призрачной Луны и Иллидане, мы не можем игнорировать угрозу, которую представляет Кель\'тас Солнечный Скиталец.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9922', 'ruRU', 'Кристаллическая конструкция вибрирует от заключенной в ней энергии. Странные знаки появляются перед вами и быстро сменяют друг друга.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9832', 'ruRU', 'Ладно. Смотри, что принесли ветры Пустоты. Заходи, садись и отдохни. Плата невысока, ты уж точно можешь себе это позволить.', 'Ладно. Смотри, что принесли ветры Пустоты. Заходи, садись и отдохни. Плата невысока, ты уж точно можешь себе это позволить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10687', 'ruRU', 'Я Заревхи из Консорциума. Мои навыки в трансмутации известны в тысяче миров!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12647', 'ruRU', 'Эй, ты. Если хочешь попасть в Нордскол, тогда твой выбор – \"Могучий Ветер\". Он донесет тебя прямо до крепости Песни Войны в Борейской тундре.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7181', 'ruRU', 'Здесь разбросана стопка бумаг и чертежей.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12281', 'ruRU', 'Добро пожаловать на борт \"Девичьего каприза\". Нас ожидает приятное плавание.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7714', 'ruRU', 'Победитель уже определен, но Джанг все еще готов купить любую вкуснорыбу, которую ты поймаешь!\r\n\r\nА свое искусство рыболова ты сможешь продемонстрировать в следующее воскресенье.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7641', 'ruRU', '*Вж-ж-ж* *ЩЕЛК*\r\n\r\nЯ запрограммирован принимать рыбу высшего сорта и выдавать взамен рыболовные аксессуары Ната Пэгла. Введите рыбу.\r\n\r\n*БУМ* *Бз-з-з*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7696', 'ruRU', '', 'Мастер Мормыш специально для состязания выпустил в эти воды много вкуснорыбы.\r\n\r\nПосле того как он определит победителя, я готова платить тебе за каждые 5 вкуснорыб, которых ты мне принесешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12271', 'ruRU', 'Привет, $gпарень:малышка;. Ты сейчас на борту шхуны \"Девичий каприз\". Зовут меня капитан Крутимотор. Тебе выпала честь плыть на флагмане Разбойников Черноводья. Пираты Кровавого Паруса к нам и близко не подходят – знают ведь, что команда у меня отменная, а в трюме полно пороха. Так оно, знаешь ли, безопасней.$b$bЕсли что непонятно, я отвечу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12272', 'ruRU', 'Да, на нем ты и стоишь. Красивый корабль, правда? Барон не так давно назначил меня капитаном. Но сам корабль не мой, имя ему давал не я, и как по мне, тот, кто это имя давал, слишком уж много времени провел в море, и слишком мало – на берегу, если ты понимаешь, про что я.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12273', 'ruRU', 'Если твой живот урчит, поговори с галерным коком Сталебрюхом, но, между нами говоря, ты бы лучше $gподождал:подождала;. У него недаром такое прозвище.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12274', 'ruRU', 'Ну что за корабль без механика, а? Гнутая Игла поможет вам во всем, что нужно. И очень ловко и быстро все починит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12284', 'ruRU', 'Урр… Если этот грязный корабельный повар предложит тебе свое блюдо, окажи услугу – выбрось еду в океан. Этим можно убить несколько макрур – вместо твоего здоровья.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10613', 'ruRU', 'Здравствуй, $gсмертный:смертная;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9488', 'ruRU', 'Малуказ без умолку болтает об исторической важности этого леса. По мне, так у нас и в настоящем забот хватает, чтоб еще о былом печься.$B$BДа, да, его дело тоже важно, но кто-то должен думать о насущном – например, о защите и припасах.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9522', 'ruRU', '', 'Должно быть, я где-то ошиблась, раз меня назначили сюда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9472', 'ruRU', 'Будь начеку, |3-6($c). Этот лес полон скрытых опасностей и потенциальных врагов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9392', 'ruRU', 'Этот лес – странное место. Он полон плохих джуджу, которые даже хуже Альянса, – так что будь настороже.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9485', 'ruRU', 'Костеклювы – я их всех ненавижу!!!$b$bЕсли бы не потеря глаза и эти ослабевающие заклинания, которые на меня иногда воздействуют, я бы только и делал, что их отстреливал!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9489', 'ruRU', 'Пока я не прошел через Награнд и не вышел в лес, мне казалось, что большая часть нашей культуры утеряна. Я рад, что ошибался.$B$BЗдесь так много надо сделать!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9425', 'ruRU', 'О нет, опять ты! Убирайся!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9407', 'ruRU', 'Мы разузнаем всю правду об этой резне, чего бы это нам ни стоило!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9444', 'ruRU', 'Спасибо тебе еще раз, $N, за то, что $gпомог:помогла; мне узнать, что же случилось в чаще.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7794', 'ruRU', '', 'С огромным удовольствием отвечу. Подарочные купоны ярмарки Новолуния – это наш способ оплатить вашу помощь на ярмарке, а взамен вы можете получить за эти купоны специальные подарки!\r\n\r\nЧтобы начать зарабатывать купоны, вам нужно будет поговорить с Хроносом, Керри Хикс, Ринглином и Эббом Ниблгиром – смотрите сами, кому из них нужнее ваша помощь. У них всегда найдется что поделать, чтобы улучшить и расширить ассортимент наших увеселений.\r\n\r\nА когда наберете достаточно купонов, поговорите с Гелвасом Углешлюзом, и он предоставит вам список призов, из которого вы сможете выбрать свой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11220', 'ruRU', '', 'Я тебе очень советую навестить Воржея, узнать твою судьбу. Знаю, знаю… он – гнолл. Но он особый гнолл, способный предсказывать будущее!$B$BА еще на нашей ярмарке постоянно появляются новые аттракционы!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11743', 'ruRU', '', 'Ультрапушка 5000 Покрышкинса – это переносное орудие, настоящее произведение искусства! Ее снаряды летят с околодраконьей скоростью, поэтому пушка по праву является гордостью семьи Покрышкинс, известной на весь мир своими изобретениями в области воздухоплавания и порохового дела!$B$BЕсли хочешь взлететь под облака и парить там, как птица, поищи Максиму Покрышкинс на Террасе Света в Шаттрате. Она выстрелит тобой из пушки, и ты попадешь прямо в маленький пруд за городскими стенами!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11482', 'ruRU', 'Приветствую, друг мой! Добро пожаловать на лучшее представление в Запределье!$b$bПожалуйста, проходите и посмотрите, что мы предлагаем посетителям. Взгляните на чудеса, которые ярмарка Новолуния собрала по всему миру! Мы устроили здесь восхитительный парк развлечений для детей всех возрастов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7358', 'ruRU', 'Если вы соберете полную колоду Порталов, то получите старшую карту Новолуния: Энергию Круговерти Пустоты. Эта карта время от времени будет возвращать вашу душу в тело после смерти и воскрешать вас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7359', 'ruRU', 'Собрав всю колоду Стихий, ты будешь $gвознагражден:вознаграждена; редкой картой Новолуния. Эта карта будет порой наносить сокрушительный удар врагам, с которым ты станешь сражаться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7360', 'ruRU', 'Если ты соберешь всю колоду Полководцев, я дам тебе редкую карту Новолуния под названием \"Героизм\", которая будет лечить тебя в ближнем бою.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10834', 'ruRU', 'Если вы соберете колоду Мстителей, то получите за нее старшую карту Новолуния – Отмщение. Носите ее с собой, и тогда всякого, кто ударит вас, настигнет священное возмездие.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10832', 'ruRU', 'Если вы сможете собрать полную колоду карт, то получите в награду карту Новолуния \"Безумие\". Сила безумия будет наполнять вас каждый раз, когда вы убиваете врага.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10833', 'ruRU', 'Если вы соберете колоду Благословений, то получите старшую карту Новолуния, Поход. Она увеличит урон, наносимый вашими заклинаниями и оружием в бою.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10831', 'ruRU', 'Если ты сможешь собрать колоду карт Ураганов, то будешь $gвознагражден:вознаграждена; редкой картой Новолуния, которая зовется Гнев. Это увеличит твой шанс нанести критический удар твоими заклинаниями или физическими атаками.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9346', 'ruRU', 'Если ты хочешь заняться ювелирным делом, найди магазин Калинды на южном краю Королевской биржи.', 'Если ты хочешь заняться ювелирным делом, найди магазин Калинды на южном краю Королевской биржи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5282', 'ruRU', '', 'Кожа, чудесная кожа! Она удерживает ваши внутренности… внутри. Возможно, вы заметили, что моя кожа не так уж хороша. Подумать только, а когда-то я так переживала из-за пор и угрей! А теперь и переживать-то не из-за чего.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6335', 'ruRU', '', 'Я и мои сестры спустились с Когтистых гор, чтобы покончить с давним несчастьем. Увы, мы не настолько сильны, чтобы справиться самим, и потому ищем себе в помощь смертных вроде тебя.$B$BЕсли у тебя хватает сил, то мы, |3-6($c), можем многое обсудить. Если же нет, то, ради твоей же безопасности, я не стану разглашать здесь подробности моей миссии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5335', 'ruRU', 'Лучшие шкуры получаются из тех кодо, которые прилегли отдохнуть в этой глуши.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4975', 'ruRU', 'Сундук мой устроен весьма хитро; чтобы его открыть, нужны два ключа, золотой и серебряный. Первый, серебряный, ключ я отдал своему первому помощнику, а золотой оставил себе.$B$BНо нам было суждено потерять их. Мой ключ был утрачен при очередной атаке наг, а ключ первого помощника – на день позже, когда нас атаковали жестколапые крабы и утянули его с собой в пучину.$B$BЯ уверен, что ключи у наг и крабов-макрур. Я рад, что они враги и не станут делиться друг с другом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4976', 'ruRU', 'После нападения макрур мы поняли, что следующего нападения \"Доблесть\" не выдержит. Поэтому я с несколькими уцелевшими моряками покинул корабль.$B$BИ вот мы на суше. Собираемся отправиться к Высоте Найджела. На пути туда нас ждет множество опасностей, и я боюсь, что мы сгинем в Пустошах.$B$BЯ пишу эти строки в надежде, что кто-нибудь проникнется нашей бедой и отомстит водным тварям, погубившим нас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8528', 'ruRU', 'Любая помощь, какую я только могу предоставить, в твоем распоряжении.', 'Любая помощь, какую я только могу предоставить, в твоем распоряжении.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9335', 'ruRU', 'Жрецы нашего прекрасного города находятся у Дворца Ярости Солнца. Поищите леди Белестру.', 'Жрецы нашего прекрасного города находятся у Дворца Ярости Солнца. Поищите леди Белестру.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9007', 'ruRU', 'Свет дает нам силу и магию, чтобы одержать победу!', 'Свет дает нам силу и магию, чтобы одержать победу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9401', 'ruRU', 'Труп одного из Сломленных лежит здесь. Странно, но непохоже, чтобы его убили бабочки-мутанты.$B$BВозможно, дело в странном предмете, лежащем прямо рядом с его вытянутой рукой?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9519', 'ruRU', '', 'Благодарю тебя еще раз, $N. Без твоей помощи мы никогда бы не справились с Советом Теней в деревне Грангол\'вар.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10744', 'ruRU', '', 'Конфликты в Запределье смертоноснее всех, что я когда-либо видела. И самые печальные жертвы – это дети, о которых забывают.$B$BТеперь, с этой угрозой араккоа Скеттиса Шаттрату, станет еще больше сирот, о которых нужно заботиться. Эти дети часто живут без всякой надежды на светлое будущее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9361', 'ruRU', 'Кто это так смело встал перед Лантрезором Клинком? Неужели ты не узнаешь смерть в лицо, $gпарень:девушка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9637', 'ruRU', 'Круг Испытаний был некогда местом великой чести и славы. Но потом пришел Картель Хитрой Шестеренки, с повозками золота и писчими перьями, которые были острее любого топора орков…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9277', 'ruRU', 'Духи сильно взволнованы. Нам нужно выяснить причину их беспокойства.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9271', 'ruRU', 'В Гарадаре полный беспорядок, $gнезнакомец:незнакомка;. С тех пор как Великая Мать слегла, Гаррош впал в глубокую депрессию. Он совсем не похож на отца.$B$B*Куркаш вздыхает.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9288', 'ruRU', 'Гезхе здесь, чтобы закончить работу. А я здесь, чтобы его в это время никто не беспокоил.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5248', 'ruRU', 'Ты $gпришел:пришла; учиться мудрости Хгарта?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10249', 'ruRU', 'Отдохни с дороги, $gскиталец:скиталица;. Будь как дома.', 'Отдохни с дороги, $gскиталец:скиталица;. Будь как дома.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10465', 'ruRU', 'Власть над этой землей ускользает из рук Иллидана. Ты это чувствуешь, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10424', 'ruRU', '', 'Слава наару, $N. Свет восторжествует вскоре над своими врагами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10473', 'ruRU', '', 'В юности я старательно училась, чтобы стать жрецом-анахоретом. Женщин-экзархов тогда почти нигде не было.$B$BСейчас же пол мало что значит, главное – смелость и твердая вера.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8455', 'ruRU', 'У меня слишком много дел и слишком мало времени, |3-6($r)! Пожалуйста, уйди с моего пути!$B$BЕсли ты $gболен:больна; слизняком или вурдалачьим гноем, я могу тебе помочь.', 'У меня слишком много дел и слишком мало времени, |3-6($r)! Пожалуйста, уйди с моего пути!$B$BЕсли ты $gболен:больна; слизняком или вурдалачьим гноем, я могу тебе помочь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8474', 'ruRU', 'Где же она? Я не так давно послал свою ученицу Шатарию на запад, к Беспросветным рудникам. Нам необходимо получить некоторые образцы руды, и как можно быстрее, иначе наш эксперимент провалится!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9020', 'ruRU', 'У меня есть свежее мясо, рыба и грибы. И море выпивки!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8487', 'ruRU', '', 'Можешь оставаться в Анклаве Странников сколько пожелаешь, |3-6($c). Любой, кто согласен сражаться в Призрачных землях, для нас желанный гость.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8638', 'ruRU', '', 'Здравствуй, |3-6($c). Тебе непременно нужно поговорить со странницей Соланной, если, конечно, ты этого еще не $gсделал:сделала;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8564', 'ruRU', 'Иногда я стою тут, смотрю на лес по ту сторону озера и вижу землю такой, какой она была когда-то… зеленеющей и живой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8429', 'ruRU', 'Что привело тебя сюда, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8479', 'ruRU', 'Среди различных кусков рукописей, содержащих техническую документацию и другую информацию о Странниках, один лежит отдельно. На нем изображен ужасный тролль, возможно, самое страшное существо, которое ты когда-либо $gвидел:видела;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9005', 'ruRU', '', '*Эраланна, судя по всему, жует что-то вроде смерть-травы.*$b$bЕсли ты ищешь, как сделать любовное зелье, ты $gпришел:пришла; не туда. Ослепление, оглушение и убийство – вот мой бизнес, как, смею надеяться, и твой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8466', 'ruRU', '', 'Если вы хотите обсудить вопросы импорта, |3-6($c), вам уместнее попросить аудиенции у госпожи Златилен. Но предупреждаю: она очень занята. Возможно, вам придется подождать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8446', 'ruRU', 'Чего тебе надо, |3-6($r)? Ты что, не видишь, что я занят?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9019', 'ruRU', '', 'Пришли за товарами для кузнецов, верно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8620', 'ruRU', 'Это не моя вина… ЭТО НЕ МОЯ ВИНА!!!$B$BЕсли бы они сделали все, как я сказал, все было бы в порядке. Но нет! Они не слушали.$B$BА теперь они все мертвы.$B$BЯ хочу домой. Домой, в Луносвет. Я так устал.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8424', 'ruRU', '', 'Я отправила Салиссу на юг в анклав, а Летвалина – на запад в Транквиллион. С тех пор прошло уже больше суток! Ты не $gвидел:видела; кого-нибудь из них? Не тебя ли прислали на помощь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8566', 'ruRU', 'Должен отметить, |3-6($c), что ты и яд $gдоставил:доставила;, и с троллями $gразобрался:разобралась;, как они того заслуживают. У тебя есть все задатки $gталантливого юноши:талантливой юной леди;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8571', 'ruRU', '', 'Одно стадо троллей упокоено, осталось еще одно. Ты даже не представляешь, как я благодарна тебе за помощь, $N! До твоего появления я и не думала, что мы сумеем совладать с кланом Призрачной Сосны…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10380', 'ruRU', 'Банк Алдоров находится к северо-западу от центра города.', 'Банк Алдоров находится к северо-западу от центра города.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8432', 'ruRU', 'Такое удовольствие – видеть тебя снова, |3-6($c). Нет, правда. Ведь ты – источник моих денег.$B$BИтак, что я могу предложить тебе сегодня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12381', 'ruRU', 'Страх всегда был нашим лучшим оружием. Целые войны выигрывались и проигрывались на шатких плечах трепета и сомнения. Так как тогда сражаться с врагом, не ведающим страха?$B$B<Саурфанг качает головой.>$B$BМы не просто сражаемся с армией Плети в Нордсколе, мы стремимся получить контроль над нашими чувствами прежде, чем они разорвут нас на части…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13735', 'ruRU', 'Ну че, как дела?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14041', 'ruRU', '', 'Чем я могу тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14077', 'ruRU', 'Эй, ты! Иди сюда! Пируй, пей, отдыхай перед следующей битвой!$b$bНо не вздумай сражаться! Иначе я скормлю тебя своим волкам.', 'Эй, ты! Иди сюда! Пируй, пей, отдыхай перед следующей битвой!$b$bНо не вздумай сражаться! Иначе я скормлю тебя своим волкам.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14014', 'ruRU', 'Пожалуйста, уважай законы Даларана, пока ты находишься здесь, $gчужеземец:чужестранка;.$B$BТы $gзаблудился:потерялась;? Я могу тебе чем-нибудь помочь?', 'Пожалуйста, уважай законы Даларана, пока ты находишься здесь, $gчужеземец:чужестранка;.$B$BТы $gзаблудился:потерялась;? Я могу тебе чем-нибудь помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13834', 'ruRU', 'Нельзя допустить, чтобы Альянс узнал тайны Берега древних и обратил их против нас. Ты поможешь нашим храбрым воинам дать отпор захватчикам?', 'Нельзя допустить, чтобы Альянс узнал тайны Берега древних и обратил их против нас. Ты поможешь нашим храбрым воинам дать отпор захватчикам?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14027', 'ruRU', 'Добро пожаловать в Клоаку! Могу я тебе помочь? Что ты ищешь?', 'Добро пожаловать в Клоаку! Могу я тебе помочь? Что ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14026', 'ruRU', 'Выбраться из Клоаки можно тремя путями: по Скатам, которые находятся на западе и на востоке, и по туннелю, который ведет вниз, ко дну Даларана.$b$bОднако пользоваться этим туннелем имеет смысл, только если вы умеете летать.', 'Выбраться из Клоаки можно тремя путями: по Скатам, которые находятся на западе и на востоке, и по туннелю, который ведет вниз, ко дну Даларана.$b$bОднако пользоваться этим туннелем имеет смысл, только если вы умеете летать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14020', 'ruRU', 'В западной части Стоков, к северу от таверны \"Черный ворон\", есть туннель, ведущий вниз и наружу.$b$bСамые отчаянные путешественники пользуются им, чтобы покинуть Даларан.', 'В западной части Стоков, к северу от таверны \"Черный ворон\", есть туннель, ведущий вниз и наружу.$b$bСамые отчаянные путешественники пользуются им, чтобы покинуть Даларан.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13575', 'ruRU', 'Это портал на поле боя \"Низина Арати\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13577', 'ruRU', 'Это портал на поле боя \"Око Бури\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7728', 'ruRU', 'Я поставляю сюда разные предметы, необходимые для колдовства и алхимии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7773', 'ruRU', 'Из камня доносится ясный голос… $b$bТы $gпришел:пришла; к нам по праву, и мы выслушаем тебя. Но не испытывай наше терпение, |3-6($c). У Совета нет времени на праздную болтовню с такими, как ты.', 'Ты $gпришел:пришла; к нам по праву, и мы выслушаем тебя. Но не испытывай наше терпение, |3-6($c). У Совета нет времени на праздную болтовню с такими, как ты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7770', 'ruRU', 'Кристалл покрыт замысловатыми знаками и рунами. Тебе они кажутся совершенно бессмысленными.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7717', 'ruRU', 'Позади меня находится алтарь Занзы, святыня Зандалара. Здесь мы молимся об укреплении нашей решимости в борьбе с кровавым богом Хаккаром.$B$BДокажите, что вы друг нашего племени – уничтожьте на алтаре все брелоки Хаккари, которые найдете в Зул\'Гурубе. Кстати, зул\'гурубские монеты я могу обменять на почетные знаки нашего народа. Приносите их мне!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7455', 'ruRU', 'Твоя решимость просто восхитительна, $N. Вновь и вновь подтверждается, что ты – настоящее сокровище нашего народа. Когда-нибудь мир оценит размах твоих великих подвигов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7615', 'ruRU', 'Здравствуйте, |3-6($c). Я Экзал, слуга Растахана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7534', 'ruRU', '', 'Чего ты хочешь, а?$B$BМайвики имеет дело только с друидами и шаманами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7707', 'ruRU', '', 'Знаки Силы – остатки древней империи Гурубаши. Мы, племя Зандалар, ищем их, ведь они очень ценны для нас не только своей исторической значимостью, но и тем, что, как мы считаем, содержат в себе сильнейшую магию, применявшуюся в старой империи.$B$BВ Зул\'Гурубе можно найти три вида Знаков: монеты, украшения и изначальные предметы. Принеси их нам в нужном количестве, и получишь в награду лучшие изделия Зандалар.$B$BНаше слово верное!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7574', 'ruRU', 'Приветствую! Я Рин\'вошо, торговец Зулдазара. Я приехал из Южных морей и привез много экзотических товаров.$B$BЕсли вы докажете, что достойны, то я, пожалуй, поторгую с вами. А если у вас есть почетные знаки моего племени, я покажу вам все свои товары!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8063', 'ruRU', 'Чтобы незаметно подкрадываться к силитидам, нужен большой опыт. Я бы не советовал тебе делать этого без крайней необходимости.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7747', 'ruRU', '', 'Рада видеть вас, $N. Благодаря вашей помощи мы заметно повысили безопасность наших путей снабжения. Я надеюсь, вы понимаете всю важность логистики в военных операциях подобного масштаба?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7771', 'ruRU', 'Тихий шепот доносится из камня… $b$bПривет, $gмалыш:малышка;. Есть новости для храмовника?', 'Изнутри камня доносится тихий шепот…$b$bПривет, $gмалыш:малышка;. Есть новости для храмовника?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7733', 'ruRU', 'Наш командир последнее время сам не свой. Его любимая поехала в археологическую экспедицию в южную пустыню – и там исчезла без следа! Очень странная история.$B$BА ты, небось, работу ищешь? Тебя огреть по голове, или ты сам намек понял?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7775', 'ruRU', 'Из камня раздается громоподобный глас…$B$BЯ чувствую твой статусный медальон, культист, но ведь у тебя нет кольца власти? Настоящий ли ты командир Сумеречного Молота или просто состоишь на побегушках у старших по званию?', 'Из камня раздается громоподобный глас…$B$BЯ чувствую твой статусный медальон, культист, но ведь у тебя нет кольца власти? Настоящий ли ты командир Сумеречного Молота, или просто состоишь на побегушках у старших по званию?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7744', 'ruRU', 'Прочь, безбожник! Чтоб тебя Бездна поглотила!', 'Прочь, безбожник! Чтоб тебя Бездна поглотила!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12508', 'ruRU', '', 'Вы очень много сделали для нас. Я тут по важному делу, но если вам нужно попасть в Пещеры Времени, я с удовольствием доставлю вас туда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7752', 'ruRU', 'Эй, спасибо, что спасли мою шкуру! Я пока что отсижусь в тишине, пока силы не восстановятся.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7749', 'ruRU', 'Как ты смеешь обращаться к герцогу Совета, ничтожество?!', 'Как ты смеешь обращаться к герцогу Совета, ничтожество?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10450', 'ruRU', 'Добро пожаловать в таверну станции Тошли. Чем могу быть полезен?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12611', 'ruRU', 'У нас здесь ничего не осталось.', 'У нас здесь ничего не осталось.', 'Мою семью безжалостно уничтожили. Даже детей.', 'Мою семью безжалостно уничтожили. Даже детей.', 'Пусть Король-лич горит в аду за то, что он призвал на эту землю.', 'Пусть Король-лич горит в аду за то, что он призвал на эту землю.', 'Мы теперь народ, у которого нет своего дома.', 'Мы теперь народ, у которого теперь нет своего дома.', 'Плеть – машина, не ведающая страха и направленная на уничтожение всей жизни в этом мире.', 'Плеть – машина, не ведающая страха и направленная на уничтожение всей жизни в этом мире.', 'Тот, кого они называют Адским Криком, может оказаться нашей единственной надеждой.', 'Тот, кого они называют Адским Криком, может оказаться нашей единственной надеждой.', 'Теперь лишь Орда может спасти нас.', 'Теперь лишь Орда может спасти нас.', '', '');
INSERT INTO `npc_text_localized` VALUES ('12618', 'ruRU', 'Даст ли Орда мне шанс сразиться с Плетью?', 'Даст ли Орда мне шанс сразиться с Плетью?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12619', 'ruRU', 'Чтобы отомстить за свой народ, чтобы изгнать заразу, захватившую нашу землю – я принесу вашу клятву. Я отдам Орде все, что у меня есть, и все, что я собой представляю.', 'Чтобы отомстить за свой народ, чтобы изгнать заразу, захватившую нашу землю – я принесу вашу клятву. Я отдам Орде все, что у меня есть, и все, что я собой представляю.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12620', 'ruRU', 'Лок\'тар огар! Победа или смерть – вот первые слова моей присяги Орде. В них заключена священная истина, суть существования каждого воина Орды.$b$bОтныне мои плоть и кровь принадлежат Вождю. Все, что я делаю, я делаю ради Вождя. Я – оружие в его руках.$b$bС этого момента и до конца своих дней я буду жить и умру – ЗА ОРДУ!', 'Лок\'тар огар! Победа или смерть – вот первые слова моей присяги Орде. В них заключена священная истина, суть существования каждого воина Орды.$b$bОтныне мои плоть и кровь принадлежат Вождю. Все, что я делаю, я делаю ради Вождя. Я – оружие в его руках.$b$bС этого момента и до конца своих дней я буду жить и умру – ЗА ОРДУ!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14561', 'ruRU', '', 'Это ужасно! Я вынуждена сидеть тут, к сожалению, хотя у меня еще так много дел! Если бы я не повредила руку в Нордсколе... Но, что уж теперь жаловаться. Таху заштопает мои раны, и пока они будут заживать, я найду себе занятие в городе.\r\n\r\nЕсли мы не на поле боя – это еще не значит, что мы ничем не можем помочь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13003', 'ruRU', 'Именно с этого трона я отдаю команды Адского Крика. Его слово – жизнь и его слово – смерть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12718', 'ruRU', 'Что? Ты хочешь у меня тренироваться, |3-6($c)? Тогда будь $Gготов:готова; к кропотливому труду.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12678', 'ruRU', 'Мы сокрушим Артаса! Плети не устоять перед мощью Орды!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12657', 'ruRU', 'Разыскиваются приговоренные к СМЕРТИ за измену и прочую вероломную деятельность против Орды!$B$BВсе вознаграждения выплачивает капитан Горт.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12812', 'ruRU', 'Да, чего тебе?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12698', 'ruRU', 'Выполняй свои обязанности по сдерживанию сил Плети, солдат! Докладывай обо всей подозрительной активности и сначала убивай, а потом задавай вопросы!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12703', 'ruRU', 'Мои обязанности в Молоте Агмара простираются гораздо дальше, чем уход за животными в конюшне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13089', 'ruRU', 'Что такое? Не видишь, я занят?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12715', 'ruRU', 'Оставь меня в покое, балда!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12713', 'ruRU', 'Добро пожаловать в храм Драконьего Покоя, $gпутешественник:путешественница;.$B$BЗдесь драконы мирно обсуждают свои дела посреди костей своих прославленных предков.$B$BПо крайней мере, так было до тех пор, пока синие драконы внезапно не напали на наш храм.$B$BСтупай, мы благословляем тебя, но знай, что мы не приемлем никакой агрессии внутри наших стен.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14138', 'ruRU', '', 'Приветствую тебя, |3-6($r). Твое присутствие – честь для меня.$B$BЧем я могу тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14151', 'ruRU', '<Супруг королевы кивает в знак того, что ему известно о вашем присутствии.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12714', 'ruRU', '', 'Хочешь отправиться обратно, |3-6($c)?$B$BЯ могу позаботиться о том, чтобы один из этих драконов донес тебя в целости и сохранности туда, куда ты хочешь попасть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12853', 'ruRU', 'Не беспокой меня по мелочам!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12721', 'ruRU', 'Спасибо, что $gспас:спасла; меня, $N. Мне никогда не забыть твоей отваги и решительности.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12722', 'ruRU', 'Я сам почту за честь сражаться рядом с тобой, $N, как брат Орды. Я принимаю твое предложение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12699', 'ruRU', 'Попомните мои слова, эти отрекшиеся дураки всех нас до могилы доведут!', '', 'Если ты хочешь узнать мое мнение, а ты $gЗАХОТЕЛ:ЗАХОТЕЛА;, я считаю, что мы должны убить всех фармацевтов, пока еще не слишком поздно!', '', 'Орда – это честь и война. А в действиях аптекарей, я уверен, нет ничего честного.', '', 'Я приглядываю за этими отрекшимися идиотами. За ними нужен глаз да глаз!', '', 'Если у тебя есть дело вон к тому доктору, будь $gосторожен:острожна;. Чем бы они там не занимались, это противоестественно.', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12942', 'ruRU', '', 'Близость Черного Дракона так страшит тебя?$B$BЧто ж, хорошо. Так и должно быть. Эти драконы тоже испытывают страх. Однако они придерживаются древней добродетели, а потому не нападут на меня – по крайней мере, здесь.$B$BГлупцы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12976', 'ruRU', '', '$N, как я рада тебя видеть!$B$BИли же это наша первая встреча?$B$BПрямо сейчас я нахожусь в столь многих местах и временах разом, что совершенно не могу за всем этим уследить!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12727', 'ruRU', '', 'Привет, |3-6($r)! Мне жаль, что тебе довелось впервые увидеть нашу священную землю, когда она в таком состоянии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12850', 'ruRU', 'Я сыграл свою роль в уничтожении целых цивилизаций… то, что мне приходится разбираться с каким-то жалким Культом Проклятых, вызывает у меня отвращение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12946', 'ruRU', 'ДА!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13018', 'ruRU', 'Приветствую, |3-6($r).$B$BЕсли ты $gхотел:хотела; бы помочь в обороне храма, пожалуйста, поговори с лордом Афрасаcтразом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12887', 'ruRU', 'Королева Алекстраза поставила меня во главе защитников Храма Драконьего Покоя.$B$BКак тебе известно, силы Малигоса уничтожили находившийся неподалеку якорь силовых линий. Затем драконы перенаправили эту силу в Лазуритовое святилище драконов.$B$BСиние драконы используют ее, чтобы стать сильнее; причем настолько, что они смеют атаковать сам замок! Они начинают побеждать.$B$BТы здесь, чтобы помочь нам, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12954', 'ruRU', 'Когда-то в прошлом чернокнижник Некрос Дробитель Черепов воспользовался мощным артефактом, чтобы поработить Алекстразу и значительную часть красных драконов. Зулухед и Некросс мертвы, но наш долг перед красными драконами еще далеко не выплачен.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12758', 'ruRU', '', 'С осторожностью ступай по саду Изеры, смертное существо. Вещи здесь иногда являются совсем не тем, чем кажутся.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13581', 'ruRU', 'Как дела?$B$BТы $gпришел:пришла; поесть, $gбратишка:сестренка;? Или, может быть, научиться готовить у лучшего шеф-повара, а?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12575', 'ruRU', 'Земля в полном смятении!$B$BСнова надвигается Темный со своим Сен\'лейном, драконы сражаются в своей великой войне Нексуса, и снова проснулись врайкулы.$B$BТвой народ плавает на север на своих деревянных кораблях, чтобы сражаться с ними и друг с другом, а мы, народ калу\'ак, меж двух огней.$B$BА теперь свершилось какое-то великое зло, и земля взрывается от магической энергии мира. Мы должны найти безопасную гавань, где у нас будет шанс выжить.$B$BОт этих двух послов у меня голова раскалывается… может быть, ты сможешь помочь моему народу, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12759', 'ruRU', 'Вам что-то нужно? Может быть, починить броню или оружие? Мм?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12750', 'ruRU', 'Верховный палач Рос выдает вознаграждения по предъявлении свидетельства.$B$BТребуются серьезно настроенные личности.$B$BКОММИВОЯЖЕРАМ ВХОД ВОСПРЕЩЕН!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12751', 'ruRU', 'Мы уничтожим живых… ну, в смысле, Плеть, конечно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14177', 'ruRU', 'Разве она не прекрасна? Видишь, как забрезжила потенциальная выгода?', '', 'Кит\'рикс Темный Повелитель пикирует на Валхалас с юго-востока!', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13897', 'ruRU', 'Добро пожаловать в Даларан, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13458', 'ruRU', 'Приветствую тебя, |3-6($c).', 'Приветствую тебя, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12804', 'ruRU', '', 'Думаешь, сможешь вытащить меня отсюда, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12808', 'ruRU', 'Я не знаю, что случилось. Я приехал в Ядозлобь, а потом вдруг очнулся в этой клетке!$B$BТы $gпришел:пришла;, чтобы спасти меня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12805', 'ruRU', 'О, отлично, ты $gпришел:пришла; меня спасти. А теперь выпусти меня отсюда!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12806', 'ruRU', 'Привет, $gприятель:красавица;. Я смотрю, ты тут рядом…$B$BКак насчет того, чтобы вытащить меня отсюда?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12857', 'ruRU', 'Здесь присутствуют значительные силы Серебряного Авангарда, друг. Уж не $gдумал:думала; ли ты, что мы позволим тебе стяжать всю славу $gодному:одной;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12795', 'ruRU', '', 'Потише, |3-6($c). Я тут под прикрытием и шпионю в пользу Ядозлоби.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12846', 'ruRU', 'Да, $gсын мой:дочь моя;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12848', 'ruRU', '<Верховный аббат внимательно смотрит на вас несколько секунд, затем улыбается.>$B$BОчень хорошо, я знаю подходящее место, дитя мое. Просто поцелуй мое кольцо и дай мне знать, когда будешь $gготов:готова;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12849', 'ruRU', 'Потом встреть меня у входа в монастырь. Я приду туда довольно скоро.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12777', 'ruRU', 'Мой отец подарил мне свои боевые доспехи и секиру. Скоро я им найду прекрасное применение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14123', 'ruRU', '', 'Время покажет...', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14050', 'ruRU', 'Ну что, ты $gготов:готова; начать наступательную операцию и отвоевать Подгород для Орды, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14051', 'ruRU', 'Сила стихий с нами, $N. С их помощью мы сделаем все, что в наших силах, чтобы остановить Вариматаса и Гнилесса! ЛОК\'ТАР ОГАР!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12706', 'ruRU', 'Впечатляет, правда? Из этих великанов вышло бы идеальное пушеч… э-э, союзники. Вот этот – их предводитель, но он продолжает отказываться со мной говорить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12707', 'ruRU', 'Здравствуй, |3-6($r). Добро пожаловать, располагайся в этом каньоне вместе с нами, он служит нам укрытием от драконов. Остерегайся гоблинов… им нельзя доверять.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12701', 'ruRU', '$gБрат:Сестренка;, западные пустоши наводнили плохие ребята. Держись оттуда подальше, если хочешь жить!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12725', 'ruRU', 'Ты не станешь $gнастоящим:настоящей; $gбратком:сестренкой;, пока не убьешь дракона… $gбраток:сестренка;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12906', 'ruRU', '', 'Послушай, $N, если ты думаешь, что твои прошлые подвиги возвышают тебя в моих глазах, то ты ошибаешься. Это не так. $B$BЕсли хочешь, чтобы тебя уважали, придется заслужить уважение. А это значит – исполнять мои приказы без вопросов и сомнений. И без исключений.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11688', 'ruRU', 'Приветствую тебя, $gнезнакомец:незнакомка;! В этих краях нуждаются в твоей помощи.$B$BСтрашные корабли с мертвецами причалили к нашим берегам. В этом фьорде стало слишком опасно находиться, и мы решили уйти на север из этих земель.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12937', 'ruRU', '', 'Хочешь прокатиться, |3-6($r)?$B$BИли просто поболтать по душам?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13504', 'ruRU', '', 'Смотри в оба, |3-6($r).$b$bЗдесь либо ты, либо тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2496', 'ruRU', 'Гррр, фррр…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2873', 'ruRU', 'Эта великая святыня была создана много лет назад ночными эльфами, которым тогда принадлежали эти земли.$B$BКакие секреты может она скрывать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2054', 'ruRU', 'Для безопасного спуска я даю крылья тем, кто в них нуждается.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8552', 'ruRU', '', 'Если у тебя есть деньги, у меня есть еда и питье для тебя. Каждая монетка идет на поддержание и развитие приюта.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8556', 'ruRU', 'У меня есть товары на любой вкус, |3-6($c). Вот, полюбуйся!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6334', 'ruRU', 'Да хранит тебя Кенарий, |3-6($c).$b$bКруг Кенария отправил меня и моих сестер в эти проклятые земли с заданием чрезвычайной важности. Но о нем я буду говорить лишь с сильными и опытными – слабые существа только зря сложат голову.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4994', 'ruRU', 'Чем я могу помочь тебе, дитя?', 'Чем я могу помочь тебе, дитя?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8770', 'ruRU', 'Не так давно здесь была ужасная битва. Мы – Стражи Пустоты и Серебряный Рассвет – сдерживали волну демонов, хлынувшую из Темного портала.$B$BТо были мрачные дни, полные кровопролития и отчаяния…$B$BНо королевство Азерота вскоре присоединилось к нам и отбросило назад этих монстров! Битва продолжается далеко за порталом…$B$BВ лагере теперь спокойно, но, я боюсь, что причина этой тишины – безумие, найденное за этими адским порталом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9991', 'ruRU', 'Еще один боец направляется в Оплот Чести… клянусь бородой Курдана, помощь-то им ох как нужна!', 'Еще один боец направляется в Оплот Чести… клянусь бородой Курдана, помощь-то им ох как нужна!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9935', 'ruRU', 'Еще один боец направляется в Оплот Чести… клянусь бородой Курдана, помощь-то им ох как нужна!', 'Еще один боец направляется в Оплот Чести… клянусь бородой Курдана, помощь-то им ох как нужна!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9938', 'ruRU', 'А, еще один победитель в помощь нашему делу? Что ты скажешь, $N?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9939', 'ruRU', '$N, Сыны Лотара сражались здесь, на Полуострове Адского Пламени, двадцать лет. Мы были со всех сторон окружены местными орками и демонами Пылающего Легиона, и хотя наши сердца еще полны мужества, нас все меньше и меньше.$B$BМы действительно рады видеть новое поколение молодых героев Азерота!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8713', 'ruRU', '', 'Здравствуй, $N. Я и прочие Сыны Лотара торчим в этом жалком мире уже почти двадцать лет как. Мы делаем все, что можем, но меня до сих пор поражает то, что сегодняшние наши испытания – лишь тени прошлых наших ошибок.$B$BМожет, ты поможешь нам их искупить?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10275', 'ruRU', 'Сыны Лотара бесконечно сражались. Я удивлен, что мы сохранили нашу экипировку в столь хорошем виде!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8658', 'ruRU', 'Сид – вот как меня звать. Рад видеть новое лицо в наших краях. Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8688', 'ruRU', 'Когда мой отец погиб в этих пустошах, я был совсем ребенком, поэтому знаю о нем только со слов других.$B$BВаши сны меняются, $N? Мои нет. Я всегда вижу только один сон: я исчезаю за алой линией горизонта, а вдалеке сражается огромная армия Легиона. Потом я склоняюсь над телом мужчины, – должно быть, это и есть мой отец, – и плачу. Все его тело изранено, он хватает ртом воздух и пытается что-то сказать, но я так и не могу расслышать его последние слова.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7783', 'ruRU', '', 'Призрачная фигура смотрит на тебя с недоверием и продолжает безмолвствовать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4633', 'ruRU', '', 'Опасные времена сейчас… Будь $gосторожен:осторожна;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8415', 'ruRU', 'Это что, серьезный вопрос? Ты правда не знаешь?$B$BЕсли ты $gтрусоват:трусовата; и боишься замарать свои ручки, то найдешь пластины из черного железа у обитателей Пылающих степей и Тлеющего Ущелья.$B$BНо если ты искатель приключений, не чуждый кровопролитию, то знай: подземелья Пика Черной горы и Глубин Черной горы содержат огромные залежи пластин…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13321', 'ruRU', '', 'Между Штормградом и Аубердином курсирует корабль \"Доблесть\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12248', 'ruRU', 'Боюсь, сейчас я не могу с тобой говорить.', 'Боюсь, сейчас я не могу с тобой говорить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2635', 'ruRU', 'Благодарю, $N! Ты – герой Кратера Ун\'Горо!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7045', 'ruRU', 'Как приятно увидеть живую душу в этом отдаленном уголке мира. Давай прогуляемся. Пусть наши тела согреют друг друга.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6574', 'ruRU', '', 'Это место… проклято.$B$BЯ едва сумела убежать – и теперь даже думать не хочу о том, чтобы снова спуститься в эти тоннели.$B$BА вот мои братья и сестры… им не повезло. Они заперты внутри и окутаны зловещей дымкой, которая стелется из этого сада зла.$B$BПрошу тебя, помоги им.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6375', 'ruRU', 'Мой скипетр… Столько лет прошло… Ты его $gнашел:нашла;! Поистине, ты – $gнаш избавитель:наша избавительница;…$B$BПрошу тебя, $N, выслушай меня внимательно. Соединить в одно целое две части скипетра можно лишь у Камня Мародона.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6373', 'ruRU', 'Благодарю тебя за все, $N. $B$BПрими же этот скипетр. Быть может, он поможет тебе в борьбе со злом, заполонившим этим края.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6336', 'ruRU', 'Приветствую тебя, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14198', 'ruRU', 'Изумрудный Сон сделался опасным местом. Я все сильнее тревожусь за тех, кто пожертвовал своей сознательной жизнью в Азероте в надежде спасти его...', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12642', 'ruRU', 'Если ты хочешь отправиться в Нордскол, ледокол \"Северное копье\" плывет отсюда в город Валгард в Ревущем Фьорде.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13454', 'ruRU', 'Приветствую тебя, |3-6($c)! Порт Штормграда представляет собой впечатляющее зрелище. Особенно если смотреть сверху. Хотелось бы тебе увидеть порт с высоты птичьего полета?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3801', 'ruRU', 'Если ты $gпришел:пришла; охотиться на драконов – будь $gмоим гостем:моей гостьей;. Кстати, по какому адресу мне отправлять соболезнования?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1302', 'ruRU', 'Невероятно… Я слышал, что орки считают запах людей тошнотворным, а люди не переносят запах орков… Но для меня все расы этого мира пахнут одинаково – омерзительной, гнилой жадностью.$B$BТы сомневаешься в моих словах? Разве ты $gсам:сама; не видишь, что всей твоей жизнью управляет жадность? Она определяет каждое твое решение.$B$BДа будет так. Жадность выгнала меня из моего дома, она же и вернет меня туда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10278', 'ruRU', 'Наши союзники из Альянса помогают Лиге Аратора в защите Низины Арати. Им могла бы понадобиться твоя помощь в борьбе против нежити Осквернителей, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5856', 'ruRU', 'Не так быстро, $gгосподин:госпожа;… Я не доверю элитного верхового барана тому, кто не заслужил уважения дворфов Стальгорна. Вот когда вас станут превозносить в нашем городе, я с радостью покажу вам всех скакунов, что у меня есть на продажу.', 'Не так быстро, $gгосподин:госпожа;… Я не доверю элитного верхового барана тому, кто не заслужил уважения дворфов Стальгорна. Вот когда вас станут превозносить в нашем городе, я с радостью покажу вам всех скакунов, что у меня есть на продажу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6575', 'ruRU', '', 'О, как я рада, что ты $gвернулся:вернулась;, $N.$B$BДух моего брата теперь свободен от проклятия, но я все равно останусь здесь и буду предаваться скорби.$B$BБольшое тебе спасибо за помощь, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6377', 'ruRU', 'Между цветами путь.$B$BСветится он на воде.$B$BТой, что тобой создана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6378', 'ruRU', 'Между оранжевым и лиловым проходами, что рядом с дверьми в Мародон, ты найдешь третий путь.$B$BКлюч к нему – скипетр, который ты держишь в руках.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6379', 'ruRU', 'Я устал…$B$BОтсюда ты можешь видеть водопады, $n… Скипетр вернет тебя на это самое место, если вдруг захочешь снова пройти через врата Мародона.$B$BВозле входа в Мародон есть путь, уходящий вниз. Чтобы создать портал для возвращения, воздействуй скипетром на место проведения ритуала.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6812', 'ruRU', 'Взгляни на это разоренное поле! Узри силу Стихий, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6842', 'ruRU', 'Я знаю многое. Слушай и запоминай, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6843', 'ruRU', 'В те времена, что позднее захватчики назовут Эрой Творения, мой повелитель и его дети правили всем этим миром. Это были они, древние боги, создавшие все.$B$BИ это они были свергнуты теми, кто назвал себя Оформителями.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6844', 'ruRU', 'Их видимые формы ослаблены в вечной битве, происходящей под этим миром – миром, который они сами создали, – падение титанов-создателей проникло сквозь поверхность и замутило разум древних богов.$B$BИменно тогда брат пошел на брата. Пять тысяч лет продолжалась битва у центра этого мира.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6867', 'ruRU', 'Во времена Раскола стихий Повелитель Огня Рагнарос вознамерился уничтожить моего господина, Громораана Принца Воздуха.$B$B*Демитриан склоняет голову, его лицо искажено злобой.*$B$BЕсли ты произнесешь его имя, я украшу этот лагерь твоими кишками. Молчи и слушай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6868', 'ruRU', 'Рагнарос, Геддон и Гарр – вот кто виновен в заговоре против моего повелителя. Громораана застали врасплох.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6869', 'ruRU', 'Замыслы Повелителя Огня были абсолютно неизвестны Громораану. Не успел мой Повелитель понять, что происходит, как Сульфурас, легендарный молот Рагнароса, нанес ему немилосердный удар. Громораан пал, полностью побежденный.$B$BПовелитель Огня поглотил сущность моего Повелителя, но не смог уничтожить его полностью.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6870', 'ruRU', 'То немногое, что осталось от сущности Громораана, было помещено в талисман Стихийных оков. Затем Рагнарос расколол этот талисман на две равные части и отдал их своим ближайшим приспешникам…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9085', 'ruRU', '', 'Ох, не обращайте внимания на Малыша. Он вас не тронет, просто иногда любит поточить коготки.$B$BОн ведь как большой котенок.$B$BПравда, Малыш? Да, конечно, ты мой сладенький! Ты мой малышок, мамочкина радость!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9484', 'ruRU', '', 'Не переживай. Мне это много дало.$B$BТаэла кажется одновременно и довольной, и раздосадованной…$B$BЯ – Высший эльф, а не эльф крови. Не волнуйся, я не собираюсь отнимать у тебя эту магию.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10662', 'ruRU', 'Аукиндон – дом духов моих предков. Может, со стороны и не похоже, но все Сломленные когда-то были дренеями… духи у нас одинаковые.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10677', 'ruRU', 'Эти пустоши изуродованы чудовищной магией. Мы должны сделать все, что сможем, для исцеления этих ран…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9568', 'ruRU', '', 'Привет! А не поможешь мне отсюда выбраться?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12526', 'ruRU', 'Артас полагает, что парочка его жучков-нежитей напугает нас так, что мы в ужасе сбежим в Восточные королевства… Ха!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12478', 'ruRU', 'Я лечил крестьян, знать, детей торговцев и нищих. Позволь мне поделиться с тобой своей мудростью: мы все одинаковы, когда смотрим в лицо смерти.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12527', 'ruRU', 'Темные силы активно действуют в этой земле, $N. Держи глаза открытыми и не теряй смелости.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12538', 'ruRU', 'Мы – шахтеры, фермеры и ремесленники – не солдаты. Но мы будем защищать нашу землю голыми руками, если придется.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12521', 'ruRU', '', 'Да будь я проклята, если эта мерзкая Плеть выгонит нас отсюда! Нас, первопоселенцев, отдавших все ради того, чтобы начать новую жизнь в Нордсколе.$B$BМожешь быть $gуверен:уверена;: мы будем драться за каждый клочок земли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12525', 'ruRU', 'Плеть повсюду! Это какой-то… кошмар!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12529', 'ruRU', 'Еще один день на кухне. Я тут точно потихоньку тупею.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12528', 'ruRU', 'Некоторым из нас удается избежать воинской службы. Хороший рыбак всегда может пригодиться… Даже генералам нужно есть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12555', 'ruRU', 'Долг любого солдата – умереть за родину. Но если назвавший эту войну безрассудным самоубийством считается изменником, то я и есть изменник.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12530', 'ruRU', 'Моя смена почти закончилась. Не могу дождаться, когда смогу прикрыть глаза часиков эдак на восемь, понимаешь, о чем я?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12553', 'ruRU', 'Могу ли я предложить вам что-нибудь? Выбор у меня немного ограничен, но это и не удивительно… учитывая обстоятельства.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12534', 'ruRU', '', 'Артас был когда-то принцем королевства людей. Армию, которую он вел в Нордскол, постигла ужасная судьба.$B$BМой брат, Тассариан, служил в той армии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11667', 'ruRU', 'Ступай осторожно, |3-6($r).$b$bТут все усеяно останками моих бравых солдат.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11676', 'ruRU', 'Держи ухо востро, $n.$b$bМы еще не выбрались из чащи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12122', 'ruRU', 'А, свежая рыбка в нашем котелке?$B$B<Зорек сплевывает на землю.>$B$BМда…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13174', 'ruRU', 'Мы то, что приносит промозглый ветер. Ледяной озноб отчаяния… Холодные объятия смерти…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13173', 'ruRU', 'Кровь – это жизнь… И управлять жизнью наших врагов так же легко, как пустить кому-нибудь кровь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13172', 'ruRU', '', 'Благодаря силе нечестивости мы можем повелевать самой смертью!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7026', 'ruRU', 'Достопочтенный доктор Густав ван Склифф может заняться твоим обучением. Он лечит раненых в казармах Терамора.', 'Достопочтенный доктор Густав ван Склифф может заняться твоим обучением. Он лечит раненых в казармах Терамора.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9784', 'ruRU', 'Лагерь экспедиции– ближайшая точка контакта Альянса с Передовой Легиона. К северу отсюда – только демоны да снова сплошные демоны. Без спутника не путешествуй.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10063', 'ruRU', 'Если тебя подташнивает, обратись к Песту. У него может найтись что-нибудь от укачивания.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10009', 'ruRU', 'Мои братья в Штормграде советовали мне не идти в армию… Хорошо, что я их не послушал!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10717', 'ruRU', 'Не волнуйся. Парящая застава вполне крепка. Но на всякий случай будь $gготов:готова; вскочить на грифона, если основание начнет рушиться…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13699', 'ruRU', '', 'Я больше не обучаю рыцарей смерти, меня утомили эти слабоумные новички и их постоянные вопросы.$B$BА теперь отвали.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13262', 'ruRU', 'Что именно тебе нужно от Корвия?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13702', 'ruRU', 'Чем могу помочь?', 'Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12579', 'ruRU', 'Каскалу атакуют! Мы должны сдерживать их напор до тех пор, пока не сможем спастись.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12580', 'ruRU', 'Проклятые великаны! Если они хотят войны, они ее получат!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12578', 'ruRU', 'Квалдиры безжалостны, $N. Но я не могу оставить священную Каскалу. Если мы не покинем духов, духи тоже не покинут нас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12585', 'ruRU', 'Похоже, что этот молодой клыкарр не утонул, а погиб в бою.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12673', 'ruRU', 'Добро пожаловать! Если тебе нужно отдохнуть, я могу помочь с решением этой проблемы.', 'Добро пожаловать! Если тебе нужно отдохнуть, я могу помочь с решением этой проблемы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12435', 'ruRU', 'Я сейчас по горло занят, |3-6($c).$B$BЧем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12456', 'ruRU', 'Ладно, садись. Это может занять какое-то время.$B$BКогда мы прибыли в этот ледяной ад, генерал Арлос, который сидит в крепости Отваги, дал мне с моими сорвиголовами особое задание: установить передовую воздушную базу для операций Альянса.$B$BРезультат – эта взлетная полоса. Скажу тебе, это было непросто – со всеми этими зверьми вокруг и с землей, твердой, как скала, от мороза! Но мы справились – и притом быстро!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12457', 'ruRU', 'В любом случае нам нужно было накачать много песка и нефти для машин. Резервуары поблизости идеально подошли для этого.$B$BЧасть песка даже обладает магическими свойствами… любопытно, не так ли? Но это уже другая история.$B$BИтак, мы выкачали большую часть воды и построили насосную станцию прямо посередине. Все шло превосходно, пока в один прекрасный день главная труба не забилась.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12458', 'ruRU', 'Не забывай, об этом я узнал позже, потому что не был там в тот момент.$B$BКогда они запустили насос, на поверхности показались куски робота, выглядевшего в точности как гном! Конечно, эти дураки работали день и ночь, чтобы собрать его, и ничего нам не сказали.$B$BПосле этого мы потеряли связь с насосной станцией.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12459', 'ruRU', 'Через пару дней тишины я отправил на платформу разведчиков. Они не вернулись.$B$BЯ послал еще одну группу на следующий день (с такими же результатами) и потерял пару ветролетов, пытаясь провести разведку с воздуха.$B$BВ этот момент я отправил кое-кого на юг за помощью, и мы притаились, готовясь к худшему. Мы направили все свои силы на изготовление доспехов, оружия и роботов, чтобы мы смогли направиться туда в полной боеготовности.$B$BКогда мы это сделали несколько дней назад, то просто не поверили своим глазам!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12460', 'ruRU', 'Как я уже говорил, то, что мы там увидели, не поддавалось никаким объяснениям.$B$BМоих людей не было видно. Вместо них была настоящая армия роботов и андроидов, которые сновали по своим делам!$B$BВсе дроиды выглядели как гномы и сказали, что ждали нас. На самом деле они, как ни странно, вели себя так, словно знали нас. Нас окружили и быстро повели на верх насосной станции.$B$BТам мы и увидели их предводителя и узнали, что он делает с оставшимися гномами!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12461', 'ruRU', 'Он называл себя механизатором Механозодом. Когда мы прибыли, он был занят превращением выживших в механические существа!$B$BОн тепло поприветствовал нас и объяснил, что он один из первых гномов, когда-либо созданных чем-то, что он называл \"Великим Зодчим\", Титаном-смотрителем залов легендарного Ульдуара.$B$BПо-видимому, он был той блокировкой, которую моя команда случайно вытащила оттуда, где он сломался тысячелетия назад. Так уж нам подфартило, что построили насосную станцию прямо над ним.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12462', 'ruRU', 'Механики, работающие на станции, доставили его назад и вернули к \"жизни\".$B$BА теперь, в качестве благодарности, он собирался помочь нам, излечив всех нас от того, что он называет \"Проклятье Плоти\".$B$BСогласно толкованию Механозода, это состояние, в котором в конце концов будут все создания Титанов! Другими словами, мы все, якобы, рождаемся чем-то наподобие роботов и через тысячи лет медленно эволюционируем в создания из плоти и крови!$B$BЗвучит абсурдно, я знаю.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12463', 'ruRU', 'Это не выглядело, на мой взгляд, никаким \"исцелением\", и я не собирался стоять и слушать какого-то безумного робота, пока он уничтожает моих людей!$B$BМы сделали то, что сделал бы любой нормальный гном на нашем месте… мы убежали! Ну, точнее, большинство из нас просто сигануло с площадки… в общем, можешь себе представить.$B$BНемногие из нас смогли вернуться сюда, и нас по-прежнему на все не хватает: нужно справляться с магнатаврами на севере и с Плетью – на востоке. К тому же на северо-западе только что обосновались ордынцы!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12464', 'ruRU', 'И вот где мы сейчас застряли, $N.$B$BМы никак не можем справиться со всем этим сами, и, честно говоря, мне как-то не по себе. Джинки знает, что нужно делать. Тебе следует пойти повидаться с ней.$B$BЯ лишь надеюсь, что ты сможешь вытащить всех нас из этой заварухи невредимыми.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12842', 'ruRU', 'Что? Не видишь, что я занят?$B$BДумаешь, этот передачник сам себя починит?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12423', 'ruRU', '', 'Как я могу что-то сделать, если Выкрутень все время стоит у меня над душой, а у меня в руках нет моих инструментов?!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13132', 'ruRU', '', 'Если тебе нужно отправиться куда-нибудь, я могу этому помочь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12554', 'ruRU', 'А?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12489', 'ruRU', 'Пилот разведывательного самолета не выжил после того, как его группа потерпела аварию.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12852', 'ruRU', '', 'Говори тише. Мы же не хотим привлечь внимание этих тварей, верно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12097', 'ruRU', 'Мы построили этот порт голыми руками! Слезами, потом и кровью!$B$BПоверь мне на слово – мы никогда не позволим захватить Валгард!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12190', 'ruRU', 'Вон тот парень очень переживает из-за своего коврика.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12153', 'ruRU', 'Приветствую тебя, |3-6($r). Я – Идальго, великий охотник и прославленный сокольничий. А вот этого крылатого убийцу на моем плече зовут Экcельзиор.$B$BТы, наверное, не раз $gзадавался:задавалась; вопросом, в чем состоит работа сокольничих. Так вот, я отвечу тебе – мы охотимся! Укрощение ловчих птиц – это искусство дрессировки, благодаря которому мы можем научить хищных птиц охотиться и приносить добычу. Может быть, тебя заинтересует эта профессия? Я мог бы взяться за твое обучение!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12168', 'ruRU', '<Сокол с жадностью хватает червяка.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12185', 'ruRU', 'С возвращением, $N!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12189', 'ruRU', 'Это я-то проиграл? Уолт несет чушь! Я этих железных дворфов одной левой уделаю!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12191', 'ruRU', 'Добрый вечер…', 'Добрый вечер…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12193', 'ruRU', 'По…полагаю, ты хочешь вернуться обратно на аванпост, да?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12194', 'ruRU', 'Верстак Уолта готов к работе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11498', 'ruRU', '', 'Всех этих ребят нужно накормить и напоить! И кто, по-твоему, должен этим заниматься? Я, конечно!$B$BЯ и готовлю, и посуду мою, и убираюсь…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11322', 'ruRU', 'Эй! Эти проклятые пираты отрезали нас от гавани!$B$BПожалуйста, помоги нам прорвать блокаду! Наш аэроплан будет здесь через $3078w |4минуту:минуты:минут;. Садись на борт и сбрось на них парочку бомб.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11494', 'ruRU', 'Мы не должны были оставлять так много войск в Валгарде. Это все адмирал Келлер, напыщенный, эгоистичный маньяк!$B$BКак мне выполнять его приказы, когда у меня нет людей? Мне нужны солдаты для защиты этого аванпоста и целый гарнизон для крепости Вилдервар, которая вот-вот будет достроена.$B$BНо теперь я хотя бы смогу рассчитывать на твою помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11504', 'ruRU', 'Я много чего повидал в своей жизни, но то, что случилось с Лигой Исследователей в Шепчущей теснине – это уже за гранью добра и зла!$B$BХорошо, что я вовремя унес оттуда ноги.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12862', 'ruRU', 'Куда желаешь отправиться?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11418', 'ruRU', 'В Скорне идут бои, вдали слышна канонада… И чем, по-твоему, я тут занимаюсь?$B$BПодбираю использованные ядра!$B$BРазве для этого я вербовался в армию?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11362', 'ruRU', 'Многие века назад в этих лесах обитала древняя магия, охранявшая царицу племени протодраконов и ее супругов.$B$BНо теперь древнее зло пробудилось и стремится взять контроль над матерью драконьего племени, чтобы использовать ее в своих чудовищных целях.$B$BЭто врайкулы вмешиваются в ход природы, тем самым угрожая вашим народам.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11496', 'ruRU', 'Я, конечно, высказываю капитану свое мнение по многим вопросам, но у меня такое ощущение, что тебя он выслушает с большим вниманием.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12590', 'ruRU', 'Одним словом, магия.$b$bБезответственные смертные с их коротким сроком жизни не задумываются о том, какие долгосрочные последствия ожидают их действия.$b$bВолшебная энергия бралась слишком щедро, нарушая мировой баланс. Если этому позволить продолжаться, скоро Азерот будет уничтожен.$b$bНасчет этого согласны обе стороны. Но прежде, чем должен был быть созван совет, Малигос начал действовать по своему усмотрению.$b$bНам не оставалось ничего другого, кроме как выступить против него.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12593', 'ruRU', 'Малигос Хранитель Магии, Повелитель синих драконов… он – аспект синих драконов, одно из древнейших созданий в Азероте.$b$bГоворят, что он приходит в себя от безумия, которое длилось тысячелетиями, но его недавние действия наталкивают меня на мысль, что до здравого рассудка ему еще далеко.$b$bЕго решение нашей проблемы – найти и уничтожить всех смертных в Азероте, которые владеют магией.$b$bБудучи защитниками жизни, красные драконы были вынуждены вмешаться.$b$bИ вот драконы сражаются с драконами, и судьба нашего хрупкого мира висит на волоске.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12389', 'ruRU', 'Здесь лежит тело одного из погонщиков каравана Стальной Челюсти.', 'Здесь лежит тело одного из погонщиков каравана Стальной Челюсти.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13455', 'ruRU', '', 'Я не думаю, что твой разум способен полностью понять природу магии порталов.$B$BТем не менее ты можешь воспользоваться кристаллом, находящимся за мной, чтобы переместиться в руины под Далараном, что в лесу Хрустальной Песни.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14013', 'ruRU', 'Добро пожаловать в Даларан, $gстранник:путешественница;.$BЕсли ты хочешь что-то найти, может быть, я мог бы помочь?', 'Добро пожаловать в Даларан, $gстранник:путешественница;.$BЕсли ты хочешь что-то найти, может быть, я могла бы помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14002', 'ruRU', 'Самая популярная таверна в Даларане – \"Приют фокусника\", которая расположена к северу от центра города.$B$BТакже таверны есть в кварталах Альянса и Орды. Кроме того, мне говорили об еще одной под городом, в стоках.', 'Самая популярная таверна в Даларане – \"Приют фокусника\", которая расположена к северу от центра города.$B$BТакже таверны есть в кварталах Альянса и Орды. Кроме того, мне говорили об еще одной под городом, в стоках.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13992', 'ruRU', 'Местечко \"Благодарность за отвагу\" расположено совсем рядом с Серебряным Анклавом, в юго-западной части Даларана.$B$BЭто заведение может похвастаться отличным садом спелого хмеля, а также тем, что на его кухне работает один из самых искусных учителей кулинарии в городе.', 'Местечко \"Благодарность за отвагу\" расположено совсем рядом с Серебряным Анклавом, в юго-западной части Даларана.$B$BЭто заведение может похвастаться отличным садом спелого хмеля, а также тем, что на его кухне работает один из самых искусных учителей кулинарии в городе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13328', 'ruRU', 'Добро пожаловать. Могу я предложить тебе отдых или пищу?', 'Добро пожаловать. Могу я предложить тебе отдых или пищу?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13591', 'ruRU', '', 'Привет! Хочешь купить рыболовных снастей или, быть может, услышать парочку мудрых советов?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14000', 'ruRU', 'Учителя каких навыков ты ищешь?', 'Учителя каких навыков ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13996', 'ruRU', 'Учителя каких навыков ты ищешь?', 'Учителя каких навыков ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13990', 'ruRU', 'Ты, без сомнения, захочешь посетить магазин \"Чарующая простота\", который находится совсем рядом с Торговой палатой волшебников в северо-западной части Даларана.', 'Ты, без сомнения, захочешь посетить магазин \"Чарующая простота\", который находится совсем рядом с Торговой палате волшебников в северо-западной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14004', 'ruRU', 'Распорядителя полетов можно найти на площадке Краса в восточной части Даларана.', 'Распорядителя полетов можно найти на на площадке Краса в восточной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12726', 'ruRU', 'Ничто так не будоражит, как охота на драконов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12965', 'ruRU', 'Жди приказа!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12667', 'ruRU', '', 'Если мне удастся сделать новую военную машину – я уверена, что получу кругленькую сумму за нее у Врат Гнева.$B$BНекоторые наши предыдущие циклы не были абсолютно ммм… стабильными, но мой ветролет должен справиться, если я сумею изготовить для него подходящую бомбометательную систему.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12666', 'ruRU', 'А? Что?! Проваливай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12668', 'ruRU', 'На этом можно наварить деньжат! Как говорят в народе: если хочешь сколотить состояние – сколоти его на войне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12708', 'ruRU', 'Будь у меня достаточно денег, я бы купил весь мир…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13977', 'ruRU', 'Порталы в различные столичные города и на поля сражений находятся в соответственных кварталах Альянса и Орды: в Серебряном Анклаве и в Прибежище Похитителя Солнца.', 'Порталы в различные столичные города и на поля сражений находятся в соответственных кварталах Альянса и Орды: в Серебряном Анклаве и в Прибежище Похитителя Солнца.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13973', 'ruRU', 'Прибывающие в Даларан гости из Альянса обитают главным образом в Серебряном Анклаве в юго-западной части города.$B$BТам есть таверна, а также порталы, ведущие в различные города Альянса и на поля сражений.', 'Прибывающие в Даларан гости из Альянса обитают главным образом в Серебряном Анклаве в юго-западной части города.$B$BТам есть таверна, а также порталы, ведущие в различные города Альянса и на поля сражений.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14018', 'ruRU', 'В Даларане единственными учителями классовых навыков являются маги. Чтобы попасть к другим наставникам, тебе придется вернуться через портал в один из крупных городов.$b$bНайти наставников-магов можно в кварталах Альянса и Орды: в Серебряном Анклаве и Прибежище Похитителя Солнца.', 'В Даларане единственными учителями классовых навыков являются маги. Чтобы попасть к другим наставникам, тебе придется вернуться через портал в один из крупных городов.$b$bНайти наставников-магов можно в кварталах Альянса и Орды: в Серебряном Анклаве и Прибежище Похитителя Солнца.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13985', 'ruRU', '\"Уголок начертателя\" находится в Торговой палате волшебников, в северо-западной части Даларана.', '\"Уголок начертателя\" находится в Торговой палате волшебников, в северо-западной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12433', 'ruRU', 'Как я рад тебя видеть! Я слишком слаб, чтобы разбить звенья этой ржавой цепи.$B$BНу же, не стой столбом! Освободи меня!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12663', 'ruRU', 'Все, что я хотел, – это вновь служить моему королю. Защищать наш народ, бороться против общего врага.$B$BА вместо этого я обнаружил, что в наши ряды проникли отродья из культа… и все признаки указывают на Наксанар – источник этой гнили.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13637', 'ruRU', '', 'Мы рады приветствовать тебя, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12810', 'ruRU', '', 'Приветствую, |3-6($c).$B$BЧем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12734', 'ruRU', 'У меня есть еда, если ты $gголоден:голодна;, и есть палатка, если тебе требуется отдых.', 'У меня есть еда, если ты $gголоден:голодна;, и есть палатка, если тебе требуется отдых.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12733', 'ruRU', 'Эти уроды из Плети… скоро они получат по заслугам!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12739', 'ruRU', 'Долины и вершины горы Хиджала, зыбучие пески Силитуса, смертоносные армии Легиона – мы прошли через все. У нас нет имен, мы лишены лиц, сыны и дочери Альянса. Клянусь Светом и мощью Альянса, первый удар принадлежит нам, а последний удар – это все, что увидят наши враги.$B$BМы – Седьмой Легион.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12904', 'ruRU', 'Грифоны нордскольских вершин – самые живучие из своего вида.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12958', 'ruRU', '<Командор останавливает урок и поворачивается, оценивая тебя взглядом, присматриваясь к тебе.>$B$BТы очень кстати, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12823', 'ruRU', '', 'Мы быстро постигаем цену смерти на этих ледяных пустошах, $N. Сила Короля-лича пронизывает каждый дюйм Нордскола, проникая даже в саму землю! Он обращает наших умерших против нас.$B$BВосставшие всадники на грифонах обрушивают свои яростные молоты бурь с неба, а ходячие мертвецы нападают на нас внизу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12843', 'ruRU', 'Верховный лорд Болвар Фордрагон сражается за нас всех, новичок. Если бы не он, Артас уже давно восседал бы на троне Штормграда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12940', 'ruRU', 'Тель\'зан должен умереть, хотя бы для того, чтобы прекратить постоянное нытье!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12941', 'ruRU', 'Объявления от граждан и чиновников крепости Стражей Зимы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12881', 'ruRU', 'Мертвые поднимаются из нашего крипта!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12860', 'ruRU', '<Забытый пехотинец качает головой.>$B$BМы не можем так продолжать, друг. Если принц Артас скоро не вернется, мы все погибнем.', '', 'Нам никогда не успеть подготовить эти корабли вовремя. Зима приближается, и с ней приближается наша судьба. Я боюсь, что молодой принц проклял нас всех.', '', 'Артас и Мурадин пропали очень давно. Надеюсь, с ними все в порядке.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12856', 'ruRU', 'Ты что, не видишь, я занят! Нам никак не подготовить эту базу к возвращению Принца!', '', 'Если бы не эти проклятые твари, поджигающие наши корабли, мы бы уже были дома. А теперь оставь меня в покое, чтобы я мог вернуться к работе!', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12858', 'ruRU', 'Эти проклятые крестьяне должны работать быстрее! Я хочу вернуться домой в Стальгорн. Я очень скучаю по жене и детям.', '', 'Капитан говорит, что как только вернется принц Артас, мы отправимся назад в Лордерон.', '', 'Я не знаю, что задерживает молодого принца, но долго мы не продержимся. Некоторые солдаты уже замерзают от холода.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12882', 'ruRU', 'Ты $gсправился, парень:справилась, девица;! Я был уверен, что тебе конец!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12844', 'ruRU', 'Я так близок к разгадке этой руды. Теперь с минуты на минуту я найду ответ!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12955', 'ruRU', 'Эта сторожка уже некоторое время принадлежит Альянсу. Временами здесь находят приют наши охотники и лесники.$B$BРаз уж идет война, лучше думать, что всем здесь заправляют военные…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13030', 'ruRU', 'Приветствую, |3-6($c)!$b$bНадеюсь, ты не против немного поработать. Мы были бы очень рады, если бы ты нам $gпомог:помогла;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13022', 'ruRU', 'Чем я могу помочь тебе, дитя?', 'Чем я могу помочь тебе, дитя?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12738', 'ruRU', 'Подходи к огню и отдохни, |3-6($r). Надеюсь, ты не возражаешь против запаха рыбы.', 'Подходи к огню и отдохни, |3-6($r). Надеюсь, ты не возражаешь против запаха рыбы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12859', 'ruRU', 'Когда мы увидели, что корабли были сожжены этими свирепыми зверьми, мы пришли в отчаяние. Никогда я не ощущал такой беспомощности…', '', 'Ярость. Вот вкратце мои чувства по отношению ко всей этой каше. Мы продирались через лес к берегу изо всех сил лишь для того, чтобы обнаружить, что опоздали. Наш единственный путь домой был уничтожен.', '', 'Несмотря на тяготы, через которые мы прошли в этой бесплодной земле, я верю, что молодой принц приведет нас к победе. Я каждый вечер молюсь за его безопасность.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9394', 'ruRU', 'Ты, конечно, $gмогуч:могуча;, |3-6($r), но по силам ли тебе стать чемпионом клана Боевого Молота?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9395', 'ruRU', 'Прочь с дороги, |3-6($r). Не видишь – мы здесь бьемся!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12960', 'ruRU', 'Да, |3-6($c), что?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12966', 'ruRU', 'Добро пожаловать в Гранитные ключи, друг.$b$bЕсли у тебя получится поймать одного из этих ледяных троллей живым, обязательно дай мне знать. Мы за них хорошо платим.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12865', 'ruRU', 'Привет, |3-6($r)!$b$bЕсли ты ищешь крошшер для езды, тебе придется отправиться вверх, в холмы.$b$bЭтого придется списать в утиль.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12868', 'ruRU', 'До того, как здесь начались крупные неприятности, мы с их помощью собирали урожай деревьев.$b$b Однако в последнее время им приходится сражаться против Орды. Как оказалось, из них получаются неплохие боевые машины. Так что если у тебя есть ключ – было бы глупо не воспользоваться одним из этих малюток!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12871', 'ruRU', 'У моего брата, Синипуса, их множество. Ты должен направиться на север в лесозаготовки Синего Неба и поговорить с ним.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14007', 'ruRU', 'В городе три банка: банк на севере, торговый банк на юге, и еще один внизу, в Стоках.', 'В городе три банка: банк на севере, торговый банк на юге, и еще один внизу, в Стоках.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14005', 'ruRU', 'Даларанский банк находится в северной части города.', 'Даларанский банк находится в северной части города.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14006', 'ruRU', 'Даларанский торговый банк находится в южной части города, неподалеку от Вечерней Зари.', 'Даларанский торговый банк находится в южной части города, неподалеку от Вечерней Зари.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13974', 'ruRU', 'В пространство под Далараном ведут три пути: входы в Стоки на востоке и на западе, а еще колодец на севере города.', 'В пространство под Далараном ведут три пути: входы в Стоки на востоке и на западе, а еще колодец на севере города.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13960', 'ruRU', 'Западный вход в Стоки находится под лестницей в Аметистовую цитадель.', 'Западный вход в Стоки находится под лестницей в Аметистовую цитадель.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14024', 'ruRU', 'В восточной части Стоков есть скат, который выходит наверх неподалеку от площадки Краса.', 'В восточной части Стоков есть скат, который выходит наверх неподалеку от площадки Краса.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12779', 'ruRU', 'К счастью, этот туалет свободен.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12825', 'ruRU', 'Добро пожаловать в Приют Янтарной Сосны, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12811', 'ruRU', 'Здесь прикован яростный протодракон тана Торвальда Эрикксона', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11320', 'ruRU', 'Эй! Эти проклятые пираты отрезали нас от гавани!$B$BПожалуйста, помоги нам прорвать блокаду! Садись на аэроплан и сбрось на них парочку бомб!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11502', 'ruRU', 'Никогда в жизни я даже не надеялся увидеть столь величественных пейзажей.$B$BЕсли бы только их не оскверняло зло и знание, которому не место в этом мире…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11808', 'ruRU', 'И как это я позволил Петрову уговорить меня поработать с этими никчемными дворфами?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11858', 'ruRU', '', 'Спасибо Лиге Исследователей за то, что взяли меня с собой. Впрочем, учитывая опасности, поджидающие на Вырубках, мне стоило захватить с собой парочку друзей на подмогу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11738', 'ruRU', '', 'Ты услада для усталых глаз!$B$BАх, у меня еще столько дел и так мало времени!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12082', 'ruRU', 'Не правда ли, красавец? Хочешь прокатиться?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11746', 'ruRU', 'Тише, |3-6($c)! Нельзя, чтобы нас услышали!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11436', 'ruRU', 'Капитан Адамс послал меня сюда на разведку. Я должен был узнать, что происходит в Халгринде, но смог добраться только до этого места. Однако я обнаружил заброшенную башню врайкулов, с которой очень удобно вести наблюдение.$B$BТак вот – Отрекшиеся что-то затевают!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11880', 'ruRU', '', 'Эй, ты не видишь – я тут пытаюсь напиться!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12221', 'ruRU', 'Мертвые поднимутся из могил и отомстят тем, кто осмелится шагнуть на их землю. Поверни назад, пока не поздно!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12364', 'ruRU', 'Я – Атуик, старейшина Калу\'ак из Камагуа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12170', 'ruRU', 'Лучше веди себя поскромнее, дружище – если понимаешь, что я имею в виду!$B$BАррр!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11898', 'ruRU', 'Держи, $gдружище:подруга;. Теперь они у нас попляшут!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11622', 'ruRU', '$GСэр:Мэм;! Мне было приказано оказывать вам посильную помощь и обеспечивать защиту.$B$BКакие будут указания?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11912', 'ruRU', 'Конечно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11614', 'ruRU', 'Да озарит тебя Свет, дитя мое.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12174', 'ruRU', 'Сокровища эльфов крови не похожи ни на какие другие ценности. Это не просто золото, шелк или драгоценности. Работа мастеров син\'дорай ценится сама по себе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12636', 'ruRU', 'Никогда не понимал пиратов, которые зарывают сокровища в землю. Предпочитаю держать свои ценности под рукой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12176', 'ruRU', '', 'День за днем этот псих Иона Стерлинг заставляет меня драить палубы.$B$BДраить палубы, представляешь!$B$BЯ маг, раздери меня акула, а не полотер!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12173', 'ruRU', 'Я тут занимаюсь организацией боев. Так что если хочешь сделать ставку – не бойся, у нас все по-честному. Если кто-то пытается мухлевать, его быстро отправляют на рею!$B$BЯ лично за этим слежу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12242', 'ruRU', 'Хей! Хочешь прокатиться на \"Сестренке милосердия\"? А билет у тебя есть?..', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12120', 'ruRU', 'Эй, дружище! Что, потрепало тебя жизнью, соленая ты треска?$B$BА у нас тут все тип-топ!', '', 'Ого, ну и свезло тебе! Че, удалось обчистить жирного маркитанта? Поделись добычей-то. Нечего жмотничать!', '', 'Эй, трави концы, друг! Этот вонючий салага мне всю морду расквасил, сечешь? Ну, я ему задам перцу, тухлая кобыла его мать… своей башкой померяет, сколько футов под килем…', '', 'Клянусь хвостом русалки, это правда, приятель! Характеры пиратов северных морей будут посолонее воды здешнего моря, морской дьявол мне в брюхо. Лучше никому не ложиться на наш курс, тысяча чертей! Только моряк с протухшими мозгами добровольно подведет себя под поцелуй дочки канонира!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12175', 'ruRU', 'Я заплачу! Я все отдам, слышишь! Не бей меня!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13009', 'ruRU', '', 'Только так Гарри может заговорить со мной! И мне приходится все время носить эту дурацкую маску!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14205', 'ruRU', '<Машина громко рокочет.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12179', 'ruRU', '', 'И что тебе налить? Рома? Эля?$B$BИли, может, чего-нибудь другого?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12180', 'ruRU', '', 'Ты $gсобрался:собралась; обчистить Джека у всех на глазах?$B$BЛибо ты очень $gсмелый, либо глупый:смелая, либо глупая;. Ну… это тебе недешево обойдется. Я не собираюсь просто так рисковать жизнью. Да смотри, сначала дождись, когда я уйду – а потом действуй!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12178', 'ruRU', '<Этот пират, очевидно, выпил лишнего и теперь какое-то время будет без сознания.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12634', 'ruRU', 'На что $gуставился, дружок:уставилась, подружка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12186', 'ruRU', '', 'Этот проклятый Иона Стерлинг погубит всех нас. Если бы он хоть иногда высовывал нос из своей пещеры, он понял бы всю безнадежность нападений на военные корабли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12188', 'ruRU', 'Как много изменений произошли с этими землями…$B$BМы, камагуанцы, боимся того, что происходит. Но что ты можешь сделать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12953', 'ruRU', '', 'Не думаю, что мы можем позволить себе пренебречь возможностью втереться в доверие к красным драконам. Учитывая предстоящие битвы, иметь их в качестве союзников стало бы просто неоценимым преимуществом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13025', 'ruRU', 'Добро пожаловать в храм Драконьего Покоя, |3-6($r). Прости нас за шумиху внизу, из-за последних действий Малигоса мы оказались под осадой. Ты можешь тем не менее отдохнуть здесь под нашей защитой, если она тебе понадобится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13989', 'ruRU', 'Инженеры чаще всего проходя обучение в мастерской \"Как часы\", которая находится в Торговой палате волшебников, в северо-западной части Даларана.', 'Инженеры чаще всего проходя обучение в мастерской \"Как часы\", которая находится в Торговой палате волшебников, в северо-западной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14010', 'ruRU', 'В Даларане нет своего аукционного дома. Тебе придется пройти через портал в один из столичных городов вашей фракции.$B$BПорталы находятся в кварталах, принадлежащих, соответственно, Альянсу и Орде: это Серебряный Анклав и Прибежище Похитителя Солнца.', 'В Даларане нет своего аукционного дома. Тебе придется пройти через портал в один из столичных городов вашей фракции.$B$BПорталы находятся в кварталах, принадлежащих, соответственно, Альянсу и Орде: это Серебряный Анклав и Прибежище Похитителя Солнца.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14082', 'ruRU', 'Добро пожаловать в \"Чудо чудное\"!$B$BПожалуйста, не спешите и с чувством, с толком, с расстановкой походите по нашему магазину, посмотрите на великолепные игрушки для детей и взрослых!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14085', 'ruRU', 'Приветствую тебя, |3-6($c).$B$BСейчас Крепость Ледяных Оков находится под контролем Альянса. Если хочешь, можешь переместиться туда с помощью портала.$B$BСледующая битва начнется через $3975w:$3976w$3782w:$3784w$3785w.', 'Приветствую тебя, |3-6($c).$B$BСейчас Крепость Ледяных Оков находится под контролем Альянса. Если хочешь, можешь переместиться туда с помощью портала.$B$BСледующая битва начнется через $3975w:$3976w$3782w:$3784w$3785w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13574', 'ruRU', '', 'Жизнь моя и душа переполнены воспоминаниями о мерзостной Орде. Времена печали, отчаяния и жертв прошли. Сердце мое никогда не покинет ярость битвы!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13833', 'ruRU', 'Нельзя допустить, чтобы Орда узнала тайны Берега древних и обратила их против нас. Ты поможешь нашим храбрым воинам дать отпор захватчикам?', 'Нельзя допустить, чтобы Орда узнала тайны Берега древних и обратила их против нас. Ты поможешь нашим храбрым воинам дать отпор захватчикам?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14172', 'ruRU', 'Извини, друг. Утвердить приобретение машины могут только офицеры Альянса, имеющие соответствующие документы.', 'Извини, друг. Утвердить приобретение машины могут только офицеры Альянса, имеющие соответствующие документы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14161', 'ruRU', 'Добро пожаловать в гости к ордену Кирин-Тор.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13046', 'ruRU', 'Эти земли осквернены присутствием Плети с одной стороны и ритуалами Драккари – с другой.$B$BМы твердо держим наши позиции между ними.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13043', 'ruRU', 'Добро пожаловать на Серебряную заставу, |3-6($c).$b$bРасполагайся как дома, но не слишком расслабляйся. Тут еще полно опасностей.$b$bЕсли мы хотим выбраться отсюда целыми и невредимыми, всем нужно будет поработать… в том числе тебе.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13050', 'ruRU', 'Я Убунго. Я представляю племя Зандалар.$B$BПлохие новости, |3-6($r). Драккари сами станут причиной своей смерти, учитывая, чем они заняты.$B$BНекоторые тролли никогда ничему не учатся.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13679', 'ruRU', 'Похоже, на тебе можно было бы заработать немного денег. Как насчет прогулки по Амфитеатру Страданий, $gдружище:старушка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14162', 'ruRU', 'Добро пожаловать в Приют фокусника, |3-6($c). Надеюсь, тебе здесь понравится.', 'Добро пожаловать в Приют фокусника, |3-6($c). Надеюсь, тебе здесь понравится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13798', 'ruRU', 'Привет тебе, $n! Чем могу помочь?', 'Привет тебе, $n! Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13628', 'ruRU', 'Пожалуйста, сохранять тишина! Малыши засыпать!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13615', 'ruRU', 'Посмотри по сторонам и оцени разрушения, |3-6($c). Все до единого тролли, жившие здесь, или мертвы, или сбежали на верхние уровни.$B$BНо это еще не самое страшное.$B$BВ отчаянии, оставшиеся в живых драккари принесли в жертву своих собственных богов-животных, чтобы выпить их кровь и обрести невиданное доселе могущество. Хотя им удалось отогнать своей новообретенной силой воинов Плети, результаты оказались катастрофическими. В доказательство этого – древняя империя Зул\'Драк лежит в руинах.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13658', 'ruRU', 'Мы влипли по уши, |3-6($r)! Будь осторожнее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13619', 'ruRU', 'Герк, Бурр, Даргат – все они покинули нас… Здесь остались лишь Маккеллар и я. Надеюсь, ты с хорошими новостями.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13639', 'ruRU', 'С моей свободой приходит отмщение шторма!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13270', 'ruRU', 'Никто не знает Плеть лучше, чем мы, рыцари смерти!$b$bПоверь мне, |3-6($r), огонь легче всего победить при помощи огня…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13261', 'ruRU', 'Добро пожаловать в лагерь, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13271', 'ruRU', 'Полегче со старым Нассом. Он не такой молодой и выносливый, как был когда-то.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13290', 'ruRU', '', '<Кровавая Роза с любопытством смотрит на вас.>$b$bВсе-таки интересно, чем ты $gзаслужил:заслужила; такое пристальное внимание?$b$bБез обид.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13045', 'ruRU', 'Ну и в заварушку мы попали.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13070', 'ruRU', '', 'Отличная заварушка.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13047', 'ruRU', 'Да хранит тебя Свет!', 'Да хранит тебя Свет!', 'Эй, ты там, |3-6($c)! У нас тут бой в разгаре.', 'Эй, ты там, |3-6($c)! У нас тут бой в разгаре.', 'Что ты там делаешь?', 'Что ты там делаешь?', 'А ты не $N? Я слышал, ты $gпогиб:погибла;.', 'А ты не $N? Я слышала, ты $gпогиб:погибла;.', 'Только быстрее… сражаться здесь не очень-то приятно. Мне нужно быть начеку.', 'Только быстрее… сражаться здесь не очень-то приятно. Мне нужно быть начеку.', 'Ты – новый рекрут? Это не предписанная уставом форма.', 'Ты – новый рекрут? Это не предписанная уставом форма.', 'Отлично, подкрепление подоспело!', 'Отлично, подкрепление подоспело!', 'У тебя для меня сообщение?', 'У тебя для меня сообщение?');
INSERT INTO `npc_text_localized` VALUES ('13156', 'ruRU', 'Это место безопасно, |3-6($r).$b$bВраг был вытеснен с этой территории некоторое время назад.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13097', 'ruRU', 'Приветствую вас, |3-6($r).$b$bБоюсь, вы должны извинить меня, но я сейчас ужасно занят.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13048', 'ruRU', 'Здесь немножко опаснее, чем ты $gожидал:ожидала;, |3-6($r)?$b$bНе могу сказать, что я удивлен. Я это слышу почти ото всех, кто добирается досюда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13069', 'ruRU', 'Я так больше не могу…$b$bОни убивают тебя, а потом едят! И это если повезет!', 'Я так больше не могу…$b$bОни убивают тебя, а потом едят! И это если повезет!', 'Ты $gдолжен:должна; помочь мне выбраться отсюда, |3-6($r)!$b$bЯ не хочу стать таким, как они!', 'Ты $gдолжен:должна; помочь мне выбраться отсюда, |3-6($r)!$b$bЯ не хочу стать такой, как они!', 'Я не могу ни есть, ни спать с тех пор, как попал сюда.$b$bЯ же не просто отрезанный ломоть для Авангарда…', 'Я не могу ни есть, ни спать с тех пор, как попала сюда.$b$bЯ же не просто отрезанный ломоть для Авангарда…', '<хнык> Мой дядя служил в Серебряном Рассвете.$b$bКак у него получалось противостоять этим злобным тварям?', '<хнык> Мой дядя служил в Серебряном Рассвете.$b$bКак у него получалось противостоять этим злобным тварям?', 'Каждый раз, когда я закрываю глаза, я вижу, как силы Плети окружают меня!$b$bМне нужно выбраться отсюда…', 'Каждый раз, когда я закрываю глаза, я вижу, как силы Плети окружают меня!$b$bМне нужно выбраться отсюда…', 'Я здесь умру… я это просто знаю.', 'Я здесь умру… я просто знаю это.', 'Мы обреченыыыы!!', 'Мы обреченыыыы!!', 'Выбирайся отсюда, пока можешь, |3-6($c)!$b$bНам ни за что не победить…', 'Выбирайся отсюда, пока можешь, |3-6($c)!$b$bНам ни за что не победить…');
INSERT INTO `npc_text_localized` VALUES ('13077', 'ruRU', 'БУМ!$b$bЭто последнее, что я помню…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13265', 'ruRU', 'Подумай обо всем, что тебе довелось повидать за этот день, $n.$b$bБудущее уже предрешено. Изменить ты можешь только свою роль в нем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13157', 'ruRU', '', 'Спасибо, что $gосвободил:освободила; меня, |3-6($r).$b$bМеня все еще немного покачивает от этого яда. Мне кажется, мне лучше остаться здесь, пока мои силы не восстановились.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12899', 'ruRU', 'Приветствую, |3-6($r).$B$BЯ ждал тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12900', 'ruRU', 'Отлично, поехали!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13300', 'ruRU', 'О-о-о! Скачи, веселись!$b$bСмотри, не споткнись!$b$bХа-ха-ха… Спать не вались!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13640', 'ruRU', 'Прежде чем мы начнем, послушай, что я знаю о нашем враге. Тебе лучше быть $gподготовленным:подготовленной;, когда мы будем громить Плеть в чистом поле.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13647', 'ruRU', 'Боевой предводитель имиръяров, Алгар Избранный, поднимает свои армии варгулов в Реликварии Боли, к северо-востоку от этой клетки.$B$BПринц Навариус находится по другую сторону Мертвых полей, к востоку отсюда, возле больших ступеней, ведущих на второй уровень Зул\'Драка.$B$BМестонахождение Трыма в настоящий момент неизвестно, что весьма любопытно, учитывая его размер.$B$BС ними со всеми нужно разобраться!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13348', 'ruRU', 'Еще $gодин соискатель:одна соискательница; благосклонности Короля-лича?$b$bТолько достойные получают темные дары…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13326', 'ruRU', 'Мфмм рмфхимм ррхумгхф?', '', 'Ррррмммммврррр ммммрррм вррррууумм!', '', 'Умфрум-ммфур, мурруфру…', '', 'Мхррумф руммрхум пхурр!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13901', 'ruRU', '\"Усмиритель небес\" патрулирует Ледяную Корону. Ни в одном королевстве ты не найдешь корабля лучше этого!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13932', 'ruRU', 'Время суда близится. В сердце своем ты знаешь, что путь справедливости – единственно праведный.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13609', 'ruRU', 'Рад снова видеть тебя, $N. Как я могу забыть $gтого:ту;, кто спас жизнь моей сестре?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14064', 'ruRU', 'От того, какие действия мы тут предпримем, зависят многие жизни. Так что живее выкладывай, |3-6($c), что там у тебя.', '', 'Какие новости с полей сражений, |3-6($r)?', '', 'Лучшая война – быстрая война. То же самое можно сказать и о беседе. Так что ты хочешь сказать, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14134', 'ruRU', 'Что-нибудь нужно прикрутить, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13325', 'ruRU', 'Ты на верном пути, $gсмертный:смертная;…$b$bТерпи – и получишь большую силу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13391', 'ruRU', 'Вам, силам Плети, не рады в Зул\'Драке!', '', 'Зул\'Драк принадлежит Драккари, $gбратишка:сестренка;. И так будет всегда!', '', 'Здесь Плеть и сгинет, ясно?', '', 'Наш народ убьет всех приспешников Плети до единого!', '', 'Уйди прочь, мерзкий приспешник Плети!', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13390', 'ruRU', 'Выпусти меня отсюда, дружище!', '', 'Ты не из Плети. Ты чего тут делаешь, а?', '', 'Почему Плеть позволяет тебе здесь расхаживать, аа?', '', 'Эй, ты что ли потерявшись тута?..', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13422', 'ruRU', '$n! Я ждал твоего возвращения. Что тебя так задержало, а?$b$bПора завершать обращение Гнилокровных.$b$bПрисоединись ко мне и стань свидетелем создания нашей армии!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13121', 'ruRU', '', 'Прекрасный день для битвы, не так ли, $gгосподин:госпожа;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13068', 'ruRU', 'Истинно, ужасы, которые происходят здесь, превосходят даже те, что мы испытали в Чумных землях.$b$bТеперь силы Плети более могущественны, чем когда-либо.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13118', 'ruRU', '', 'Ах, не нужно было мне идти в специалисты-добровольцы. Они постоянно требуют от нас невозможного!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13117', 'ruRU', 'Вот именно этого нам и не хватало – чтобы эти безумные тролли Драккари сбили некрополь Плети у нас в тылу!$B$BКак им это вообще удалось?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13092', 'ruRU', 'Не стоит так задирать нос!$B$BМогу тебе сообщить, что Ступесты – старинное семейство с давними традициями толчения в ступе.$B$BДа-да!$B$BЛучших алхимиков нет на всем Азероте, да, кстати, и вообще где-либо, могу тебя заверить!$B$BМожет быть, я сейчас ношу звание подмастерья, но в умении я никому не уступаю! Однако приходится выносить такие унижения, чтобы получить место под солнцем.$B$BХотя, может быть, ты здесь затем, чтобы мне помочь?$B$B<Миниатюрный гном нагло улыбается.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13098', 'ruRU', 'Время не ждет, $n.Ты $gготов:готова; пройти испытание?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13119', 'ruRU', 'Быстрее, |3-6($c)!$b$bКлади ингредиенты прямо в горшок. Время не ждет!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13176', 'ruRU', 'Мы все опечалены тем, как Драккари уничтожают самих себя.$B$BСила, которую они получают, убивая настоящих богов этой земли, эфемерна.$B$BВсе, чем они сейчас пользуются, позднее будет утрачено навеки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13234', 'ruRU', 'Топор Ахуна принесет смерть Драккари.$B$BА если и топор подведет, то уж ты-то будешь на подхвате.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13282', 'ruRU', 'Хотя мои соотечественники хотели бы, чтобы вы принимали активное участие в происходящем в Зул\'Драке, мы, зандалары, находимся здесь в первую очередь для того, чтобы наблюдать за происходящим и запечатлеть конец империи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13180', 'ruRU', '', 'Зим\'Торга может предоставить тебе известную степень защиты, но она была очень ослаблена присутствием Плети и действиями драккари.$B$BНа нашу долю с Ауне выпало наблюдать обломки нашей былой защиты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13139', 'ruRU', '', 'Ты станешь моим спасением.$B$BА может быть, моей кончиной.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13291', 'ruRU', 'Драккари держат мое тело при смерти в здании, где алтарь. Но душу мою они не могут видеть.$B$BМы должны вместе помешать им завладеть моей силой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13331', 'ruRU', '', 'Тебе нравится новый дом, что я создала для своих так называемых поклонников?$B$BВ агонии перед моей смертью я прокляла их и затянула вместе с собой в этот ад, созданный моими собственными руками.$B$BЯ думаю, мне стоит наслаждаться их вечными муками, но что я на самом деле хочу, так это отомстить моему бывшему пророку!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13368', 'ruRU', '', 'Я – твой вечный должник, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13850', 'ruRU', '', 'Моей обязанностью будет записать эти трагические события ради блага будущих поколений.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13548', 'ruRU', 'Приветствия, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9481', 'ruRU', 'Если вы ждете официальных приветствий, то вынужден вас разочаровать, |3-6($c). Традиций мы тут не соблюдаем, бесполезное это занятие.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9393', 'ruRU', '', 'Орда – не единственная опасность в этом лесу. Не позволяй кажущемуся спокойствию обмануть тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9492', 'ruRU', '', 'Когда я впервые прибыла сюда, надеялась встретить того, в чью честь назван город.$B$B*Роз\'алета оглядывается по сторонам*$B$BБольшая часть юных Высших эльфов перерастают этап \"я хочу быть Странником\" к тому времени, как дорастут до первого обучения. Тогда настает этап \"я буду магистром\".$B$BШвеей не мечтает стать никто, но, как говорила моя матушка, не ходить же Странникам голыми.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9521', 'ruRU', 'Мы здесь на службе, $gпарень:девушка;, смотрим, чтобы никто не смог застать нас врасплох.$B$BЭто называется упреждающий тактикой!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9566', 'ruRU', 'Слушаю тебя, $gгорожанин:горожанка;. О каком важном деле ты желаешь со мной говорить?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13848', 'ruRU', 'Да, это скорбный день в истории троллей.', '', 'Это кошмар! Не могу на это смотреть!', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12511', 'ruRU', 'Несмотря на то, что варварская деятельность Эрнестуэя в мире дикой природы Нордскол была остановлена, рано или поздно он попытается отыграться. Ты $gдолжен:должна; оставаться начеку, $N!$B$BА главное – не поддавайся искушениям, которые будут возникать перед тобой… демон попытается околдовать тебя. Если это случится, ты $gдолжен:должна; устоять!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14017', 'ruRU', 'Я предвижу, что тебе предстоит поговорить с верховным магом Лан\'далоком, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12617', 'ruRU', '<Бадд держится очень прямо, притворяясь, что не слышит тебя. Приглядевшись к нему поближе, вы видите струйку слюны, стекающую из-под его маски.>', '', '<Бадд качается с пятки на носок, невнятно бормоча что-то на тролльском наречии.>', '', 'Проваливай отсюда, друг.', '', 'Нет времени на разглагольствования, $gбраток:сестренка;. Мне надо прикорнуть…', '', 'Сила троллей, прикинь?', '', 'Что с тобой, $gбраток:сестренка;? Ты что, никогда раньше троллей не $gвидал:видала;?', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12999', 'ruRU', 'Были времена, когда я вырвал бы тебе сердце просто для того, чтобы посмотреть, какое у тебя станет лицо…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12998', 'ruRU', '<Дракуру протягивает свою истекающую кровью руку через прутья клетки и выжидательно смотрит на тебя.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13001', 'ruRU', 'Привет, $gбратишка:сестренка;!$b$bРад снова тебя видеть…$N, да? Да, да, конечно.$b$bМы с тобой будем добрыми друзьями. Дракуру в таких вещах разбирается.$b$b<Раскрашенное лицо тролля медленно расплывается в улыбке.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13011', 'ruRU', 'Привет, $gбратишка:сестренка;!$b$bЭто не совсем то, чем кажется. Ну, может быть…$b$bВсе очень сложно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12659', 'ruRU', 'День добрый, $gприятель:девушка;! Как насчет присесть и пропустить кружечку?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12669', 'ruRU', 'Стоя перед этим древним идолом, вы ощущаете чье-то незримое присутствие.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12670', 'ruRU', 'Там, где тысячи лет пребывало Око пророка, теперь осталась лишь пустая ниша.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12909', 'ruRU', 'Привет. Оруженосец Волтер и я отвечаем за уход за Старой Савраской и остальными лошадьми.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13511', 'ruRU', '$N! Тебя и не узнать в этой новой одежке! Гляжу, на тебе моя туника… Или ты тогда $gвыбрал:выбрала; ботинки? Или, может, посох? Не могу припомнить, так давно это было…$B$BКак бы то ни было, это хорошо, что ты снова здесь и можешь нам помочь. Нам сейчас любая помощь пригодится. Послушай, $N, если ты будешь действовать так же, как с народным ополчением в Западном Крае, тогда у нас есть шанс.$B$BТяжелые времена настали; если мы не выиграем этот бой, возможно, всему Западному Краю придет конец.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12610', 'ruRU', 'Добро пожаловать в наш лагерь, |3-6($c). Если хочешь, можешь отдохнуть здесь.', 'Добро пожаловать в наш лагерь, |3-6($c). Если хочешь, можешь отдохнуть здесь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12694', 'ruRU', 'Тебе бы лучше убраться отсюда, если не хочешь оказаться в одной из этих клеток…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13019', 'ruRU', 'Добро пожаловать в мой лагерь, |3-6($r).$b$bОн выглядит не ахти, но сейчас это наш дом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13519', 'ruRU', 'Приветствую тебя, друг.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12753', 'ruRU', 'Что привело тебя сюда, $gмалыш:малышка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12752', 'ruRU', 'Хромоногий Джо, это ты. Это правда ты!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14208', 'ruRU', 'Наконец-то!$b$bВот $gтот:та:r; |3-6($r), чей приход был предречен в пророчествах!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14238', 'ruRU', 'Приветствую тебя, друг! Ты $gпришел:пришла;, чтобы помочь в бою детям Азерота?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14178', 'ruRU', 'На редкость замечательный денек, не правда ли? Единственное, чего не хватает, так это славного фейерверка.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11673', 'ruRU', '', 'Мы с моими сестрами приветствуем тебя, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11297', 'ruRU', 'Мешать…*ик*… яд и алко…*ик*…бухло небезопасно…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11287', 'ruRU', 'По-моему, я тебя уже где-то видел…', '', 'Мы знакомы?', '', 'Вот тебе мудрый совет: никогда не верь гномам.', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12112', 'ruRU', '<Улклык, рыча, обнюхивает вас, и вы с удивлением осознаете, что понимаете его речь.>$B$BСтранный белый ворг с севера, Гарвал, встал во главе моей стаи. Но мы вырвем его глотку, и я снова стану вожаком воргов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13797', 'ruRU', 'Мы обнаружили этих протодраконов у бронзового святилища драконов, что на Драконьем Погосте. Мы тотчас же поняли, что это наши братья – наша кровь из далекого прошлого.$B$BПриношу свои извинения, |3-6($c). Я, должно быть, сбил тебя с толку. Я – Пенумбриус из бронзовых драконов. Я принял эту форму для удобства. Гораздо проще общаться и жить среди смертных, когда ты выглядишь так же, как и они.$B$BЧем я могу тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13851', 'ruRU', 'Мы не должны расслабляться. Даже мгновение отдыха может поставить всех нас под угрозу. Увы, солдатам необходима передышка от этого безумия.$B$BРадуйся победе, $N. Если бы не ты, мы бы потеряли Оплот.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13856', 'ruRU', 'То, что я теперь делаю для Серебряного Авангарда, может быть рассмотрено в качестве выполнения обязательств. Платы, если тебе угодно. Верховный лорд освободил меня из-под власти Короля-лича и открыл пред всеми нами путь к жизни вечной. Говоря это, я подразумеваю, что наши методы разнятся – и очень сильно. Я и остальные рыцари Черного Клинка помогут Серебряному Авангарду обосноваться в Ледяной Короне, и тогда наши пути разойдутся. Мы расплатимся полностью.$B$BДругая судьба ждет нас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13790', 'ruRU', 'Здрасьте, здрасьте!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13795', 'ruRU', '', 'Привет, милашка, как хорошо, что ты здесь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13783', 'ruRU', '', 'Ох уж эти мужчины… И с ними жить нельзя, и без них тоже нельзя. Нет, можно, конечно – но это так скучно!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14180', 'ruRU', '', 'Если вещь не сломана, это не значит, что ее нельзя починить. Чем я могу тебе помочь, дорогуша?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14087', 'ruRU', 'Привет, $gдружище:подруга;. Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13896', 'ruRU', 'Взятие Вершины Рыцарей – лишь первый шаг в долгом и опасном путешествии. Нам еще многое предстоит сделать, прежде чем мы сможем подступиться к цитадели Ледяной Короны.$b$bБез твоей помощи и помощи других героев, подобных тебе, нам не справиться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13908', 'ruRU', '', 'О чем бы ты ни $gхотел:хотела; попросить, проси скорее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13979', 'ruRU', 'В мои обязанности в Кирин-Торе входит следить за соблюдением стратегических интересов владык-магов.$b$bНа этой неделе у нас еще много дел, $n. Ты мне поможешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13871', 'ruRU', 'Врайкул мирно отдыхает, погруженный в магический сон.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13525', 'ruRU', 'Да, мы сокрушим Короля-лича. И наконец-то отомстим.$B$BНо сначала мы должны убедиться, что чума по имени Алый Натиск будет уничтожена раз и навсегда!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13671', 'ruRU', 'Если тебе больше нечем заняться, поубивай эти штуковины!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13616', 'ruRU', 'Не попадайся на глаза, ладно? Я пытаюсь притворяться, что я все еще под контролем Короля-лича!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14116', 'ruRU', '$GРешил:Решила; начать приносить пользу?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14068', 'ruRU', 'Голоса говорят со мной.', '', 'Я должен повиноваться.', '', 'Тебе нужно выбираться отсюда, пока оно и до тебя не добралось!', '', 'Это вокруг нас. Это у меня в голове!', '', 'Саронит… в этом материале что-то есть!', '', 'Здесь что-то есть. В камнях, в самой земле!', '', 'Мне нужно выбраться отсюда! Можешь освободить меня?', '', 'Ты слышишь, как оно разговаривает у тебя в голове?', '');
INSERT INTO `npc_text_localized` VALUES ('12208', 'ruRU', 'Времена меняются, $N. Пришла война.$B$BВосстанет ли наш народ, воспользовавшись случаем, и поможет нашим союзникам… или так и будет, мешкая, стоять в стороне?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9389', 'ruRU', '', 'Нам надо вернуть ящики с припасами назад!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9733', 'ruRU', 'Никогда не гляди Сломленным прямо в глаза. Уверен, стоит им захотеть, и они высосут из тебя всю душу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9374', 'ruRU', 'Прежде чем выставлять задание, обсудите его со стражем-смотрителем Мой\'прном Джиллом.$B$BНесанкционированное выставление заданий наказуемо.$B$BВывешивать объявления воспрещается.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12294', 'ruRU', 'Меня звали Иололом, пока в городе не прошло тайное голосование, в результате которого меня заставили сменить имя. Так и сказали: либо меняй его, либо иди по стопам Корки. Ты бы $gсам:сама; так же $gпоступил:поступила;. А как тебе мое новое имя? Правда, намного лучше?$B$BКстати, если ты ищешь работу, глянь на доску объявлений.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10893', 'ruRU', 'Мой разум наполняют видения событий, что происходят по всему миру и тех, что только должны произойти. Многие из них зловещи, но мы не должны верить, что их невозможно изменить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9240', 'ruRU', 'Приветствую, |3-6($c). Хочешь довести свое мастерство охоты до предела? Ты $gпришел:пришла; по адресу.$B$BХеминг Эрнестуэй организовал лучшую экспедицию, которую знали руины этого мира. Отправляйся вместе с нами, если не трусишь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9219', 'ruRU', 'Гляди в оба, |3-6($c). Никогда не знаешь, когда какой-нибудь зверек вздумает поохотиться на тебя, а не наоборот.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9290', 'ruRU', '', 'Мы бы никогда не разбились, если бы Хеминг не прострелил элерон, когда палил по дичи на земле!$B$BНо ничего страшного, я скоро все починю.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10866', 'ruRU', 'Власть Иллидана над моим племенем сильна, $N. Так или иначе, скоро все закончится.$B$BТы $gготов:готова;, $N? Будущее моего народа зависит от нас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10288', 'ruRU', 'Возникает ощущение, что Орик изо всех сил пытается всех рассмешить. Лучше просто пройти мимо, чтобы не поощрять его жалкие попытки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12238', 'ruRU', '', 'Добрый день, |3-6($c).$b$bМногое нужно сделать, чтоб помочь нам. К сожалению, я очень мало могу для тебя сделать, пока не соберу все свои припасы.$b$bСейчас у меня имеется $3223w |4процент:процента:процентов; корней острошипа, нужных мне для начала. Любая помощь с твоей стороны пригодится.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10290', 'ruRU', '*Гигантская боевая кошка, облаченная в доспехи и оседланная. Казалось бы, уже одно это выглядит достаточно абсурдно. Так нет же, эта кошка еще и разговаривает…*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14213', 'ruRU', 'Эта огромная, сверкающая магическая книга помогает создавать символы заклинаний и способностей. Она всегда находится рядом с учителем начертания в районе Фармацевтов.', 'Эта огромная, сверкающая магическая книга помогает создавать символы заклинаний и способностей. Она всегда находится рядом с учителем начертания в районе Фармацевтов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13730', 'ruRU', '<Гнилесс смотрит на вас с таким выражением лица, что вам становится не по себе.>$B$BКакой замечательный образец…$B$BТы случайно не хочешь принести себя в жертву на благо науки?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13727', 'ruRU', '', 'Я знаю, что Тралл гневается на Артаса, но этот гнев бледнеет пред моим собственным. Если он был бы столь разгневан, как любой из Отрекшихся, армии Орды со всей яростью обрушились бы на Нордскол и истребили бы всю Плеть в этой проклятой замерзшей пустыне.$B$BНо я не лидер Орды, и я не отдаю приказов о начале войны. Тралл хочет обдумать свое решение, несмотря на то что пришла пора действовать. Но скоро наше время придет… очень скоро.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13721', 'ruRU', 'С тех пор, как я в последний раз стоял перед гражданами Альянса во времена Третьей войны, наш союз претерпел многие изменения. Многие перемены пошли ему на благо, других же…$B$B<Бролл бросает суровый взгляд на Валиру.>$B$B…стоило избежать.$B$BСосредоточившись на Пылающем Легионе, мы упустили из виду Плеть, которая смогла набрать силу и выступить против нас. Легион и Плеть – это две грани одной беды, которая нарушает естественный порядок вещей, и потому должна быть устранена.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13718', 'ruRU', 'Пройдет не так уж много времени перед тем, как мы выступим против Короля-лича. Он – угроза не только человечеству, но и всему Азероту.$B$BНо решение об объявлении войны не должно приниматься поспешно. Глупо бросаться в Нордскол неподготовленными, ибо таким образом мы только сыграем на руку Королю-личу.$B$BМы должны взвесить все шансы. Сейчас наша армия сильна, как никогда, но нас может подвести любая тактическая ошибка. $B$BНадеюсь, Гаррош это поймет…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13724', 'ruRU', 'Я понимаю Гарроша. В жилах каждого орка течет горячая кровь, которая побуждает нас броситься в битву и отомстить за павших братьев. Но вождь прав – правильно выбранная стратегия столь же важна в битве, как и сила.$B$BМы выступим, когда будем готовы, ибо Орда – это больше не скопище безмозглых, свирепых варваров. Мы изменились.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13723', 'ruRU', 'Эй, ты! Чего прохлаждаешься? Нужно перебить этих чудищ из Плети, пока они не разрушили весь Оргриммар!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13717', 'ruRU', 'Ты нужен Орде! Оттесним войска Короля-лича!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13449', 'ruRU', '', 'Добро пожаловать на мою кухню! Здесь, в Даларане, мы готовим множество наивкуснейших блюд.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13840', 'ruRU', 'Еще один голодный рекрут?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13734', 'ruRU', 'Чем могу служить?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14040', 'ruRU', 'Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12362', 'ruRU', 'Глюг бмргл грггл!', '', 'Мавурргггл, гургл лур!', '', 'Глумрггл?', '', 'Брггл-брггл! Мргл-Мргл! Глюк!', '', 'Блрмрргл гулргл грр.', '', 'РАВГРЛРЛРЛРРЛГЛРЛ!!!', '', 'Мрглрглрглргл… глргл?', '', 'Мрглмргл Глрггл!', '');
INSERT INTO `npc_text_localized` VALUES ('13004', 'ruRU', '', 'Да благословит тебя Элуна, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12124', 'ruRU', '<Старый Ледоспин чертит какие-то знаки на песке. Присмотревшись к рисункам, вы понимаете, что он просит вас о помощи>.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12755', 'ruRU', '<Клыкарр смотрит на тебя подозрительно.>$b$bНе задерживайся надолго. Тебе не понять нашего народа, я не хочу, чтобы из-за тебя были неприятности.', '<Клыкарр смотрит на тебя подозрительно.>$b$bНе задерживайся надолго. Тебе не понять нашего народа, я не хочу, чтобы из-за тебя были неприятности.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12222', 'ruRU', 'Везет мне, все-таки. Многим ли тауренам удается послужить юнгой на таком фрегате? Греззикс даже позволяет мне управлять этими боевыми шлюпками!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14117', 'ruRU', 'Какого торговца ты ищешь?', 'Какого торговца ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14095', 'ruRU', 'Ты найдешь то, что ищешь, в лавке \"Еще по сто\" в северной части Даларана.', 'Ты найдешь то, что ищешь, в лавке \"Еще по сто\" в северной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13737', 'ruRU', 'Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13839', 'ruRU', 'Я ТУТ ГОТОВЛЮ!!!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12121', 'ruRU', 'Давай шевелись, крысиный мешок! Чини скорей эту фиговину, кишка сухопутная!$B$BЗа работу уже! Увидимся!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13910', 'ruRU', '', 'Теперь мы займемся этим. После такого боя ты, несомненно, заслуживаешь отдыха.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11332', 'ruRU', 'Я пленник! Меня подстрелили, и мой прекрасный дирижабль рухнул с неба прямо в лапы этих мерзавцев! Вот скажи, за что мне такое наказание? За то, что я занимался поставками материалов для Орды? И что в этом такого? Все этим занимаются!$B$BА теперь они гоняют мою малютку по кругу над заливом и заставляют сбрасывать бомбы на пиратов. Вот помяни мое слово – когда-нибудь этот чудесный дирижабль развалится прямо в небе! Он и так на соплях держится, а запчастей мне не поставляют!$B$BНу ладно, если хочешь им помогать – твое дело. Не позже чем через $3078w |4минуту:минуты:минут; дирижабль вернется в порт.$B$BНо все равно, я с этим не смирюсь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13579', 'ruRU', 'Это портал на поле боя \"Ущелье Песни Войны\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13576', 'ruRU', 'Это портал на поле боя \"Альтеракская долина\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12961', 'ruRU', 'Рад снова видеть тебя, $N.$B$B$gПришел:Пришла;, чтобы освежить знания о тактике Плети?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12883', 'ruRU', 'Что ты еще здесь делаешь? Иди назад в город!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14131', 'ruRU', '', 'Мы должны избежать еще одной войны с Ордой любой ценой!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14140', 'ruRU', 'Будь $gготов:готова; выступать, $N. Мы начнем атаку, как только прибудут все наши войска!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14142', 'ruRU', 'Решающая битва скоро начнется, $N. Не терять бдительности!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13031', 'ruRU', '', 'Ветер и деревья шепчут о беде – о той, что крадется незримо… Кажется, что эта земля здорова; но здесь происходит что-то, чего мы не видим…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12705', 'ruRU', 'Погибаю? Я? Хе, это вряд ли, $Gмалыш:девчушка;. Это просто царапина.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13349', 'ruRU', 'Добро пожаловать в даларанское бюро регистрации гильдий. Чем могу помочь?', 'Добро пожаловать в даларанское бюро регистрации гильдий. Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13503', 'ruRU', 'Раз уж тебе удалось пополнить наши запасы, я могу приступить к готовке. Итак, что я могу для тебя сделать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13012', 'ruRU', 'Эй, ты! Я тебя знаю?$b$bАх, да, у Пригмона хорошая память на лица.$b$bА ты не следишь ли за мной случайно? Что бы там ни было, Пригмон этого не делал!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13100', 'ruRU', 'Ну надо же! Даже удивительно, что в такое место, как это, забрался кто-то еще! Если бы я знал, что вы сюда собираетесь, я бы присоединился к вам вместо того, чтобы лететь на этом кошмарном гномском устройстве. Ну, раз уж вы здесь, помогите-ка мне добраться до Трибунала! Если мои предположения верны, в этих стенах хранятся сведения о сотворении всей расы дворфов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13308', 'ruRU', 'Локен?! Час от часу не легче… Мы справились с железными дворфами, но готов поспорить, что рядом есть другая машина, выплевывающая железных врайкулов пачками.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11551', 'ruRU', 'Я передал вам все свои знания, однако вам еще есть чему поучиться. За морем, в квартале магов Штормграда, живет великий портной. Его зовут Джорджио Болеро, и, думаю, дальше вы будете учиться у него. Поезжайте, поговорите с ним.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13506', 'ruRU', '', 'Почему все на меня смотрят так, как будто это я разбила корабль?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13322', 'ruRU', 'Мой бедный ветролет. Он не перенес аварии, а без запчастей мне его никогда не починить!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13042', 'ruRU', 'Я зову ее Духом Гномрегана. Ну не красотка ли?! Без твоей помощи ее никогда бы не удалось восстановить.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13517', 'ruRU', 'Чем Корг может тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13462', 'ruRU', 'Налей себе пинту!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13307', 'ruRU', 'Похоже, тебе пришло в голову то же, что и мне. Ищешь фрукты, а?', 'Похоже, тебе пришло в голову то же, что и мне. Ищешь фрукты, а?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13375', 'ruRU', 'Хороший день для полета, не правда ли?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13359', 'ruRU', 'Бешеное Сердце захватит землю для себя. Длинноязыкие уйдут, или мы всех их убьем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13360', 'ruRU', 'Может быть, волчеры Бешеного Сердца и кажутся извергами, но в глубине души мы добрые и сентиментальные. Ты увидишь, если задержишься надолго – мы хорошо ладим с сильными и умными.$B$BМы просто не любим глупых.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13120', 'ruRU', '<При твоем приближении пойманный кроколиск издает низкий рык.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13366', 'ruRU', 'Все в порядке?', '', 'А то!', '', 'Хорошо иметь верного товарища по охоте.', '', 'Что-то не так?', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13124', 'ruRU', '<Раненый оракул Прибежища Гласа Дождя стонет от боли.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13361', 'ruRU', 'Это Великий камень дождя. Мы храним его в безопасности и приносим ему блестяшки, а он заботится о нас.$B$BУ тебя есть еще блестяшки для камня?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13137', 'ruRU', 'Омерзительный друг длинноязыких, тебе здесь не место. Уходи из земли Бешеного Сердца, или я призову всякую разную магию, чтобы убить тебя!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13138', 'ruRU', 'Бешеное Сердце никогда не станет дружить с длинноязыкими. Надо быть тупицей, чтобы вернуться сюда, $gпредатель:предательница;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13362', 'ruRU', 'Чего мне не хватает в росте, я добираю в магии. Я хорошо забочусь о великом камне тумана… большую часть времени.$B$BТупые волосатые…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13147', 'ruRU', 'Видели ли вы когда-нибудь драконов, подобных этим? Отличное место для охоты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13367', 'ruRU', 'Привет! Ты как? Мы хорошо… угу, хорошо-хорошо. Привет!', '', 'Когда-нибудь ты $gспотыкался:спотыкалась; о свои ноги? Мы спотыкались. Как-то больно. Не делай этого, неприятно. Нет.', '', 'Привет! Ты на свете всех сильнее, и прекрасней, и все такое. Ты мне нравишься. Давай дружить?', '', 'Хочешь, чтобы мы тебе помогли? Чем тебе помочь? Мы хорошо помогаем.', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13365', 'ruRU', 'Привет, сухокожее создание.', '', 'На что $gуставился:уставилась;?', '', 'Есть причина, по которой мы все еще здесь?', '', 'Я так понимаю, тебе что-то нужно.', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13318', 'ruRU', 'Мохоброд, похоже, тяжело ранен.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13438', 'ruRU', 'Когда-нибудь Реджек стать могучий воин.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13451', 'ruRU', 'Надеюсь, ты донесешь это до длинноязыких в целости. Они как раз уходят.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13364', 'ruRU', 'Картак приходит за мясом длинноязыких. Бешеное Сердце обеспечивает, чтобы много мяса было готово.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13303', 'ruRU', '', 'Плеть – это грязное пятно, оскверняющее эти непорочные земли. Клянусь силой моих создателей, я сокрушу их всех, до последней гнилой косточки!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13510', 'ruRU', 'Хм-м… Твое лицо кажется мне знакомым, |3-6($r). Дай-ка подумать…$B$BАх, да! Западный Край – вот откуда я тебя знаю! Ты же $n! Ну что ж, рад тебя видеть. Надеюсь, в этот раз ты поможешь нам закончить работу.$B$BСейчас мы далеко от Западного Края, но, если этот бой будет проигран, боюсь, нам некуда будет возвращаться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13445', 'ruRU', '', 'Эти чудища убили моего отца и похитили мою сестру. Они за все заплатят!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13838', 'ruRU', '', 'Мясопилка – это сердце производственной машины Плети для создания вурдалаков, поганищ и мясистых великанов. Если мы сможем уничтожить ее, Королю-личу будет нанесен чувствительный удар.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14028', 'ruRU', 'Плеть необходимо остановить любой ценой! Мы не можем допустить, чтобы они вывели на поле боя всю армию, которую сейчас собирают.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13978', 'ruRU', 'Мясопилка никогда больше не будет производить бойцов для легионов Короля-лича!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13352', 'ruRU', 'Надеюсь, верховный маг оценит, через что я прошел, чтобы Хеминг выполнил свою часть соглашения.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13306', 'ruRU', 'Этот форпост – зона, свободная от шестеренок. Те, кто используют шестеренки при сборе механических устройств, будут подвергнуты безжалостным насмешкам.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13975', 'ruRU', 'В Даларане есть много достопримечательностей. Какую именно ты ищешь?', 'В Даларане есть много достопримечательностей. Какую именно ты ищешь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13873', 'ruRU', '<Ик!>$b$bБудет так одиноко тут без моих друзей, $gпарень:девочка моя;.', '', 'Аррр! Кому-то придется задать хорошую трепку этим уродам!$B$B<ик!>', '', 'Пришло время надрать пару задниц и выпить двойного рома!$B$BА двойного рома почти что и не осталось…', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12824', 'ruRU', 'Должно быть, Кренна сомневается в моих способностях. Пока наши лучшие воины сражаются с Альянсом и его союзниками или наводят ужас на троллей, меня отправляют разбираться с этими надоедливыми врайкулами из клана Укротителей драконов!..$B$BМожет, если мы объединим усилия, то сможем показать, на что мы способны на самом деле.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13033', 'ruRU', 'Шкуры для Орды, $gбратишка:сестренка;! Все самое лучшее для наших воинов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13034', 'ruRU', 'Запасы в дорогу? Может быть, освежающий напиток, пока вы смотрите бои?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13902', 'ruRU', '\"Молот Оргрима\" в Ледяной Короне и готов нанести удар. Лучшего судна не найти!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13998', 'ruRU', 'Высшая эльфийка Хира Снежная Заря обучает полетам в непогоду.$B$BОна восседает на дракондоре на площадке Краса в восточной части Даларана.', 'Высшая эльфийка Хира Снежная Заря обучает полетам в непогоду.$B$BОна восседает на дракондоре на площадке Краса в восточной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12773', 'ruRU', '', 'Эти горы прекрасны, но в глубине таят великую тьму… Будь $gосторожен:осторожна;!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12912', 'ruRU', 'Добро пожаловать в Лагерь Уанква.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12793', 'ruRU', 'Когда-то я вольно бродил по Седым холмам, сын великого Урсока, властителя этих земель.$B$BНо теперь… теперь я обречен провести остаток жизни в этой пещере.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12794', 'ruRU', 'Ты не понимаешь. Если бы я захотел, то смог бы пробиться к выходу из этой пещеры.$B$BВсе дело в моем отце, Урсоке. Он восстал из мертвых… но теперь он не тот, кем был прежде.$B$BОн отдал свою жизнь во время войны Древних тысячи лет назад. Многие века фурболги, поклонявшиеся ему, пытались найти способ вернуть его к жизни.$B$BВ конце концов, они решили попробовать восстановить разрушенное мировое дерево, Фордрассил, и использовать его магию, чтобы вернуть моего отца.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12796', 'ruRU', 'Взращенный ими саженец дал плоды. Сначала казалось, что все идет хорошо. Урсок вернулся, и ненадолго мое сердце исполнилось радости.$B$BОднако по мере его выздоровления становилось все яснее, что он не таков, как прежде. Как и фурболги.$B$BМне неведомо, какое зло заставило древних друидов уничтожить Фордрассила, но зло это воскресло в новом древе и обратило народ фурболгов в кровожадных чудовищ. $B$BТо существо, которое ныне бродит в Седых холмах – не мой отец; оно должно упокоиться навеки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12797', 'ruRU', 'Я – Кодиан, дочь Урсока. Я присматриваю за этой некогда священной гробницей.$B$BЯ не видела здесь чужаков уже долгое время.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12798', 'ruRU', 'Фурболг по имени Тур Яролап. Он не один из нас.$B$BОн пришел из южных земель. Он из племени Древобрюхов.$B$BОн хотел встретиться с Урсоком. Похоже, он считал, что Фордрассил может быть очищен и использован для спасения моего отца.$B$BКак глупо питать такие иллюзии. Ты согласишься со мной, когда увидишь ту тьму, что поразила его, как увидела это я.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13853', 'ruRU', '', 'Наконец-то подкрепление! Стражи больше не могут сдерживать этот натиск! Вам нужно будет сменить моих воинов и попытаться продержаться как можно дольше. Вот кристаллы контроля, активирующие автоматическую охрану тюрьмы, но их нельзя использовать слишком много раз. У меня есть кристаллы для всей вашей группы, обязательно возьмите их у меня.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12785', 'ruRU', 'Когда я услышал о том, что у меня есть родственники здесь, на севере, я пришел сюда, чтобы познакомиться с ними поближе. И что я увидел – воинственные племена, идущие против самой нашей природы.$B$BУ меня не осталось никакой надежды, |3-6($r). Даже сам великий Урсок запятнан этим проклятым местом.$B$BЯ потерял надежду.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12787', 'ruRU', 'Да… Я чувствую магию Фордрассила… ничем не запятнанную.$B$BПожалуйста! Позволь мне помочь тебе. Судьба привела меня сюда – возможно, я смогу исполнить свое предназначение!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13432', 'ruRU', 'Алый орден будет служить новому хозяину – приняв смерть…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13355', 'ruRU', 'Мы отнимем у них все, рыцарь смерти.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13342', 'ruRU', 'О, у Харгуса новый покупатель?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13433', 'ruRU', 'Ты вызываешь меня на поединок, $gбрат:сестра;?', 'Ты вызываешь меня на поединок, $gбрат:сестра;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13395', 'ruRU', 'Может, сейчас в это трудно поверить, но чумной котел действительно станет источником заразы, чумы и порчи!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13419', 'ruRU', 'Рыцари Алого ордена…$b$b<Ривендер качает головой.>$b$bОни не такие, как все прочие люди. У меня есть теория, что у них проблемы с высшей нервной деятельностью, поэтому они не в состоянии оценить ситуацию логически и просчитать последствия. Достаточно только упомянуть при них о Свете – и они уже готовы ринуться в бой. При этом в бой они бросаются очертя голову, без оглядки на собственную участь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13396', 'ruRU', 'Как ты смеешь! Убирайся прочь!', '', 'Наверное, это мерзавец сейчас отплясывает в своих хоромах.', '', 'НИКОГДА не произноси при мне это имя! Считай это первым и последним предупреждением!', '', 'Где его точно нет, я знаю…', '', 'Хейган, наверное, прибирается в своем залитом слизью и заваленном мусором танцевальном зале.', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13397', 'ruRU', 'Чума овладеет Новым Авалоном – это всего лишь вопрос времени.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13444', 'ruRU', 'Мы избранные, $N. Мы рыцари смерти и посланы силами Плети, чтобы сеять хаос и разрушение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13425', 'ruRU', 'Я тяжело ранен, но все же постараюсь помочь, чем сумею. Оставайся внутри моего антимагического барьера, а не то магия послушников растерзает тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13475', 'ruRU', 'Ты точно этого хочешь?$B$BПосле того как я сотру знания из твоей памяти, ты их уже не вернешь.', 'Ты точно этого хочешь?$B$BПосле того как я сотру знания из твоей памяти, ты их уже не вернешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13482', 'ruRU', 'Здесь я отдал все во имя спасения своего отца. И вот, я пришел разрушить это место…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13903', 'ruRU', '<Верховный лорд Дарион Могрейн кивает.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8351', 'ruRU', 'Будь $gосторожен:осторожна;, |3-6($r). Мы, Братство Света, не столь снисходительны, как наши братья, когда надо воздать по заслугам тем, кто делает этот мир хуже.$B$BГоре тем, кто разгневает Корфакса.$B$BА теперь, если у тебя нет дела ко мне или другим членам Братства, уходи, пожалуйста, мы должны продолжить наше собрание.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9969', 'ruRU', 'Земля в этом месте все еще теплая от недавно пролитой крови. Ах, я бы отдал свой собственный глаз за то, чтобы быть здесь, когда демоны прорвались через портал и схлестнулись с героями Азерота! Что за битвы то были!$B$BНо теперь, по ту сторону портала, битва идет еще более яростная!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9996', 'ruRU', 'Тром\'ка, $gбрат:сестра;! Добро пожаловать в Запределье. Орде нужны все силы, какие можно созвать в этом искаженном мире. Если ты здесь в поисках битвы и славы – обещаю, ты обретешь их в достатке.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10005', 'ruRU', 'Тром\'ка, |3-6($c). Ты тут, видать, недавно. Вид у тебя свежий и неиспорченный кошмарами этих мест.$B$B*Сержант Лободроб сплевывает в пыль.*$B$BЭто ненадолго.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10293', 'ruRU', '', 'Королевское Фармацевтическое общество из Подгорода радо помочь нашим союзникам-оркам здесь, в Запределье. Это отличный шанс укрепить наши отношения и найти новые образцы и ингредиенты для изучения.$b$bГоворя про ингредиенты, я имею в виду яд.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8767', 'ruRU', 'Да уж, вот это называется поработал… Предсказательница Регулкут предупреждала меня, но я не слушал, за что и поплатился.$B$BЯ пришел сюда изучать руины Зет\'Гор, но по пути наткнулся на отряд Легиона. И вот, полюбуйтесь теперь, что за рана у меня на ноге…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10301', 'ruRU', 'Говорят, будто пыль и жара Полуострова Адского Пламени сильно раздражают… Сам не видел, но вполне доверяю моим уважаемым и все еще живым союзникам.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9619', 'ruRU', 'Спасибо тебе за помощь в изготовлении бальзама, $N. Как только он немного заглушит боль, я посмотрю, как далеко смогу уйти на этой ноге.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9789', 'ruRU', 'Клянусь, никогда больше не буду срезать путь! Откуда же мне было знать, что в небе над Полуостровом Адского Пламени так опасно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10636', 'ruRU', 'Это место скрывает тайны, которые мы, орки, отчаялись раскрыть. Тайны нашего прошлого, тайны нашего будущего, тайны, проблескивающие у нас внутри.$B$BЯ надеюсь разгадать их… Пройдешь ли ты этот путь со мной?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8675', 'ruRU', 'Существующее в Запределье демоническое создание гораздо более могущественно, чем все, что мы когда-либо видели в нашем старом мире. $B$BВеликая сила придет к любому, кто не испугается ее, но нужно учиться управлять и владеть ею.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8680', 'ruRU', 'Запределье пропитано магической энергией насквозь, она там повсюду! Чувствуете, $N? Нашей расе тут самое место. Скоро син\'дорай обретут новый дом!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8685', 'ruRU', 'Дружба с Солнечными Скитальцами окупила себя: я получил возможность заняться исследованием предметов, которые могу найти лишь в Запределье, и они получили возможность любоваться… моей обаятельной личностью и демонически приятной внешностью.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9988', 'ruRU', 'Чего тебе надо? Я не настроен болтать без толку.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8848', 'ruRU', '', 'Что тебе нужно?$B$BТебя в детстве не учили, что донимать незнакомцев нехорошо?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8751', 'ruRU', 'Мне не до болтовни, |3-6($c). Я должен позаботиться о паломниках и подготовить их к предстоящему долгому путешествию.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8756', 'ruRU', '', 'Я думаю, что начинаю сожалеть о том, что затеяла это паломничество. Я понимала, что дорога будет тяжелой, и я не новичок в путешествиях, но не была готова к суровости Запределья.$B$BВ довершение всего этот Талерис, похоже, хочет изгнать меня отсюда до того, как я окончательно восстановлю силы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8750', 'ruRU', '', 'Добро пожаловать, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9770', 'ruRU', 'Иногда проблемы лучше всего решать \"в лоб\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8716', 'ruRU', 'Грустно наблюдать за тем, что произошло с этой землей. Кенарийская экспедиция сделает все возможное, чтобы разобраться в случившемся и возместить нанесенный ущерб.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8715', 'ruRU', '', 'Уровень заражения в Запределье поражает воображение, |3-6($c). Круг Кенария надеется понять, как этим созданиям вообще удалось выжить. Изучение этой разоренной, измученной земли и ее обитателей может научить нас, как исцелить болезни нашего мира.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9769', 'ruRU', '', 'Мы должны выяснить, что кроется за тайной гигантского кристалла, упавшего с небес на гряду Упавшего Неба.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10109', 'ruRU', '', 'Твоя помощь бесценна, друг мой!$B$BУверяю тебя, Кенарийская экспедиция примет все необходимые меры безопасности, чтобы кристаллы порчи, сброшенные с неба Иллиданом, не причинили вреда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10103', 'ruRU', 'Дух Воды покинул нас. Племя обречено.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10104', 'ruRU', 'Ветра не говорят больше с нашим племенем, $N. Мой народ лишился старейшин, посредников в общении с духами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9787', 'ruRU', 'Демоны иллидари держат меня здесь из-за моих познаний о древних кристаллах и реликвиях дренеев.$B$BОни захватили в рабство многих моих соплеменников и используют нас для поиска могущественных кристаллов, которых, скорее всего, здесь даже нет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9788', 'ruRU', 'Для чего? Если демоны Иллидана не поймают меня и не поработят опять, тогда это сделают наги Зангартопи.$B$BОбъединение с Иллиданом и его приспешниками обернулось всего лишь рабством для большинства племен Сломленных. Для моего народа нет никакой надежды.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10105', 'ruRU', '$N, эти цепи сковывают меня и не позволяют принести дух огня моему народу. Души, лишенные огня, не способны восстать против своих хозяев…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11662', 'ruRU', 'Куда ты хочешь попасть, $N? Мои нетопыри готовы отвезти тебя куда угодно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9274', 'ruRU', 'Вот список наград для нагов, вождей племен Темного Гребня и Кровавой Чешуи', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9110', 'ruRU', 'Скажи, тебе случайно не встречалась такая арка в озере на западе? Интересно, куда она ведет? Я бы понырял ради интереса возле нее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9752', 'ruRU', 'Темный охотник Дэнжай использует эту доску, чтоб вешать важные объявления и новости о наградах за поиску врагов Забра\'джина.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9260', 'ruRU', '', 'Пока ты в Зангартопи – выдели время на рыбалку. Неизвестно ведь, когда ты снова увидишь воду.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9750', 'ruRU', 'Мы уже контролируем руины Двух Башен! Быстро отправляйся на их защиту!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9250', 'ruRU', '', 'Торгеш считает, что может предсказывать будущее по полету птиц или броскам костей своих предков. На самом деле, все, что нужно сделать, – это просить духов. Только не говорите ему об этом, $N. Он очень упрямый.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10730', 'ruRU', 'Мне одиноко. Как бы мне хотелось, чтобы здесь были дети моего возраста. Иногда я скучаю по приюту в Шаттрате…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9176', 'ruRU', 'Природа благоволит к тем, кто хочет познать ее тайны, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9177', 'ruRU', 'В природе – могучая сила, |3-6($r). Те, кто пытаются подчинить ее, не смогут сделать это навечно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10525', 'ruRU', 'Центр помощи находится в южной части Возвышенности Алдоров.', '\"Центр помощи\" находится в южной части Возвышенности Алдоров.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9391', 'ruRU', 'Будь $gосторожен:осторожна;, когда отправишься в чащу, |3-6($c). Это место окутывает губительная аура. Должно быть, она осталась от того, что уничтожило моих друзей друидов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9383', 'ruRU', 'Кто ты? Что тебе здесь надо?', '', 'Почему ты так поступил с нами?', '', 'Там ЧЕТЫРЕ огонька!', '', 'Мои пухоспинки отомстят за меня!', '', 'Куда все делись?', '', 'Гостям на ужине стоит быть повежливей!', '', 'О, какому святому месту пришел конец!', '', 'Помажь мне голову целебным бальзамом. Как тебе нравится моя корона из шипов?', '');
INSERT INTO `npc_text_localized` VALUES ('9405', 'ruRU', 'Очень занят… я очень занят! Что?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9406', 'ruRU', 'А теперь, когда на мне живого места не осталось, чего тебе нужно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10236', 'ruRU', '', 'Если ты собираешься явиться сюда, постарайся быть $gполезным:полезной;, потому что здешний тенелов себя не утруждает.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9494', 'ruRU', '*Кайд шипит на тебя.*$B$BТише, или на нас свалятся орки и Альянс, $gпридурок неуклюжий:дура неуклюжая;!$B$BПравильно, кивай, а когда мы поговорим, ты уйдешь и забудешь, что $gвидел:видела; меня здесь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9520', 'ruRU', '', 'Надо узнать, что эти мрачные личности делают в деревне Грангол\'вар!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9523', 'ruRU', 'А тебе известно, что они потеряли полный контроль над Бормотуном в Темном лабиринте?!', 'А тебе известно, что они потеряли полный контроль над Бормотуном в Темном лабиринте?!', 'Они никогда с нами за это не расплатятся. Но, безусловно, лучше эта разрушенная деревня, чем бесцельные скитания по Аукиндону.', 'Они никогда с нами за это не расплатятся. Но, безусловно, лучше эта разрушенная деревня, чем бесцельные скитания по Аукиндону.', 'Я присоединился к ним потому, что они пообещали оплатить мне путь в академию. Дядя говорит, что в следующий раз я туда иду!', 'Я присоединилась к ним потому, что они пообещали оплатить мне путь в академию. Дядя говорит, что в следующий раз я туда иду!', 'Эй, должно быть, вы новобранец! Приятно познакомиться!$B$BЕсть известия из Темного лабиринта в Аукиндоне по поводу того, возобновили ли мы контроль над Бормотуном?', 'Эй, должно быть, вы новобранец! Приятно познакомиться!$B$BЕсть известия из Темного лабиринта в Аукиндоне по поводу того, возобновили ли мы контроль над Бормотуном?', 'Если интересуетесь, чуть попозже мы собирались в Шаттрат, выпить.', 'Если интересуетесь, чуть попозже мы собирались в Шаттрат, выпить.', 'Не желаешь присоединиться к лиге игроков в кожаный мяч? Мы играем, как правило, с понедельника по среду. Со следующего цикла можешь к нам присоединиться. Мы тут собираемся жребий бросать.', 'Не желаешь присоединиться к лиге игроков в кожаный мяч? Мы играем, как правило, с понедельника по среду. Со следующего цикла можешь к нам присоединиться. Мы тут собираемся жребий бросать.', 'Я надеюсь, из этого места получится хорошая передовая база для операций Совета теней. Я понимаю, это странно, но мне не хватает Темного лабиринта.', 'Я надеюсь, из этого места получится хорошая передовая база для операций Совета теней. Я понимаю, это странно, но мне не хватает Темного лабиринта.', '*Он нагибается ближе.*$B$BТолько между нами, я думаю о том, как бы сбежать в одну из этих ночей. Ты понимаешь, о чем я?', '*Она нагибается ближе.*$B$BТолько между нами, я думаю о том, как бы сбежать в одну из этих ночей. Ты понимаешь, о чем я?');
INSERT INTO `npc_text_localized` VALUES ('9505', 'ruRU', 'И что же, по-твоему, я могу для тебя сделать? Ты не видишь, что я занят, что я обсуждаю торговые дела с нашим славным предводителем?$B$BБыстро выкладывай свое дело, или получишь палкой, собака!', 'И что же, по-твоему, я могу для тебя сделать? Ты не видишь, что я занята, что я обсуждаю торговые дела с нашим славным предводителем?$B$BБыстро выкладывай свое дело, или получишь палкой, собака!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9506', 'ruRU', 'Ну, я только что отчитывался перед нашим лидером о том, как идут в соответствии с планом наши дела. Еще только несколько недель, и мы все восстановим в лучшем виде.$B$BКак бы то ни было, мы можем захотеть, чтобы Совет Теней поселил в этой деревне только орков. Так мы сможем потребовать наследство и работать под прикрытием.$B$BПогоди-ка. Мы знакомы? Как тебя зовут?', 'Ну, я только что отчитывалась перед нашим лидером о том, как идут в соответствии с планом наши дела. Еще только несколько недель, и мы все восстановим в лучшем виде.$B$BКак бы то ни было, мы можем захотеть, чтобы Совет Теней поселил в этой деревне только орков. Так мы сможем потребовать наследство и работать под прикрытием.$B$BПогоди-ка. Мы знакомы? Как тебя зовут?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9524', 'ruRU', 'Кем ты себя $gвозомнил:возомнила;, что так просто входишь сюда и обращаешься ко мне? Я представляю в этой деревне Совет Теней. Прочь с глаз моих!$B$BЕсли попадешься мне еще раз, больше не увидишь уже никого. Ясно?$B$BЧто с тобой сделать – похоронить в лабиринте Аукиндона или отдать в жертву Бормотуну?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9502', 'ruRU', 'Да? Архиморд… Архимонд побери! Теперь придется начинать все сначала!', 'Да? Архиморд… Архимонд побери! Теперь придется начинать все сначала!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9503', 'ruRU', 'Тренировки! Скоро я буду достаточно хорош, чтобы присоединиться к другим заклинателям и в свою очередь войти в Темный лабиринт в Аукиндоне. $b$bНадеюсь, что у меня будет возможность вызвать что-нибудь большое! Может, такое же огромное, как Бормотун, только уж я-то не потерял бы контроль над ним!', 'Тренировки! Скоро я буду достаточно хороша, чтобы присоединиться к другим заклинателям и в свою очередь войти в Темный лабиринт в Аукиндоне. $b$bНадеюсь, что у меня будет возможность вызвать что-нибудь большое! Может, такое же огромное, как Бормотун, только уж я-то не потеряла бы контроль над ним!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9497', 'ruRU', 'Да, чего надо? Не видишь, я занят? Совет Теней мне не по часам платит!', 'Да, чего надо? Не видишь, я занята? Совет Теней мне не по часам платит!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9498', 'ruRU', 'А я что, по-твоему, делаю? Чиню тут все – и никто мне не помогает, кстати, – чтобы мы могли тут поселиться!$B$BОбосновавшись в лесу, нам легче будет отслеживать, что делают друиды Шаттрата, Аллерийской крепости, Форта Камнеломов и Кенария. Из Аукиндона следить за ними несподручно.$B$BПостой-ка, а я тебя знаю?', 'А я что, по-твоему, делаю? Чиню тут все – и никто мне не помогает, кстати, – чтобы мы могли тут поселиться!$B$BОбосновавшись в лесу, нам легче будет отслеживать, что делают друиды Шаттрата, Аллерийской крепости, Форта Камнеломов и Кенария. Из Аукиндона следить за ними несподручно.$B$BПостой-ка, а я тебя знаю?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10680', 'ruRU', 'Я пришел, чтобы очистить эти земли от неупокоенных духов, но после первого же боя понял, что мне не справиться. Столько народу попало в беду… Пожалуйста, помогите им.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9477', 'ruRU', 'Это просто ужасно.$B$BНаши неудобства меня не волнуют, но ведь надо же что-то сделать, чтобы облегчить страдания мертвых в Костяных пустошах!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9495', 'ruRU', 'Может, мы и работаем вместе, но тем важнее осмотрительность. Еще раз: следи за своими словами и поступками.$B$BЯ могу гарантировать, что мы здесь далеко не единственные.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9276', 'ruRU', 'Нас ждут смутные времена, $gнезнакомец:незнакомка;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9739', 'ruRU', 'Дренеи вернулись! Эта группа напала на двух моих лучших всадников на волках, когда они патрулировали западные дороги. Разумеется, мы победили в этой схватке.$B$BТеперь я не уверен, что происходит, но мои лазутчики докладывают, что эльфы крови и дренеи развелись по всей Халаа. Я не любитель эльфов крови, но вот кого я ненавижу – так это дренеев. Если увидишь дренеев или кого-нибудь из их союзников, непременно пни их пониже спины для меня.$B$BХалаа лежит к западу… Если ты, конечно, хочешь пойти.', 'Дренеи вернулись! Эта группа напала на двух моих лучших всадников на волках, когда они патрулировали западные дороги. Разумеется, мы победили в этой схватке.$B$BТеперь я не уверен, что происходит, но мои лазутчики докладывают мне, что эльфы крови и дренеи развелись по всей Халаа. Я не любитель эльфов крови, но вот кого я ненавижу – так это дренеев. Если увидишь дренеев или кого-нибудь из их союзников, непременно пни их пониже спины для меня.$B$BХалаа лежит к западу… Если ты, конечно, хочешь пойти.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9275', 'ruRU', 'Они умерли. Все до единого…$B$BКогда воины Темной Крови прорубались сквозь наши ряды, мной овладело нечто непонятное. Скажи, доводилось ли тебе испытывать такую ярость, при которой все внутри будто горит? Горит так, что меркнет мир вокруг? Так, что кровь закипает в жилах? Вот это я и чувствовал.$B$B*Глаза Саурфанга на мгновение вспыхивают красным светом.*$B$BЯ единственный остался в живых. Меня окружило 30 врагов… Из Темной Крови. Я не помню того, что было потом. Я будто потерял сознание и очнулся через какое-то время.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9377', 'ruRU', 'Надо поискать выживших. Если кто-нибудь еще жив, надо хотя бы попытаться вернуть их.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9419', 'ruRU', 'Что ты видишь, глядя на запад, $gсмертный:смертная;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9420', 'ruRU', 'Знаешь, что я вижу? Предпосылки к вторжению.$B$BДа, хозяин был мудр, когда опечатал порталы, но с тех пор он… как бы сказать? Хозяин стал рассеянным. Да… $B$BЛегион воспользовался удобным случаем. Теперь они проникают сюда, день ото дня их все больше и больше.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9421', 'ruRU', 'Пылающий Легион. Демоны. Я вижу их так же ясно, как вы видите свою руку у себя перед глазами. Да, именно так, я мог ясно видеть их. $b$bС возвращением Легиона придут и новые интриги. В нормальных обстоятельствах я почую присутствие демона за тысячу шагов. $b$b*Алтруис морщится.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9422', 'ruRU', 'Они ускользают от меня. Я не вижу сквозь их защиту. Они поставили заслоны против обнаружения.$B$B*Алтруис указывает на запад.*$B$BОни боятся нас, потому что знают, что мы ничего не боимся. Такая сила дорого обходится и требует много жертв.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9423', 'ruRU', '<Алтруис оборачивается к тебе и улыбается.>$B$BНет.$B$B<Алтруис указывает на своего скакуна Нетрандамуса.>$B$BМы должны остановить их, пока эти лагеря не завершили работу над тем, что они там сооружают.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9424', 'ruRU', 'Так вот как называются эти строения, загрязняющие ландшафт. Легион нанял мо\'аргов и ган\'аргов для постройки оружия массового уничтожения в этих передвижных Лагерях Легиона. Мы же не хотим узнать, что они собираются тут построить в Награнде?$B$BТы будешь моими глазами. Твоим оружием будет моя ярость.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9181', 'ruRU', 'Служители Земли стараются сохранить силу и величие стихий.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9182', 'ruRU', '', 'Как мой народ может бороться, когда стихии так бушуют? Они не видят, что ли, что без поддержки стихий мир попросту не сможет существовать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9180', 'ruRU', 'Духи не знают отдыха, |3-6($r). Обращайте внимание на то, что вас окружает, пребывая в Награнде, а не то их ярость пожрет вас.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9183', 'ruRU', 'Я Унтраг из Служителей Земли. Постарайся не раздражать стихии, пока остаешься вблизи Трона Стихий.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9654', 'ruRU', 'Я с ума схожу из-за этого. Никак не достану из гнезда эти проклятые яйца!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9284', 'ruRU', '$GМогучий охотник:Могучая охотница; $N возвращается. Расскажи мне об охоте на свою последнюю жертву.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9286', 'ruRU', 'Да-да, я чувствую себя лучше, но мне здесь вроде как понравилось.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9257', 'ruRU', 'Ты $gубил:убила; их всех: Хол\'стяка, Бантар, Потрошительницу и даже могучую Клыкач! Ты и впрямь самый лучший участник большой охоты, что я знаю!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9362', 'ruRU', 'Значит, ты тупица. Разве тебе не известно, кто я?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9363', 'ruRU', 'Орк…$B$B*Лантрезор смеется.*$B$BЯ – мастер клинка и сам наполовину орк.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9364', 'ruRU', 'Дренеи… Из-за этого я никто. Ни орк, ни дреней. Мое племя – редкий вид. Большую часть перебили десятилетия назад… Я живу лишь благодаря своей силе воли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9365', 'ruRU', 'Прикуси язык, |3-6($r). Я за свою жизнь навидался такого, о чем ты только в легендах $gслышал:слышала;. Я без жалости убивал мужчин, женщин и детей. Я служил под знаменами двух вождей Орды… А теперь я правлю кланом Тяжелого Кулака.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9366', 'ruRU', 'И что вашему племени требуется от Лантрезора, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9367', 'ruRU', '*Лантрезор громко хохочет.* $b$bИ где ж ты, по-твоему, находишься, дурачина? Это же земля огров. Раньше, чем орки, раньше, чем дренеи – здесь были огры. И все же ты хочешь начать войну, в которой неизбежно проиграешь? Тяжелый Кулак сокрушит тебя. А если тебя не уничтожит Тяжелый Кулак, это сделает Боевой Молот… $b$bНо, я думаю, твое начальство об этом и так знает…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9368', 'ruRU', '*Лантрезор пристально смотрит на тебя так, будто видит тебя насквозь.*$B$BВозможно, есть другой путь… Я соглашусь на требование вашего лидера только в том случае, если ты выполнишь в точности то, что я попрошу.$B$BС твоей помощью мы, может быть, сможем очень хорошо помочь друг другу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9369', 'ruRU', 'Вы разыграете серию сцен, которые заставят орков бастиона Вечной Скорби поверить, что на их базу напали огры клана Боевого Молота, и устроите другую серию событий, которая убедит огров клана Боевого Молота в том, что орки бастиона Вечной Скорби напали на их базу.$B$BСделай так, и ты избавишь нас от обеих проблем.$B$BОгры клана Тяжелого Кулака получат назад свои территории, а наш народ будет защищен от нападений Тяжелого Кулака, Боевого Молота и Совета Теней.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9879', 'ruRU', 'Запертые в этой разоренной земле, мы сделали все что смогли, чтобы выжить. Со временем же осознали, что новое зло из Круговерти Пустоты собирается вокруг нас. Хуже, чем темные орки, – сам Пылающий Легион стремился захватить это Запределье…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9880', 'ruRU', 'Пока мои товарищи сдерживали наступление тьмы, я использовал свою магию, чтобы пробраться в Великую запредельную тьму. И там я ощутил присутствие существ беспредельного Света и непостижимой чистоты.$B$BЭто был А\'дал и его наару. Это было истинное чудо!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9881', 'ruRU', '<Кадгар улыбается собственным мыслям.> $b$bЯ убедил А\'дала и его собратьев вернуться сюда со мной. Я уверен, что только их сила может заставить Легион убраться из Запределья. Но, как вы вскоре, несомненно, поймете, их сила в первую очередь должна жить в наших сердцах. $b$bЧтобы бороться с тьмой, мы должны стать маяками их вечного Света.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10379', 'ruRU', 'В Шаттрате есть два банка, оба находятся вдоль границы центральной части города. Одним владеют дренеи из Алдоров, другим – эльфы крови из Провидцев.', 'В Шаттрате есть два банка, оба находятся вдоль границы центральной части города. Одним владеют дренеи из Алдоров, другим – эльфы крови из Провидцев.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5863', 'ruRU', 'Чтобы начать обучение верховой езде, тебе необходимо завоевать полное доверие орков Оргриммара, |3-6($c).', 'Чтобы начать обучение верховой езде, тебе необходимо завоевать полное доверие орков Оргриммара, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10251', 'ruRU', 'Я чту своих предков, как велит древняя шаманская традиция. Мы должны всегда помнить о жертве, которую они принесли для того, чтобы мы могли жить здесь.$b$bА мы, в свою очередь, должны приносить жертвы для того, чтобы у наших потомков было будущее.$b$bЗачем ты $gпришел:пришла; ко мне, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9949', 'ruRU', 'Когда он говорит \"младший брат\", то забывает добавить, что мы близнецы. Он и старше-то всего на пару минут.$B$BТак что не дайте этой \"восходящей звезде\" обмануть вас. Я никогда не буду таким торговцем, как он.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10752', 'ruRU', 'Я здесь по приказу принца Хараманда. Странные вещи происходят у подножья Штормовой Вершины…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10059', 'ruRU', 'На моей родине, в Кареше, всеми эфириалами правило братство Эфириума. К несчастью, нам пришлось эвакуироваться из Кареша незадолго до того, как повелитель пустоты, Пространствус, привел свои войска, и они ворвались в этот мир, уничтожая все на своем пути. Братство Эфириум поклялось отомстить за это, и с тех пор они постоянно выслеживают Пространствуса.$B$BОднако Протекторат возник не из-за Пространствуса, а из-за братства Эфириума.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10060', 'ruRU', 'Братство Эфириум обратилось ко тьме… исказилось. Месть стала их единственным прибежищем, и всякий, кто встанет у них на пути или не поддержит их дело, считается врагом. Многие увидели, что братство Эфириум погружается в безумие, и ушли. Несколько принцев Эфириума покинули Братство и создали другие группы и фракции, с иными целями. Консорциум – одна из таких отделившихся групп, другая же – Протекторат.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10061', 'ruRU', 'Мы чувствуем, что братство Эфириум так же опасно для нас, как и Пространствус. И их, и его надо остановить…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10057', 'ruRU', 'Спасибо тебе, теперь мы сможем направиться к Руинам Фаралона, как и собирались.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10055', 'ruRU', 'Помощь, оказанная тобой в Руинах Фаралона, позволила мне вновь начать работать по графику. Теперь я собираюсь отправить туда мою команду.$B$BСпасибо тебе, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10209', 'ruRU', 'В другое время и в другом месте меня сочли бы всего лишь подростком. Здесь я бывалый воин, готовый жить и умереть ради славы наару.$B$BДесятеро из нас пошли в Манагорн Ара. Все остальные достались Легиону.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9958', 'ruRU', 'Эта площадка астрального телепорта покрыта яркими кнопками и переключателями, но, кажется, давно уже не использовалась.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10467', 'ruRU', 'Нужно наточить клинок или, может быть, приобрести новое оружие? Лучше перестраховаться, чем потом пожалеть…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11384', 'ruRU', 'Королевское фармацевтическое общество приветствует тебя в Нордсколе, |3-6($c). Надеюсь, ты $gпришел:пришла; помочь нам?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13738', 'ruRU', 'Чего изволите?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13841', 'ruRU', 'Что тебе нужно?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12103', 'ruRU', 'Теперь обычные собаки ко мне даже не подходят… Честно говоря, меня это очень огорчает… когда-то я очень любил животных.$B$BНо чумные псы тоже очень милые, хотя с первого взгляда так не скажешь! Впрочем, взгляни на меня – сразу поймешь, что не надо судить по внешности.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11562', 'ruRU', 'Земля… смерзлась. Мы тут застряли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11564', 'ruRU', 'На что это похоже? Земля там промерзла до самого основания!$B$BКолеса этих повозок не приспособлены ехать по такой поверхности! Нам понадобится поганище и пара цепей, чтобы вытащить их отсюда.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11586', 'ruRU', '', 'Эти странные воины Альянса знают, что обречены, но продолжают сражаться до последнего вздоха.$B$BВпечатляющее, и в то же самое время жалкое зрелище.', '', 'Через несколько часов остатки флота Альянса на Обнажившемся Дне будут разбиты. Они окружены и обречены.', '', 'Наши враги падут, ибо такова воля Сильваны.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11588', 'ruRU', '', 'Нам мешают только эти две пушки вдоль стены. Если бы их не было, наши войска смогли бы с легкостью взять их укрепления без значительных потерь.$B$BНадо либо уничтожить пушки, либо дождаться, когда у них кончатся боеприпасы. Похоже, Ансельм склоняется ко второму варианту.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11671', 'ruRU', '', 'Нет, эти губошлепы Альянса определенно сошли с ума! Пытаться пилотировать на разрушителях Отрекшихся… это же самоубийство!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11742', 'ruRU', 'Горт не боится смерти. Отрекшиеся просто соберут меня заново.', '', 'Горт иногда что-то не понимает. Горт умеет убивать. Поэтому Горта сделали сержантом. Но теперь Горта не посылают никого убивать!', '', 'Врайкулы больше и сильнее нас. Но мы зато не такие ужасные на вид!', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13035', 'ruRU', 'Нас окружили со всех сторон, и старейшины собираются бежать, оставив эти земли врагу. Мне кажется, это неправильно! Мы должны остаться и сражаться за нашу родину!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11874', 'ruRU', '', 'Я буду скучать по Лагерю Заиндевевшего Копыта. Но я понимаю, почему вождь Прахтотем принял такое решение. Сейчас мы должны заботиться о нашем выживании, а не о комфорте родного очага.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11689', 'ruRU', 'Мой долг – помочь и защитить тебя от врагов.$B$BЧто мы собираемся предпринять?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11910', 'ruRU', 'Конечно. Я нашел его на теле дворфа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11799', 'ruRU', 'Будь осторожнее, |3-6($r), – вокруг полно ходячих мертвецов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11756', 'ruRU', 'Пожалуй, я совершил ошибку, путешествуя так близко от Вырубок и Гьялерброна.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12090', 'ruRU', '', 'А? Ну, что еще?$B$BВремя никого не ждет!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11691', 'ruRU', '', 'Подойди поближе, $gмилый:милая;, слух у меня уже не тот, а кричать мне бы не хотелось.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12130', 'ruRU', '<Старый Ледоспин настороженно смотрит на вас, прогоняя кивком головы.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11724', 'ruRU', 'Так много применений для нового штамма чумы – и так мало времени на работу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11827', 'ruRU', 'Мы, распространители чумы, обычно экспериментируем больше обычных аптекарей. В конце концов, наша основная задача – заразить чумой все живущее.$B$BНо это не значит, что наши теоретические познания недостаточно основательны. Просто мы предпочитаем сразу переходить к делу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11884', 'ruRU', '', 'Кости… плоть… мускулы… жилы… моя пила распилит что угодно!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12187', 'ruRU', '', 'Уходи отсюда, $N. Я не хочу, чтобы на меня повесили смерть Ионы.$B$BМне и так будет непросто захватить бразды правления. А если кто-нибудь прознает, что я причастна к смерти нашего главаря, мне несдобровать…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10503', 'ruRU', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12645', 'ruRU', 'Наша большая морская черепаха, \"Зеленый остров\", может довезти тебя до Гавани Моа\'Ки в Драконьем Погосте.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12628', 'ruRU', '', 'Эти дети будут жить. Окруженные любовью и заботой, они станут сильными воинами Орды и со временем забудут ужасы своей прежней жизни.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12977', 'ruRU', 'Гниль, гниль, гниль… это все, о чем здесь говорят. Мне не хватает разнообразия!$b$bТак-так. У вас есть домашние любимцы, да? Конечно, есть. Конечно. Маленькие животные. У меня есть микстура для них. Она сделает их умными и сильными существами, да? Да. Сильными.$b$bПопробуйте ее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12978', 'ruRU', 'Осторожно с ним. Оно нестабильно и быстро теряет силу. Используй его поскорее! И… лучше сделай это в своем поле зрения, да…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12830', 'ruRU', 'Я думаю, он в южной части аббатства.', 'Я думаю, он в южной части аббатства.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12827', 'ruRU', 'Я думаю, они посадили его в клетку возле лесопилки.', 'Я думаю, они посадили его в клетку возле лесопилки.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12833', 'ruRU', 'Мне кажется, я видел, как они вели его по северной части аббатства, около мишеней для стрельбы.', 'Мне кажется, я видела, как они вели его по северной части аббатства, около мишеней для стрельбы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12832', 'ruRU', 'Мне кажется, они ее держат возле виселицы.', 'Мне кажется, они ее держат возле виселицы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12851', 'ruRU', '', 'Хорошо, но в следующий раз будь поосторожнее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3797', 'ruRU', 'Я… я серьезно ранен…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13846', 'ruRU', 'Рад встрече, |3-6($c). Мой совет тебе таков: когда ты путешествуешь по миру, остерегайся магии, ибо она неумолима к непосвященным.', 'Рада встрече, |3-6($c). Мой совет тебе таков: когда ты путешествуешь по миру, остерегайся магии, ибо она неумолима к непосвященным.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14091', 'ruRU', 'Только глупец может смеяться над тем, как выглядит мое обмундирование.$B$BЭто одеяние из стали я добыл в древнем месте, где каждая песчинка дышала мудростью веков…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14163', 'ruRU', 'Добро пожаловать в таверну \"Ведьма и ворон\", |3-6($r).$B$BБудь здесь поосмотрительнее, ладно? Нариса очень не любит отмывать кровь с пола.', 'Добро пожаловать в таверну \"Ведьма и ворон\", |3-6($r).$B$BБудь здесь поосмотрительнее, ладно? Нариса очень не любит отмывать кровь с пола.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13707', 'ruRU', 'Когда-то у меня было все. Слава, состояние, полный комплект лучших гладиаторских доспехов… – и все это я пустил на ветер!$b$bКак только картель поймал меня на договорных поединках… на подтасовке результатов… все пошло псу под хвост!$b$bУ меня не осталось ничего, только вот эти тряпки, да загубленная печень.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14090', 'ruRU', '', 'Когда дело доходит до сокровищ, не стесняйся забирать чужое добро. Украденный плод всегда слаще, а бесплатный сыр – всегда бесплатный, каким бы путем он тебе не достался.', '', 'Я это называю \"скидка трех пальцев\". В воровстве самое главное – твои руки… а если они подведут – то всегда выручит ножик под бок, зажатый в той же самой руке.$B$BУмелые ручки стоят дороже золота, запомни хорошенько.', '', 'А на этого Короля-лича мне вообще плевать. Большие шишки приходят и уходят, да всегда что-то такое мутят между собой. А потом фортуна крутит колесо – и все, они уходят в историю. Этому царьку тоже не убежать от судьбы.', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14065', 'ruRU', '', 'Этот портал ведет в Пещеры Времени, расположенные глубоко под землей в далеком Танарисе.$b$bВ моей власти отправить тебя туда, если ты этого захочешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14045', 'ruRU', 'Добрый день!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13690', 'ruRU', 'Чем я могу тебе помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13573', 'ruRU', 'Ты стоишь перед мастером тайного искусства. Было бы неразумно вызывать меня на поединок. За мной лежит путь, выстланный сгоревшими телами неосторожных.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14086', 'ruRU', 'Приветствую тебя, |3-6($c).$B$BСейчас Крепость Ледяных Оков находится под контролем Орды. Если хочешь, можешь переместиться туда с помощью портала.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', 'Приветствую тебя, |3-6($c).$B$BСейчас Крепость Ледяных Оков находится под контролем Орды. Если хочешь, можешь переместиться туда с помощью портала.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10614', 'ruRU', 'На востоке Полей Крыльев Пустоты находится крепость Драконьей Пасти. Именно там жестокий орочий клан Драконьей Пасти мучает пленных драконов, пытаясь сломить их волю. Многие теряют надежду и соглашаются выполнять приказы Драконьей Пасти. Другие… они отказываются приземляться, боясь, что боевые отряды орков схватят их и утащат в крепость.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10615', 'ruRU', 'Я… Мы бессильны. Иллидан нашел и доставил в Драконью Пасть их лидера – Зулухеда. Этот орк ответственен за пленение и заключение в оковы Алекстразы. Как мы могли сражаться против такой силы? Он захватил Аспект!$B$BИ с такой же легкостью он пленил мою подругу…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10616', 'ruRU', '<Нельтараку кивает головой.>$B$BКаринаку содержат в заключении в верхней части крепости Драконьей Пасти. Я предпринял множество безуспешных попыток освободить ее. Ты и раньше $gсражался:сражалась; с этими тварями, не так ли?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10855', 'ruRU', 'Ты много $gсделал:сделала; для нашего дела, $N, но ты не сможешь развиваться дальше, не освоив полеты. Только самые стойкие всадники могут помогать Крыльям Пустоты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10549', 'ruRU', 'Раз уж ты сюда $gдобрался:добралась;, то назад теперь дороги нет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11219', 'ruRU', 'Ой, да перестаньте! Я – единственный и неповторимый Бадд Недрек!$b$bЯ обошел всю эту землю, от вершины горы Хиджал до самых глубин Затонувшего храма. Собственно, я первый и назвал этот храм затонувшим!$b$bДа, это был я. Даже и не сомневайтесь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11221', 'ruRU', 'Да, да, послушай, |3-6($c). Я старше, чем выгляжу, и куда мудрее своих ровесников. Тебе не стоит так часто сомневаться во мне.\r\n\r\nСмотри, тебе не обязательно верить мне на слово. Почитай любую местную доску объявлений. Я там повсюду!$b$bТолько окажи себе услугу, не обращай внимания на объявления, озаглавленные \"РАЗЫСКИВАЕТСЯ\" в заглавии. Простые недоразумения, уверяю тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6534', 'ruRU', '', 'Почему ты интересуешься Синими драконами?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3815', 'ruRU', 'Эта книга повествует о драконах, объединившихся против своего могущественного сородича Смертокрыла. Из нее можно понять, как появились всецветные драконы. Аспекты готовились к войне.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13289', 'ruRU', '', 'Проходи, проходи. Не на что тут смотреть.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13883', 'ruRU', 'Готово! Я как раз вроде разобрался с этой дверью. Дай мне знать о своей готовности – и я ее открою!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13854', 'ruRU', '', 'Ну что, все готовы продолжать? Я приведу в действие этот кристалл, чтобы прикрыть отступление моих стражей.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13179', 'ruRU', 'Чем дальше, тем сложнее. Стратхольм пылает. Мы должны бросить вызов Плети и пламени, чтобы добраться до Мал\'Ганиса. Приготовьтесь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13287', 'ruRU', 'Ты $gготов:готова; встретиться лицом к лицу с Мал\'Ганисом вместе со мной?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12939', 'ruRU', '', 'Добро пожаловать, $gстранник:странница;. Ты $gпришел:пришла; как раз вовремя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12949', 'ruRU', '', 'Драконы Бесконечности пытаются изменить судьбу принца Артаса Менетила. Тебе он известен под именем Короля-лича, но здесь и сейчас он все еще принц Лордерона, пытающийся сделать все возможное для блага своего королевства. Сегодня Артас принимает судьбоносное решение, ведущее к тому, что мы, Хранители Времени, называем переломным моментом в течении времени.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12950', 'ruRU', '', 'Артас отдал приказ зачистить Стратхольм, убивая все живое в его стенах. Он обнаружил, что Плеть заразила город своей предательской чумой и что эта болезнь не просто убивает. Те, кто умирают от чумы, восстают из мертвых в виде лишенных разума зомби, вставая в ряды военной машины Плети.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12952', 'ruRU', '', 'Драконы Бесконечности пытаются вести себя необычно тонко. Они пробуют спрятать свидетельства, которые подтолкнут Артаса к решению очистить Стратхольм. Их доверенные лица воздействовали магией иллюзий на зараженное чумой зерно в соседней деревне, чтобы оно казалось нормальным. Мне нужно, чтобы ты $gнашел:нашла; это спрятанное зерно и $gвоздействовал:воздействовала; на него разбивателем чар. Воины Артаса ищут зараженное зерно – и они быстро его найдут, когда иллюзия развеется.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12992', 'ruRU', '', 'Отличная работа! Теперь Артас знает о зараженном чумой зерне в Стратхольме, и он вот-вот прикажет начать его зачистку. Но что-то все еще не так. Я чувствую присутствие чего-то чуждого во временном пространстве, и это должно означать, что драконы Бесконечности где-то рядом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12993', 'ruRU', '', 'Я не знаю, и это меня беспокоит. Я сделаю что смогу, чтобы найти драконов Бесконечности. И сейчас все, что мне от вас нужно, – это чтобы вы держались как можно ближе к Артасу, вступив в ряды его армии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12994', 'ruRU', '', 'Что ж, тебе не нужно записываться в наемники, просто сражаться рядом с ним. Главное – убедиться в том, что Артас вычистит Стратхольм и победит Мал\'Ганиса. Теперь, когда рядом нет Утера и Джайны, ему понадобится любая помощь. Если ты поговоришь с Артасом, он отправит тебя уничтожать войска Мал\'Ганиса. Выполняй этот приказ до тех пор, пока я не выясню, что происходит с драконами Бесконечности. Я свяжусь с тобой, когда у меня появится новая информация.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12995', 'ruRU', '', 'Удачи тебе и будь $gосторожен:осторожна;. Теперь сюда идут Утер и Джайна.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13125', 'ruRU', 'Должно быть, ты из тех воинов, что отважно сражались с ведомыми Мал\'Ганисом силами Плети. Отличная работа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13126', 'ruRU', 'И правда. Мал\'ганис руководит силами Плети с Площади рыцарей. Ворота, ведущие из города на площадь, закрыты. Но он не знает, что небольшая группа солдат может проникнуть туда через ратушу. Ты поможешь мне?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13177', 'ruRU', 'Быстро, соберитесь с духом, надо продолжать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13794', 'ruRU', 'У этих гор славная история… постарайся погрузиться в эту атмосферу, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6250', 'ruRU', 'С праздником Зимнего Покрова, э-э… $gмалыш:малышка;. У всех торговцев Дымного Леса сейчас, э-э… скидки для хороших детей, да!', '', 'Есть у меня одна мыслишка о том, как Дедушка Зима сегодня будет отдыхать… Я припас бутылочку \"Зимнего Покрова\" – и я намерен высосать ее до самого донышка.', '', 'Нет… У меня на коленях сидеть нельзя.', '', 'Да-да, конечно, скажи Дедушке Зиме, что ты хочешь получить на праздник Зимнего Покрова.$B$BХо-хо! Смелее!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6194', 'ruRU', 'Сезонные скидки для вас и вашей семьи!', 'Сезонные скидки для вас и вашей семьи!', 'С нашей фермы прямо на вашу тарелку! \"Пастбища Дымного Леса\" – неповторимый, устойчивый вкус!', 'С нашей фермы прямо на вашу тарелку! \"Пастбища Дымного Леса\" – неповторимый, устойчивый вкус!', 'Пришло время отличных сделок! Фа-ла-ла-ла-ла!', 'Пришло время отличных сделок! Фа-ла-ла-ла-ла!', 'С праздником Зимнего Покрова! Отпразднуйте его как следует, с дымнолесскими лакомствами!', 'С праздником Зимнего Покрова! Отпразднуйте его как следует, с дымнолесскими лакомствами!', 'Обязательно скажи Дедушке Зиме, какой подарок ты бы $gхотел:хотела; получить на этот Зимний Покров!', 'Обязательно скажи Дедушке Зиме, какой подарок ты бы $gхотел:хотела; получить на этот Зимний Покров!', 'Пусть веселится всяк – и богач, и бедняк! Покупайте лакомства Дымного Леса на праздник Зимнего Покрова!', 'Пусть веселится всяк – и богач, и бедняк! Покупайте лакомства Дымного Леса на праздник Зимнего Покрова!', 'Сейчас бы каштанов пожарить… Ммм!', 'Сейчас бы каштанов пожарить… Ммм!', 'Не забудьте запастись оберточной бумагой! Красиво упакованные доспехи и оружие – лучший подарок на праздник Зимнего Покрова для семьи и друзей!', 'Не забудьте запастись оберточной бумагой! Красиво упакованные доспехи и оружие – лучший подарок на праздник Зимнего Покрова для семьи и друзей!');
INSERT INTO `npc_text_localized` VALUES ('8076', 'ruRU', 'На шее у оленя висит жетон с эмблемой Пастбищ Дымного Леса и кличкой \"Метцен\". $B$BМетцен печально и жалобно смотрит на тебя. Кажется, будто олень скован какой-то незримой силой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8077', 'ruRU', 'Глаза Метцена радостно вспыхивают, когда вы посыпаете его пылью. Он трясет рогами и облегченно вздыхает.$B$BВокруг вас начинается скапливаться магическая энергия. Кажется, сейчас что-то произойдет!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8139', 'ruRU', 'Счастливого Зимнего Покрова, $gдруг:подруга;!', 'Счастливого Зимнего Покрова, $gдруг:подруга;!', 'Приветствую по-зимнему, мира вам!', 'Приветствую по-зимнему, мира вам!', 'Счастливых праздников, друг!', 'Счастливых праздников, друг!', 'Ах, омела…', 'Ах, омела…', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8125', 'ruRU', 'Новинка года: PX-238 Чудовольт Мороза. Не стесняйтесь, поднимайтесь!$b$bДа, сначала подпишите эти бумажки, потому что у нас тут осталось несколько мелких недоработок…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9626', 'ruRU', 'Во время Зимнего Покрова Дедушка Зима оставляет всем под деревом в Стальгорне подарки!', 'Во время Зимнего Покрова Дедушка Зима оставляет всем под деревом в Стальгорне подарки!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9686', 'ruRU', 'Дедушку Зиму можно найти перед банком в Стальгорне. Подарки ждут всех под деревом, рядом с которым он стоит.', 'Дедушку Зиму можно найти перед банком в Стальгорне. Подарки ждут всех под деревом, рядом с которым он стоит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9962', 'ruRU', 'Друзья с пастбищ Дымного Леса открыли магазины в Оргриммаре и Стальгорне. Интересно, какое желание они загадают в этом году?', 'Друзья с пастбищ Дымного леса открыли магазины в Оргриммаре и Стальгорне. Интересно, какое желание они загадают в этом году?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5878', 'ruRU', 'Тебе повезло быть $gодним:одной; из нас – ведь только маг сможет создать портал для возвращения сюда. Я могу научить тебя, если хочешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12215', 'ruRU', '\"Леди Мели\" достаточно значима в Тераморе, чтобы нам велели охранять ее пассажиров.', '\"Леди Мели\" достаточно значима в Тераморе, чтобы нам велели охранять ее пассажиров.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11285', 'ruRU', '', 'Никогда в жизни мне не было так холодно, |3-6($r). Я замерзаю даже на расчищенных от снега и льда равнинах Нордскола.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11655', 'ruRU', '', 'Поторопись, |3-6($c)!$b$bСитуация критическая, и мы почти не можем ее контролировать!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11293', 'ruRU', '', 'Слава Свету, эту таверну построили еще до того, как начались бедствия на севере.$B$BТы к нам надолго?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13984', 'ruRU', 'Ювелирные изделия от \"Картье и Ко.\" можно купить в Торговой палате волшебников в северо-западной части Даларана.', 'Ювелирные изделия от \"Картье и Ко.\" можно купить в Торговой палате волшебников в северо-западной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12343', 'ruRU', 'Ммррглглглгл? Глбмр брммрггл глмррбмргл.$B$BГрглгрлбрр мргл!!!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13353', 'ruRU', '', 'А сейчас держись-ка поближе к Артасу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13324', 'ruRU', 'Скоро, $n!$b$bСкоро ты почувствуешь вкус настоящей мощи. Скоро тебя ждет награда за преданную службу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13082', 'ruRU', 'Поторопись, $gбрат:сестра;. Жизнь покидает меня…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14240', 'ruRU', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Альянса, я смогу открыть портал для всех, кто хочет туда попасть.$B$BБитва уже началась. Поспеши в Ледяные Оковы и помоги нашим силам!', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Альянса, я смогу открыть портал для всех, кто хочет туда попасть.$B$BБитва уже началась. Поспеши в Ледяные Оковы и помоги нашим силам!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13804', 'ruRU', 'Добро пожаловать на Грозовую Гряду, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13611', 'ruRU', '<Разведчик Зиморожденных пытается подняться на ноги при вашем приближении, но падает обратно на землю, зажимая серьезную рану в боку.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13784', 'ruRU', '', 'Эти рудники – поистине страшное место. По счастью, я не собираюсь оставаться здесь надолго.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13777', 'ruRU', '', 'Посмотри на них… они заботятся лишь о том, как бы превзойти друг друга.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13691', 'ruRU', '', 'Этот маленький зеленый щенок был полностью бесполезен в шахтах. Ну да неважно, по крайней мере, это станет прекрасной разминкой перед битвой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13778', 'ruRU', '', 'Видишь ли, Торим не похож на этих гарпий. Он – достойное существо с телосложением, подобным богу.$B$BОн состоял на службе у титанов в качестве хранителя Ульдуара и Террасы Творцов.$B$BЭто было до тех пор, пока его жена, Сиф, не была коварно убита его братом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13779', 'ruRU', '', 'Торим потерял контроль над собой. Он внезапно напал на своих самых близких союзников, снежных великанов.$B$BЭто одна из причин, почему по сей день эту землю разрывает на части война между хильдами и Сынами Ходира… и почему Торим отправился в изгнание в Храм Штормов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13780', 'ruRU', '', 'Почему меня должно волновать, что эти волчицы и снежные великаны убивают друг друга каждый день?$B$BНет, нет… Я хочу, чтобы Торим сбросил свое оцепенение, объединился со своими союзниками и отбил Ульдуар.$B$BМир будет расколот пополам, если он не сможет этого сделать!$B$BИ, конечно же, я достойно тебя награжу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13660', 'ruRU', '', 'Я порву тебя в клочья.', '', 'Ты бросаешь мне вызов?', '', 'Ты не очень-то $gгрозен:грозная;.', '', 'Ты думаешь, у тебя хватит на это сил?', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13565', 'ruRU', '', 'Ледоклык вела нас вперед во множестве битв.$b$bПусть тебя не смущает то, что она не облачается в броню. Она самая сильная медведица из всех, кого я видела.', '', 'Не смей задерживать меня, девчонка!$b$bСкоро предстоит сражение!', '', 'Мы сражаемся ради славы Бруннхильдара!', '', 'Ледоклык проявляет великую отвагу, отправляясь в бой без брони.$b$bВозможно, ее пример вселит немного мужества и в тебя, $n.', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13785', 'ruRU', '', 'Медведи – прекрасные существа. Дикие, сильные, яростные в битве – они похожи на нас, сестра.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13843', 'ruRU', '', 'Мы служим Ториму, сестра.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13799', 'ruRU', 'Говори, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13801', 'ruRU', 'На какое-то мгновение мне показалось, что ты – это моя возлюбленная Сиф… эта прядь волос… но нет! Этого не может быть… Сиф мертва… мой брат… убил ее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13802', 'ruRU', 'Ульдуар… Я поклялся защищать Ульдуар.$B$BПоклялся защищать Террасу Творцов.$B$BПоклялся защищать соглашение, заключенное между расами холода.$B$BНо я не сделал этого! Я позволил моему брату обмануть меня. Я позволил ему обратить меня против моих союзников. Он обманул меня, обвиняя в смерти Сиф моего дорогого друга, Арнгрима, короля снежных великанов.$B$BКогда я понял, что я натворил… Я… впал в отчаяние.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13803', 'ruRU', 'Кто ты $gтакой:такая;, $gмаленький:маленькая;? Чтобы приходить и говорить такие слова могучему Ториму…$B$BМне следует… убить тебя!$B$BНо ты $gвывел:вывела; меня из оцепенения. Твое присутствие здесь – это знак!$B$BНе случайно я принял тебя за Сиф. Что бы она обо мне подумала? Я должен подготовиться к битве.$B$BЯ должен отомстить за ее смерть и освободить священные земли титанов от безумия моего брата!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14033', 'ruRU', '', 'Наши обычаи священны. Мы скорее умрем, чем оставим Торима и присоединимся к таким, как Король-лич!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13540', 'ruRU', '', 'Круг хильд священен. Кто бы ни победил, он проведет свою жизнь рядом с Торимом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13470', 'ruRU', '', 'Что ты делаешь, почему ты возвращаешься сюда? Тебе нужно отправиться назад, в Стратхольм, немедленно! Телепортировать тебя туда?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13747', 'ruRU', 'Тебе позволено посещать нашу крепость, $gмаленький:маленькая:r; |3-6($r).$b$bНе опозорь нас!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14209', 'ruRU', 'Что тебе, $Gмалыш:малышка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13697', 'ruRU', 'Наковальня Фьорна теперь стоит на видном месте в Дун Ниффелеме в качестве символа победы сыновей Ходира над Фьорном и его армией.$b$bВы улыбаетесь, осознавая, что именно вам суждено было оставить такой значительный отпечаток в истории этого мира.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13748', 'ruRU', 'Я недостаточно тебе доверяю, чтобы разговаривать о таких вещах.$b$bТы $gдолжен:должна; уважать те испытания, через которые пришлось пройти нашему народу, ту ношу, что нам пришлось вынести…$b$bВозможно, если бы ты $gсделал:сделала; нечто великое для Дун Ниффелема, я бы согласился поделиться с тобой знаниями моего народа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13685', 'ruRU', 'Эта копия рога Ходира находится в мемориале, посвященном королю Арнгриму и всем остальным, погибшим в битве при Громовержье.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13715', 'ruRU', '', 'Когда-то у меня был дом в густых лесах Кель\'Таласа. Когда-то у меня была семья, муж… дети. Когда-то я была единым целым с этим миром, живая. Король-лич забрал все это в мгновение ока.$B$BХотя у меня нет возможности нанести ему прямой удар, я отплачу ему за это сторицей!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14128', 'ruRU', 'Чем я могу тебе помочь?', 'Чем я могу тебе помочь?', 'Чем я могу тебе помочь?', 'Чем я могу тебе помочь?', 'Чем я могу тебе помочь?', 'Чем я могу тебе помочь?', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10043', 'ruRU', '', 'Эмиссар Талуун взял меня с собой, надеясь, что я смогу рассказать нашим новым союзникам о духах этого мира… но никто не будет слушать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13950', 'ruRU', '', 'Отлично сработано! Артас уже на пути в Нордскол!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13844', 'ruRU', '', 'Всегда приятно встречать таких героев, как ты, на этой забытой земле. Я могу тебе чем-нибудь помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14197', 'ruRU', 'Охота за сокровищами и немного доброго эля – что может быть лучше, верно, $gдружище:подруга;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12210', 'ruRU', '', 'Ты что, не видишь? Я работаю! Уходи, пока Ковальски не увидел.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7664', 'ruRU', 'Низина Арати когда-то была нашей. С твоей помощью мы можем ее вернуть. Поможешь ли ты нам, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13650', 'ruRU', 'Яд… мои внутренности горят…', 'Яд… мои внутренности горят…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13651', 'ruRU', 'Скажи, когда будешь $gготов:готова;. Я бы предпочел раньше, а не позже… если принять во внимание медленную смерть от яда и прочее.', 'Скажи, когда будешь $gготов:готова;. Я бы предпочла раньше, а не позже… если принять во внимание медленную смерть от яда и прочее.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13940', 'ruRU', 'Ах… слишком поздно, $gбрат:сестра;.$B$BМне… конец…', 'Ах… слишком поздно, $gбрат:сестра;.$B$BМне… конец…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9203', 'ruRU', 'Болотники пожирают мешочки со спорами! Надо их остановить!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9128', 'ruRU', '', 'Все сущее в природе объединено и связано, это называется экология. Если мы будем достаточно осторожны, чтобы не нарушить эту систему, и ограничимся наблюдением, нам, возможно, удастся воссоздать сложные взаимоотношения между окружающими нас живыми существами.$B$BА постигнув их, мы сможем сделать так, чтобы все эти существа жили в гармонии. Стоит лишь вмешаться в ход вещей, не обладая знанием, и беды не миновать.$B$BЯ прибыла сюда наблюдать за сложными взаимоотношениями спорлингов и болотников.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9265', 'ruRU', 'За последние 20 лет Телредор видел перемен больше, чем за несколько прошлых веков. Но я не жалуюсь, $N, ведь мир сейчас меняется куда быстрее, чем раньше.$B$BБлагодаря Свету я обрел таких друзей и компаньонов, с которыми никогда не познакомился бы, оставайся я отшельником. Узнай здесь все, что можешь, о своем прошлом. Это поможет тебе понять, куда ведет тебя твой путь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9370', 'ruRU', 'Никакой жук-переросток не выгонит меня из Зангартопи! Я покажу им, как связываться с Лигой Исследователей!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9264', 'ruRU', 'Никогда раньше я не видел столько разных грибов в одном месте. Если б у меня было достаточно времени, я бы точно смог найти им всем применение.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9121', 'ruRU', 'О, слава богам! Мы с Когтем спокойно исследовали это место, как вдруг на нас напал Владыка болота Мусел\'ек. Он может управлять животными и даже друидами!$b$bК несчастью, он застал друида в облике медведя и превратил в своего питомца. Я боюсь принимать облик кошки, иначе он поработит и меня тоже!$b$bДумаю, тебе придется сразиться с ним, чтобы спасти Когтя. Ступай на запад, мимо не пройдешь…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13786', 'ruRU', 'Извини, друг. Боюсь, мы уже пустили в ход все детали, что могли.', 'Извини, друг. Боюсь, мы уже пустили в ход все детали, что могли.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9751', 'ruRU', 'Эта старая дренейская доска объявлений, она восстановлена куренай, и теперь на ней вывешиваются объявления и портреты разыскиваемых за награду.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12658', 'ruRU', 'Приветик! Пилоты Выкрутня лучшие из лучших. Оставаться такими нам помогают постоянные тренировки. Они включают и прыжки с парашютом. Ты никогда не знаешь, когда тебе понадобится парашют, но ты знаешь, что однажды ты без всяких проблем сможешь им воспользоваться.$b$bКроме того, у нас есть портал телепортации, ведущий на вершину этого пика. Попав туда, спуститься можно только одним способом: прыгнуть с парашютом. На вершине сидит наш десантник, снабжающий парашютами.$b$bЕсли ты $g готов:готова; попробовать, |3-6($c), я подготовлю тебя для телепортации. После этого тебе надо будет просто вступить в портал.', 'Приветик! Пилоты Выкрутня лучшие из лучших. Оставаться такими нам помогают постоянные тренировки. Они включают и прыжки с парашютом. Ты никогда не знаешь, когда тебе понадобится парашют, но ты знаешь, что однажды ты без всяких проблем сможешь им воспользоваться.$b$bКроме того, у нас есть портал телепортации, ведущий на вершину этого пика. Попав туда, спуститься можно только одним способом: прыгнуть с парашютом. На вершине сидит наш десантник, снабжающий парашютами.$b$bЕсли ты $g готов:готова; попробовать, |3-6($c), я подготовлю тебя для телепортации. После этого тебе надо будет просто вступить в портал.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12687', 'ruRU', 'Добро пожаловать, |3-6($c)! Надеюсь, ты не боишься высоты!$b$bЯ приготовил для тебя тренировочный парашют. Эти парашюты используются для быстрых приземлений. Если ты хочешь аккуратно спланировать на землю, воспользуйся ездовым животным. Если ты хочешь острых ощущений и адреналина, просто упади. И в последний момент раскрой парашют. Вот так все и происходит. Быстрое падение! Ты $gготов:готова; испытать ощущения от быстрого полета?$b$bПарашют действует лишь несколько секунд. Так что тебе нужно все правильно рассчитать. Я не уверен, что ты справишься с этим, |3-6($c). Хотя я был бы рад ошибиться.', 'Добро пожаловать, |3-6($c)! Надеюсь, ты не боишься высоты!$b$bЯ приготовила для тебя тренировочный парашют. Эти парашюты используются для быстрых приземлений. Если ты хочешь аккуратно спланировать на землю, воспользуйся ездовым животным. Если ты хочешь острых ощущений и адреналина, просто упади. И в последний момент раскрой парашют. Вот так все и происходит. Быстрое падение! Ты $gготов:готова; испытать ощущения от быстрого полета?$b$bПарашют действует лишь несколько секунд. Так что тебе нужно все правильно рассчитать. Я не уверена, что ты справишься с этим, |3-6($c). Хотя я была бы рада ошибиться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8867', 'ruRU', 'Чего смотришь-то?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11714', 'ruRU', 'Добро пожаловать в мою таверну, усталый путешественник. Что я могу сделать для тебя?', 'Добро пожаловать в мою таверну, усталый путешественник. Что я могу сделать для тебя?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8378', 'ruRU', '', 'Приветствую вас, $gгосподин мой:госпожа моя;. Надеюсь, вы хорошо проводите время?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10193', 'ruRU', 'Я знаю местного алхимика. Мне кажется, его зовут Шэйнатрен. Когда я его видел последний раз, он направлялся на виллу Салтерила, наверняка, чтобы вступить в очередную смехотворную партию…', 'Я знаю местного алхимика. Мне кажется, его зовут Шэйнатрен. Когда я его видела в последний раз, он направлялся в Гавань Салтерила – наверняка, чтобы вступить в очередную смехотворную партию…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10200', 'ruRU', 'Кто лучше справится с составлением букетов для приема, чем ботаник Тиниаррель? Наверняка Салтерил расплачивается с ним вином…', 'Кто лучше справится с составлением букетов для приема, чем ботаник Тиниаррель? Наверняка Салтерил расплачивается с ним вином…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9323', 'ruRU', 'Повелительница небес Предзакатка и ее дракондоры находятся снаружи, к западу от Врат Пастыря.', 'Повелительница небес Предзакатка и ее дракондоры находятся снаружи, к западу от Врат Пастыря.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8445', 'ruRU', 'И как можно работать в таких условиях?! Да еще вокруг эти мерзкие Отрекшиеся! Хочу в Транквиллион и Кель\'Талас!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13078', 'ruRU', '<вздыхает>…$b$bМожет быть, поможешь чуток?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13083', 'ruRU', '<вздыхает>…Как ты думаешь, мы можем выдвигаться, |3-6($c)?$b$bЯ быстро теряю жидкость!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13076', 'ruRU', '$gГотов:Готова; ли ты пойти на все, чтобы защитить Лордерон?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14110', 'ruRU', 'Множество торговцев из \"Диковин и странностей\" в южной части Даларана продают аксессуары, реликвии, фамильные ценности и предметы в левую руку всех видов.', 'Множество торговцев из \"Диковин и странностей\" в южной части Даларана продают аксессуары, реликвии, фамильные ценности и предметы в левую руку всех видов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13988', 'ruRU', 'Лучший в Даларане центр неотложной помощи \"Спешим на помощь\" расположен в Торговой палате волшебников, в северо-западной части города.', 'Лучший в Даларане центр неотложной помощи \"Спешим на помощь\" расположен в Торговой палате волшебников, в северо-западной части города.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13991', 'ruRU', 'Учителя, обучающие кулинарному искусству, ждут тебя на кухнях при тавернах Альянса и Орды, находящихся в соответствующих кварталах.', 'Учителя, обучающие кулинарному искусству, ждут тебя на кухнях при тавернах Альянса и Орды, находящихся в соответствующих кварталах.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13431', 'ruRU', '', 'Это, конечно, не то, на что я рассчитывала, но… По крайней мере, тут тепло.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13288', 'ruRU', 'Все готово, можно начинать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12917', 'ruRU', 'Белгаристраз говорит правду, время на исходе!\r\n\r\nЯ могу наделить тебя способностью призвать бронзового дракона. Поговори с Белгаристразом или Вердисой, если ты предпочитаешь полагаться на силу красных или зеленых драконов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13256', 'ruRU', 'Янтарные драконы добились превосходных успехов в нанесении урона с помощью манипуляций со временем ради собственной выгоды.\r\n\r\nИспользуя Шок времени, они могут разрывать поток времени, окутывающий враждебную цель, нанося ей мгновенный урон. Сила этого заклинания резко увеличивается, если цель предварительно поражена шоковым разрядом.\r\n\r\nЕсли ему угрожают, янтарный дракон может ненадолго останавливать ход времени для своих врагов. Этот временной разрыв накапливает большое количество шоковой энергии на пораженных целях в виде шоковых разрядов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13466', 'ruRU', 'Второе кольцо защищает Варос Заоблачный Странник со своими стражами кольца. Твои драконы вполне способны справиться со стражами кольца, однако Варос скрывается за непроницаемым щитом, созданным энергией самого Окулуса. Щит поддерживается десятью центрифужными созданиями, установленными на кольце и платформах над ним. Уничтожь их – только тогда можно будет одолеть самого Вароса.', 'Второе кольцо защищает Варос Заоблачный Странник со своими стражами кольца. Твои драконы вполне способны справиться со стражами кольца, однако Варос скрывается за непроницаемым щитом, созданным энергией самого Окулуса. Щит поддерживается десятью центрифужными созданиями, установленными на кольце и платформах над ним. Уничтожь их – только тогда можно будет одолеть самого Вароса.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12916', 'ruRU', 'Варос Заоблачный Странник и его стражи охраняют второе кольцо. Твои драконы вполне способны справиться со стражами кольца, но вот сам Варос скрывается за неуязвимым щитом, создаваемым энергией самого Окулуса. Десять центрифужных созданий подпитывают щит с кольца и с платформ, находящихся выше. Уничтожь созданий, и Варос потеряет свою неуязвимость.\r\n\r\nЯ могу наделить тебя способностью призывать красного дракона. Если захочешь вместо этого прибегнуть к помощи бронзового или зеленого дракона, поговори с Этерносом или Вердисой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12915', 'ruRU', '', 'Белгаристраз говорит правду, ситуация на самом деле серьезная. \r\n\r\nЯ могу дать тебе силу призывать дракона из крыла зеленых драконов. Поговори с Белгаристразом или Этерносом, если ты хочешь получить силу, соответственно, красных или бронзовых драконов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13254', 'ruRU', 'Рубиновые драконы отличаются способностью защищать своих союзников, смягчая наносимый им урон.\r\n\r\nЗаклинание Жгучий гнев позволяет дракону выдыхать потоки жидкого огня, который перекидывается от цели к цели, с каждым разом нанося все больший и больший урон.\r\n\r\nЕсли противник наносит рубиновому дракону урон, дракон получает возможность пользоваться обходным маневром, то есть совершать ряд движений, позволяющих ему какое-то время уклоняться от вражеских ударов и заклинаний.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13269', 'ruRU', '', 'Белгаристраз говорит правду, ситуация на самом деле серьезная. \r\n\r\nТвоим драконам теперь доступна полная мощь! Используй ее, чтобы победить Эрегоса!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13259', 'ruRU', '', 'Обладающий всей своей силой изумрудный дракон может создать канал сна, который будет отдавать его собственную жизненную энергию дружественной цели, исцеляя ее. Отбирающий яд может быть использован для того, чтобы восполнить убыток здоровья, нанесенный при использовании этой способности.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('767', 'ruRU', 'Дурацкий снег… Эй, ты – $N? Я только что получил сообщение насчет тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('768', 'ruRU', 'Вас призывает Лаго Злобноверт, главный чернокнижник Стальгорна. Рекомендую вам немедленно отправиться к нему. Вы найдете его в Заброшенном гроте.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13258', 'ruRU', '', 'Изумрудные драконы умеют исцелять себя, одновременно отравляя своих врагов.\r\n\r\nИспользуя отбирающий яд, они могут красть жизненную силу своих врагов. Многократное использование этого яда может увеличить его эффективность.\r\n\r\nС помощью \"Прикосновения кошмара\" изумрудные драконы могут погружаться в Сон, превращая часть своей жизни в удары невероятно высокой силы, которые ослабляют их цель и на некоторое время снижают наносимый ею урон.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1303', 'ruRU', 'Значит, ты $gвернулся:вернулась;, чтобы помочь Кум\'ише, |3-6($r)? Не бойся, та боль, что ты чувствуешь внутри, – всего лишь признак того, что ты утрачиваешь силу воли.$B$BНо она тебе тут и не понадобится. А вот безделушки, которые я тебе дам в награду, понадобятся точно. Но сначала принеси мне нужную вещь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1874', 'ruRU', 'Ты далеко $gпродвинулся:продвинулась; $N. Но тебе еще многому надо научиться. Возможно, продолжать обучение тебе стоит в южных Степях…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13238', 'ruRU', 'Самое сложное еще впереди. Хранитель энергии Эрегос – это синий дракон, обладающей невероятной силой. Он может на время становиться неуязвимым для заклинаний огня, сил природы или тайной магии, которыми пользуются твои драконы. Он летает над самым верхним кольцом.\r\n\r\nТвои драконы теперь могут использовать всю свою силу. Они помогут тебе победить Эрегоса!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13255', 'ruRU', 'Рубиновый дракон в расцвете сил может применить заклятие Мученика, которое в течение короткого времени защищает все дружественные цели, перенаправляя всю враждебную магию на рубинового дракона. Во время действия заклинания могут быть использованы маневры уклонения.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13268', 'ruRU', 'Белгаристраз говорит правду: времени почти не осталось!\r\n\r\nТвои драконы могут теперь пользоваться всей своей силой. Вместе с ними ты $gдолжен:должна; победить Эрегоса!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13257', 'ruRU', 'Полный силы янтарный дракон может произвести временную аномалию, которая создаст пузырь наполненного энергией времени вокруг врага. Заклинания, проходящие через пузырь, причиняют двойной урон и могут наносить удар по цели шоковым разрядом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13304', 'ruRU', '', 'Титаны не за одну ночь жизнь создавали. Процесс был постепенным, требовались серьезные эксперименты и различные циклы повторений.$B$BИменно по этой причине существуют столь хорошо защищенные места, как низина Шолазар.$B$BЧтобы обеспечить безопасность своих экспериментальных площадок, титаны создали механизм защиты. Его разрушительная сила беспримерна, и я ничего не расскажу тебе до тех пор, пока ты не поклянешься в верности моему делу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13305', 'ruRU', '', 'Открывать тайны, подобные этой, смертному существу – граничит с кощунством, но у меня нет выбора.$B$BС тех самых пор, как Фрейя, дочь титанов, оставила меня здесь, у меня была лишь одна задача, и потерпеть неудачу я не могу. Жизнь нужно защитить любой ценой.$B$BЕсть спираль, связующая это место с другим, очень похожим. Именно там можно найти оружие, которое мы ищем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13376', 'ruRU', 'Я узнаю аса с первого взгляда. Еще раз спасибо за спасение моей жизни!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13534', 'ruRU', '', 'Подумай о всех страданиях, что причинил орден Алых за эти годы. Пытки, фанатичные изуверства, убийства.$B$BЯ не позволю этому продолжаться. Неужели же ты позволишь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13608', 'ruRU', 'Как успехи в войне против Короля-лича, |3-6($c)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13792', 'ruRU', '', 'Привет, ягодка моя, как хорошо, что ты здесь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13661', 'ruRU', '', 'Рада встрече, сестра по оружию. Я могу помочь тебе вернуть павших товарищей.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13663', 'ruRU', '', 'Идем, сестра по оружию.$B$BЕшь, пей – и сражайся с новой силой!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13654', 'ruRU', 'Мы должны непрерывно атаковать в Йотунхейме. Не дать им ни шанса перевести дыхание, никакой возможности поспать – и не брать пленных!$B$BТирион Фордринг со всей мощью своего Испепелителя и стоящего за ним Серебряного Авангарда может даже не надеяться совершить то, что делаешь ты!$B$BА теперь, давай быстро разберемся с твоими делами, $N. Для праздной болтовни тут слишком много всего происходит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13831', 'ruRU', 'Мрфррмрфрмррр!$B$B<Упырь прочищает горло.>$B$BДа?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13659', 'ruRU', 'Рыцари Черного Клинка в долгу перед тобой за твою помощь в захвате Мрачного Свода. Хорошо, что у нас есть ты.$B$BПока Альянс и Орда бьются друг с другом, делая за Короля-лича его работу, мы сосредоточимся на текущей задаче.$B$BГде Серебряный Авангард может заколебаться и начать раздумывать о том, как бы получше сразить врага, мы действуем без промедления и сомнений.$B$BТы станешь инструментом нашей мести, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13678', 'ruRU', '', 'Не мешай мне. Я наблюдаю за неумелыми попытками сражаться этих так называемых воинов.$B$BЯ с легкостью одержу победу над всеми ними, и тогда мне позволят служить самому Королю-личу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13947', 'ruRU', 'Мы атаковали под прикрытием сильной вьюги…$B$BПлеть мы застали врасплох… это была верная победа…$B$BА потом… сзади… словно ниоткуда, появились воины Орды.$B$BЭто стоило нам… всего…', 'Мы атаковали под прикрытием сильной вьюги…$B$BПлеть мы застали врасплох… это была верная победа…$B$BА потом… сзади… словно ниоткуда, появились воины Орды.$B$BЭто стоило нам… всего…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13948', 'ruRU', 'Постой, $gпаренек:девочка;…$B$BЕще одна вещь…', 'Постой, $gпаренек:девочка;…$B$BЕще одна вещь…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14035', 'ruRU', 'Прошу тебя, |3-6($r)!$b$bПожалуйста, добей меня…', 'Прошу тебя, |3-6($r)!$b$bПожалуйста, добей меня…', 'Не раздумывай, |3-6($c).$b$bПросто… просто сделай это!', 'Не раздумывай, |3-6($c).$b$bПросто… просто сделай это!', 'Передай моим родным…$b$bПеред смертью я думал только о них…', 'Передай моим родным…$b$bПеред смертью я думал только о них…', 'Добей меня… Умоляю…', 'Добей меня… Умоляю…', 'Сделай это, |3-6($c). И… и отомсти за меня!', 'Сделай это, |3-6($c). И… и отомсти за меня!', 'Прошу тебя, |3-6($r)… Не оставляй никого, кого Плеть могла бы воскресить!', 'Прошу тебя, |3-6($r)… Не оставляй никого, кого Плеть могла бы воскресить!', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14046', 'ruRU', '', 'Воздушный бой грохочет неподалеку, $n. И мы, и Орда отправили бомбардировщиков в зону бомбардировки и Долину Потерянной Надежды. Присоединишься ли ты к нам в этой битве?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14155', 'ruRU', '', 'Это был весьма занятой денек, да, $N? Может быть, ты хочешь немного отдохнуть и перевести дыхание, прежде чем вернуться назад?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13674', 'ruRU', 'Что ТЫ здесь делаешь?$B$BПо крайней мере постарайся не путаться под ногами. Я не позволю, чтобы Алый Натиск отвлекали такие типы, как ты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13701', 'ruRU', 'Ону\'зун пришел, чтобы вызвать на бой врайкула и утвердить себя в новом качестве.$B$BА потом, когда Ону\'зун будет сражаться в Валхаласе и победит, Ону\'зун станет служить Королю-личу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13695', 'ruRU', '', 'Эй, я тебя узнала! Я думаю, мне стоит поблагодарить тебя за то, что я здесь стою. Тот случай с телепортом был лучшим, что случилось в моей жизни.$B$BЕсли тебе случится вернуться назад в Борейскую тундру, передай моему занудному дубликату, что я немного преуспела в этом мире!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13526', 'ruRU', 'Хорошо. На твоем месте я задумался бы над тем, что ты сейчас услышишь, а не просто бездумно кивал головой в ответ на каждую реплику.$B$BКак и все хорошие истории, эта история начинается с предательства. О, не то чтобы нас предали… совсем нет. Король-лич возвысил нас, рыцарей смерти, над всеми другими.$B$BСкорее, это мы предали его, когда помогли верховному лорду Дариону Могрейну.$B$BВ битве за Часовню Последней Надежды в Чумных землях, находясь на волосок от уничтожения, Тирион Фордринг помог Могрейну разорвать узы Короля-лича, сковывавшие его и всех, кто был там.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13527', 'ruRU', 'И когда Король-лич в страхе перед Фордрингом, вооруженным Испепелителем, бежал с поля боя, мы легко смогли отбить Черный оплот у остатков войска Плети.$B$BТем не менее, это не разрешило проблем между нами и Орденом Алых.$B$BКак узнали Отрекшиеся на своем горьком опыте, Алые не видят разницы между нами и Плетью.$B$BНами движут собственные побуждения, мы руководствуемся своими целями, обладаем практически всеми свойствами живых организмов, но для них мы ничем не отличаемся от лишенных разума рабов Короля-лича.$B$BОни ошибаются, думая, что мы собираемся упасть ничком и притвориться мертвыми.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13528', 'ruRU', '<Лорд-командир пристально смотрит на вас, прежде чем продолжить.>$B$BЯ как раз к этому подхожу. В самом деле, когда верховный генерал Аббендис высадила свои войска в Нордсколе, она переименовала их в Алый Натиск.$B$BА незадолго до этого перед ней предстал адмирал Бареан Вествинд в Новом Дольном Очаге, что на Драконьем Погосте. Судя по всему, она этого не ожидала. И тем более не ожидала она, что он станет подрывать ее авторитет и выхватит бразды правления из ее рук.$B$BПохоже, ты $gбыл вовлечен:была вовлечена; в заговор против нее, но теперь это не важно. Как только она умерла, адмирал загадочно исчез, и мы потеряли его след.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13529', 'ruRU', 'Как я и сказал, мы потеряли его след, но есть кое-что, что, я уверен, ты сможешь выяснить для меня. Я подозреваю, что он залег где-то неподалеку, но мои недавно вернувшиеся шпионы не смогли обнаружить его присутствия нигде на этом острове.$B$BОтрекшиеся в Ядозлоби проделали прекрасную работу, саботировав Новый Дольный Очаг, но воины Алого Натиска, размещенные там, это закаленные в битвах ветераны. К сожалению, они неплохо экипировались по пути сюда, да еще и отобрали грифонов у каких-то глупцов.$B$BЯ пытаюсь сказать, что это не будет легкой прогулкой по деревне, $N, даже для тебя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13530', 'ruRU', 'Мы начнем через несколько секунд.$B$BЯ просто хочу, чтобы ты ясно $gпонял:поняла; меня – мы здесь не для того, чтобы тратить время на увещевания. Мы здесь для того, чтобы положить конец болезни, называющей себя Алым Натиском.$B$BЕсли ты не хочешь участвовать в этом, если не желаешь вступать в бой, улетай отсюда сей же час. Мне нужен тот, кто будет здесь до самого конца.$B$BТы с нами или нет?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13531', 'ruRU', '<Лорд-командир, смерив вас взглядом, награждает проницательной усмешкой.>$B$BВот это я и хотел от тебя услышать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14165', 'ruRU', 'Если тебя собьют на землю и захватят в плен, не говори им свое настоящее имя. Используй псевдоним.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14055', 'ruRU', 'Нас ждет нелегкий путь. Если ты $gуверен:уверена; в своих силах, держись поблизости, пока один из моих отрядов не соберется подниматься в Имирхейм. Им понадобится любая помощь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14052', 'ruRU', '', 'Чтоб мне пропасть! Тише, $N, тише! Ты что, хочешь, чтобы нас всех перебили?$b$bНу да, разумеется, я тебя знаю! Кто же в Альянсе не знает имени $N?$b$bКстати, как тебе мой прыгобот? Он сделан на основе мехагнома. Легкий, аэродинамичный!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13793', 'ruRU', 'Рад встрече, |3-6($c).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13733', 'ruRU', 'Где Зиморожденные, там и орлы. Они были нашими союзниками с тех времен, когда я еще не бродил по этой земле… а я не так уж и юн, $Gпарнишка:девчушка;.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13612', 'ruRU', 'Вряд ли, $Gдружище:подруга;. Я потерял слишком много крови… живым мне отсюда не выбраться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13613', 'ruRU', 'Стая гарпий поднялась из долины. Их вела одна большая, по имени Сирана… Мне было некуда бежать. Они давно не были столь агрессивны… Я не ожидал этого… Я не был готов к нападению… я сам виноват.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13614', 'ruRU', 'Уже поздно… дружище.$B$B<Разведчик из клана Зиморожденных кашляет кровью на снег.>$B$BСразись за меня… с этой дрянью…$B$B<Разведчик из клана Зиморожденных роняет голову на снег, делая свой последний вдох.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13642', 'ruRU', 'Только подумай, какие знания и какая мудрость заперты внутри Ульдуара. Там могут быть даже самые сокровенные тайны жизни!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14114', 'ruRU', 'Какого рода доспехи?', 'Какого рода доспехи?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14109', 'ruRU', '\"Танки – наше всё\" – самый популярный магазин в городе, торгующий латами и щитами. Он находится в северной части Даларана.', '\"Танки – наше всё\" – самый популярный магазин в городе, торгующий латами и щитами. Он находится в северной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13402', 'ruRU', 'Да, мой друг, у Финия есть то, что ты ищешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13430', 'ruRU', '<Кольтира стонет.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13485', 'ruRU', 'Здесь я отдал все во имя спасения своего отца. И вот, я пришел разрушить это место…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13905', 'ruRU', 'Единственная причина того, что жизнь еще не покинула тебя, заключается в моем любопытстве.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14056', 'ruRU', 'На \"Неустрашимом\" каким-то образом оказалось полным полно поломок, и мы не можем отправиться в Нордскол.', '', 'Все мы должны были отправиться в Нордскол, но неожиданно заметили на корабле массу зачумленных.', '', 'Прежде чем отправиться в Нордскол, нам нужно разобраться с проблемой чумы на борту \"Неустрашимого\".', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12641', 'ruRU', '', 'Не терпится попасть в Нордскол, да? Оснащенный мощным паровым двигателем \"Кракен\" отбывает отсюда в крепость Отваги, что в Борейской тундре.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12644', 'ruRU', 'Эй, ты! Скучаешь по Восточным королевствам, да? Ну, \"Кракен\" отплывает обратно в Штормград, если тебе действительно не терпится вернуться. Лично мне бы понравилась погодка потеплее здешней!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12637', 'ruRU', 'Большая морская черепаха Идущая-по-Волнам плавает от этой пристани до гавани Моа\'ки на Драконьем Погосте.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12664', 'ruRU', 'Вот откуда идут приказы для всех приверженцев культа. Их лидер должен скоро появиться.$B$BДавайте не упустим такой возможности.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8846', 'ruRU', 'Привет. Что привело тебя в лагерь Промозглого Ветра на этот раз?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3668', 'ruRU', '', 'Мое время истекает… наше общее время…', '', 'Мне еще столько всего нужно сделать, пока не пришла чума…', '', 'Джон, дорогой, ты где?', '', 'Что случилось? Мне так… холодно…', '', 'Тссс… Чуешь что-нибудь?', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8964', 'ruRU', 'Как продвигается твой поиск Знака?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3997', 'ruRU', 'Распространение чумы Плети практически остановлено. Еще несколько котлов – и мы победим! Только не забудь сделать свою часть дела.', 'Распространение чумы Плети практически остановлено. Еще несколько котлов – и мы победим! Только не забудь сделать свою часть дела.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4254', 'ruRU', 'Раса сама по себе не делает чести, |3-6($r). Пока ты у меня на ферме, я прошу тебя помнить об этом.$B$BЯ знал орков, которые не уступали в благородстве любому рыцарю, и людей, столь же злобных, сколь худшие из Плети.$B$BНо я не буду тебя утомлять историями моей юности! Тебя ждет много работы – если ты этого желаешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12789', 'ruRU', '', 'Мы никогда не забудем твою отвагу, $N. Тебя всегда будет ждать радушный прием у Стражей зимы.$B$BХочешь сделать эту таверну своим домом?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6933', 'ruRU', 'Ткань, из которой соткан наш мир, очень тонка. Чтобы разорвать ее, достаточно малой толики знания и умения. Вот так и появляются расщелины.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2727', 'ruRU', '', 'Добро пожаловать в таверну \"Угрюмый обжора\"!', '', 'Обязательно отведай эля Черного Железа. Говорят, что местные дворфы хлещут его без остановки.', '', 'Мой господин Штоппор Наливалс – хороший начальник. К тому же он много зарабатывает. Его карманы просто трещат по швам!', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('5834', 'ruRU', 'Братство Тория обладает знаниями, необходимыми, чтобы изготовить священные оружие и доспехи Повелителя Огня, но нам не хватает людей, чтобы добыть нужные для этого материалы.$B$BЯ, Локтос Зловещий Торговец, получил от братства полномочия обменять лучшие рецепты на материалы, собранные в Огненных Недрах.$B$BСкажи, что ты предлагаешь, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2973', 'ruRU', 'Не люблю разговоров.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2598', 'ruRU', 'Я больше никогда не прикоснусь к камню. Никогда не испытаю радость отливки металла…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2604', 'ruRU', 'Ты хочешь научиться древнему ремеслу? Ты хочешь плавить черное железо?$B$BПорадуй меня, |3-6($r). Принеси мне жертву, и я приму ее!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2601', 'ruRU', 'Наша судьба – предостережение всем тем, кто захочет иметь дело с Великим огнем.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7154', 'ruRU', 'На поверхности шара виден отпечаток большой ладони. Несмотря на то что шар явно часто и много использовали, он в довольно хорошем состоянии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14149', 'ruRU', 'Мы всегда рады видеть тебя здесь, друг и соратник всех драконьих племен. Отдохни здесь, в наших залах.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13502', 'ruRU', 'Наши склады опустошены. Мне нечего продать, пока не раздобудем припасов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12863', 'ruRU', 'Вступив в хороший клуб, ты можешь изменить всю свою жизнь.', 'Вступив в хороший клуб, ты можешь изменить всю свою жизнь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3488', 'ruRU', 'Да? Разумеется, я кузнец! А вы как думали, только дворфы могут по наковальне колотить? Ну и ладно! Вот пусть вам дворф и помогает!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13749', 'ruRU', 'Очень хорошо, кроха-|3-6($c).$b$bХотя тебе предстоит еще многое свершить, чтобы улучшить наши отношения с Торимом, я обязан поделиться с тобой одним секретом.$b$bОбратись ко мне снова, когда будешь $gготов:готова; увидеть то, что ты ищешь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14034', 'ruRU', 'Слишком долго терпел я своего брата. Пришла пора расставить все по местам. Пора уничтожить его и его приверженцев.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13800', 'ruRU', 'Я достаточно долго скрывался от своей судьбы. Теперь совершенно очевидно, что следует делать.$b$bЛокен должен пасть от моей руки!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13101', 'ruRU', 'Вы только посмотрите! Какое чудесное зрелище! Если бы только мои дорогие братья могли быть сейчас рядом. Они бы глазам своим не поверили! Трибунал стал хранилищем для дисков титанов! Они здесь, в этой самой комнате. Смотрите! Видите там, внизу диски? Вперед! Нас ждет величайшее открытие в истории дворфов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14182', 'ruRU', 'Распределитель УРВР выжидательно урчит.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14179', 'ruRU', 'Здравствуй, $gмаленький:маленькая:r; |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13457', 'ruRU', 'У тебя есть время? Су-ни научит тебя хорошей песне!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13452', 'ruRU', 'В Заиндевелой гавани все еще идет большое сражение.$B$BМожет, у тебя иногда найдется время помочь там?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13363', 'ruRU', 'Волосатые атакуют нас уже несколько дней. Великий камень молний, однако, по-прежнему в безопасности.$B$BСу-холу пришел к нам на помощь, как и много раз прежде… у волосатых нет ни единого шанса.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14083', 'ruRU', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Альянса, я смогу открыть портал для всех, кто хочет туда попасть.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Альянса, я смогу открыть портал для всех, кто хочет туда попасть.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13983', 'ruRU', 'Учителей горного дела и торговцев сопутствующими товарами можно найти в офисе сортировочной компании \"Все, что блестит\", расположенном в Торговой палате волшебников в северо-западной части Даларана.', 'Учителей горного дела и торговцев сопутствующими товарами можно найти в офисе сортировочной компании \"Все, что блестит\", расположенном в Торговой палате волшебников в северо-западной части Даларана.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6937', 'ruRU', 'Возможно, когда-нибудь ты так же прославишь Альянс, как и герои, которых ты $gвидел:видела; сегодня. Воздай им почести, $N.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14168', 'ruRU', '', 'Большинство драконов боится залетать сюда, |3-6($r).$b$bЧто же могло $gсмертного:смертную; привести в столь коварное место?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14169', 'ruRU', '', 'Достанет ли тебе смелости, чтобы бросить вызов самым могучим драконам Малигоса в их собственных владениях, |3-6($r)?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14170', 'ruRU', '', 'Мои драконы с нетерпением ждали твоего возвращения, |3-6($c).$b$b$GГотов:Готова; ли ты к полету?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2315', 'ruRU', 'Приветствую тебя, |3-6($r). Я Франклорн Искусник. Ты $gпришел:пришла;, чтобы узнать о Пике и Черном Железе?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13847', 'ruRU', '', 'Конечно! Ступай, да благословит тебя Элуна.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13607', 'ruRU', 'Ты впустую тратишь мое время. Иди давай, убивай прихвостней Короля-лича, |3-6($c)!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14204', 'ruRU', 'Быстрее, |3-6($c)!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14115', 'ruRU', '$GРешил:Решила; начать приносить пользу?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14118', 'ruRU', 'Тайные знания гоблинской инженерии ничего не рассказывают о том, что же можно сделать, чтобы вещи прекратили взрываться. Но зато в них сказано, как сделать так, чтоб они взрывались именно тогда, когда вам это нужно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14053', 'ruRU', 'Стоп-стоп-стоп. Потише, $N. Пожалуйста, тише!$B$B<Бубух оглядывается по сторонам.>$B$BМы же не хотим, чтобы эти гадкие врайкулы нас заметили, да?$B$BЧто ты думаешь о моем прыгоботе? Я его сделал на основе мехагнома. Классно получилось, а?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13930', 'ruRU', 'Угггх… не… не возись со мной.$B$BМне… уже не жить…', 'Угггх… не… не возись со мной.$B$BМне… уже не жить…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13931', 'ruRU', 'Собирали… информацию… о Плети.$B$BПоявились силы Альянса…$B$BМы затаились – ждали, пока они атакуют ворота… а потом ударили по ним с тыла.$B$BЗажатые между нами… и Плетью… они не выдержали…$B$B$gБраток:Сестричка;… Это было… славное дельце.', 'Собирали… информацию… о Плети.$B$BПоявились силы Альянса…$B$BМы затаились – ждали, пока они атакуют ворота… а потом ударили по ним с тыла.$B$BЗажатые между нами… и Плетью… они не выдержали…$B$BОх, $gбратик:сестричка;… Это было… славное дельце.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13935', 'ruRU', 'Я отдал все, что имел… во имя Орды…', 'Я отдала все, что имела… на благо Орды…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13906', 'ruRU', 'Кроваво-красный омут внутри глаза клубится, открывая образы огромной армии Плети. Глаз как будто бы начинает тянуться к вам, пробивая себе путь к вашему разуму и заставляя вас инстинктивно отпрянуть. В присутствии этого объекта вам крайне неуютно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13694', 'ruRU', '', 'Ты $gпришел:пришла; сюда, чтобы доказать, что ты $gдостоин:достойна; служить Королю-личу?$B$BЭто немного старомодно, не находишь? То, что приходится сражаться и выживать?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13782', 'ruRU', 'Оставь меня наедине с моим стыдом, |3-6($r).', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4173', 'ruRU', '', 'Серебряный Рассвет приветствует тебя, |3-6($c). Я Хасана – одна из нескольких доверенных интендантов Рассвета.$B$BСражаясь со злом от нашего имени, и заручаясь при этом нашим доверием и уважением, ты заработаешь у офицера Серебряных знаки доблести. Они служат двум целям. Благодаря им ты сможешь добиться нашего доброго отношения или обменять их на наши особые товары.$B$BЧем лучше к тебе будут относиться в Серебряном Рассвете, тем больше предметов будет тебе доступно.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4041', 'ruRU', '', 'Приветствую вас, $gюный господин:юная госпожа;. Я Ева Саркофф, а это мой муж Люсьен. Приятно с вами познакомиться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4042', 'ruRU', '', 'Разумеется, $gсэр:мисс;. Раньше мы служили в доме Баровых, в котором теперь Некроситет.$B$BЯ была старшей горничной, а Люсьен – дворецким господина Барова.$B$BМы работали на них многие десятилетия, пока…$B$B<Ева плачет.>', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4043', 'ruRU', '', 'О, это ужасная история, $gдобрый господин:добрая госпожа;. В поместье Баровых когда-то кипела жизнь… да, умели они жить. Они владели обширными землями…$B$BНо потом мы с Люсьеном заметили, что семейство Баровых приходит в упадок. Приступы страха и гнева часто охватывали и господина, и госпожу. По ночам мы слышали, как они ссорятся в своих покоях. Я думаю, что их погубила жадность.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4044', 'ruRU', '', 'Мы думаем, что Баровы заключили договор с могущественным человеком – магом по имени Кел\'Тузад, в надежде, что это поможет им сохранить свое состояние и землю и после смерти.$b$b*Ева вздрагивает.*$b$bИз этой сделки позднее появилась школа некромантии – место, которое Культ Проклятых сделал своей столицей.$b$bС каждой неделей дом Баровых ветшал все больше и больше…$b$bЕго комнаты заселили ужасные создания, и поддерживать дом в нормальном состоянии стало невозможно… Да никто бы и не стал заниматься столь отвратительным делом.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4045', 'ruRU', '', 'И куда идти? Мы ничего не знали и не могли узнать.$B$BВ конце концов, мы потеряли связь с семейством Баровых и оказались отрезаны от остальных слуг. Мы не знали, что произошло с ними, только слышали крики. Чудовищные крики.$B$BНам с Люсьеном некуда было идти, и мы спрятались в наших комнатах. Днем, когда в школе было относительно тихо, Люсьен тайком выбирался добыть еду и питье.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4046', 'ruRU', '', 'Мы прятались в доме шесть месяцев и видели, как пугающе менялось все вокруг. По всему особняку сновали мерзкие монстры. Темные служители культа наполнили все атрибутами своих ритуалов и жертвоприношений. Мы знали, что долго скрываться не сможем.$B$BКак и ожидалось, нас нашли и схватили. Затем нас раздели догола и посадили в круг из крови. Мы сидели там несколько дней, голые и напуганные.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4047', 'ruRU', '', 'Тюрьма? В этом мире есть вещи пострашнее кандалов и решеток, $gюный сэр:юная леди;. Нежить окружила нас и непрестанно мучила своими чудовищными деяниями.$B$BВ конце концов пришел он. Его имя – доктор Теолен Крастинов. Мы называли его \"Кровавый мясник\".', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4048', 'ruRU', '', 'Наконец-то выяснилось, что это были за крики. Мясник причинил нам такую боль, о которой ранее мы не могли и помыслить! Он ставил над нами бесчисленные эксперименты, чтобы вывести новую чуму.$b$bКаждый день тянулся как неделя… Мы бы умерли еще в первый день, но этот мерзавец поддерживал в нас жизнь с помощью магии.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4049', 'ruRU', '', 'Кровавый мясник все говорил о \"крови невинных\" и своем господине по имени Киртонос, которого он должен задобрить.$B$BНаконец, злодей завершил свои опыты. Из нас по капле вытекла вся жизнь. Дух наш был сломлен. Сладостные объятья смерти уже манили нас, и мы стремились к ним от всего сердца. Но в своей безграничной жестокости Кровавый мясник вновь вернул нас к жизни.$B$BОн решил заживо скормить нас вурдалакам. Как он смеялся, когда эти твари терзали нашу плоть…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4050', 'ruRU', '', 'Мы ничего не чувствуем. Наши души пребывают здесь вне времени… Мы не сможем освободиться, пока наши останки не будут найдены и упокоены.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3753', 'ruRU', 'Добро пожаловать на фронт, |3-6($c). Здесь нет места интригам и хитростям политиков. Здесь, в Бастионе, нам каждый день приходится бороться за жизнь.$B$BЕсли вы хотите совершить подвиг, то здесь найдете предостаточно возможностей для этого… особенно когда мы пойдем на Андорал и бросим вызов тем силам, что повелевают Плетью.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9983', 'ruRU', '', 'Очень хорошо, |3-6($c). Сейчас Орда как раз напала на Ущелье Песни Войны. Ты будешь сражаться там вместе с Альянсом?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12270', 'ruRU', '', 'Мы поклялись что есть сил защищать пассажиров \"Доблести\". Маршрут между Аубердином и Штормградской гаванью должен быть безопасным.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12262', 'ruRU', 'Команда \"Доблести\" сделает все, чтобы ваше путешествие было быстрым и приятным!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12264', 'ruRU', '', 'Добро пожаловать на борт \"Доблести\", $gсэр:мадам;. А теперь прошу меня извинить…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12263', 'ruRU', 'Извини, но нам придется поговорить как-нибудь в другой раз. Прощай.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12266', 'ruRU', '', 'Я почти ревную к нашему первому помощнику, Волнопеву. Подумать только, он, может быть, красивее меня!..', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13023', 'ruRU', '', 'Мы с Мистером Ушастиком отправились на прогулку, но заблудились, и я испугалась, потому что, понимаешь, там были волки, и они погнались за нами. Но потом я увидела эту башню, и мы с Мистером Ушастиком спрятались тут. Волки ушли, но я все еще боялась и не знала, как нам добраться домой…', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12089', 'ruRU', 'Как странно, |3-6($c). Я было подумал, что ты $gобнаружил:обнаружила; нечто, связанное с этими смертоносными кристаллами.$B$BНам обязательно надо найти способ снять защиту и уничтожить кристаллы. Прошу тебя, возвращайся и продолжай убивать существ Плети, пока не обнаружишь что-нибудь важное.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('11352', 'ruRU', 'День за днем я сижу здесь, готовлю боеприпасы и натягиваю луки.$B$BНадо понимать, тебе тоже что-то от меня надо?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13656', 'ruRU', 'Когда будешь $gготов:готова; я посажу тебя к себе на голову, и мы пойдем в самую гущу Плети в Зул\'Драк. Тебе нужно будет направлять меня, чтобы я громил и уничтожал наших врагов!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3756', 'ruRU', 'В этом мире у меня немного времени, но, волею Света, его хватит, чтобы исполнить предначертание. Когда ты встретишься с бароном Ривендером, я буду с тобой.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3757', 'ruRU', 'Оставь меня. Мои раны глубоки, и я боюсь, что смерть скоро настигнет меня. Спасибо тебе, $N. Я умру здесь, но благодаря тебе… я умру с миром.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13680', 'ruRU', 'Теперь ты чемпион, $gприятель:подруга;! Думаешь, у тебя есть все, что нужно, чтобы защитить твой титул?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13081', 'ruRU', '', 'Быстрее, |3-6($r).$b$bМое время истекает.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6929', 'ruRU', '*Вы не можете разобраться в механизмах, описанных в этом свитке.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13061', 'ruRU', 'Не приставай ко мне, |3-6($c)! У меня тут горячий цех – надо следить за тем, чтобы эти два котла с пивом не выкипели!$B$BЕсли вкус будет не слишком хорош, мастер Корен Худовар сварит меня там живьем!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13074', 'ruRU', 'В другой день я бы не стал этого делать, но… я хочу выпить в хорошей компании!$B$BДо дна!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13063', 'ruRU', 'Эх, хорошо пошла! Аж согревает изнутри! Кстати, дружище $N, я тебе говорил, что мне нравится твой стиль одежды?', '', 'О, да, $gсэр:мэм;, это отличный напиток! Можно сказать, превосходный!', '', 'Давай споем!$B$BЛя-ля,$B$Bля-ля!$B$BЧерное железо – это для меня!$B$BХа-ха. Нравишься ты мне, $N!', '', 'На чем это я остановился? Ах, да! Я сказал ей: \"Ты нужна мне! Это все, что мне отпущено знать!\"… а она рассмеялась и ускользнула прочь! Ах, $N, эти наги могут быть такими жестокими!', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2597', 'ruRU', 'Наш вождь Рок\'рел услышал этот вызов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13471', 'ruRU', '', 'Отыщите эти спрятанные ящики с загрязненным чумой зерном, а когда найдете – воздействуйте на них вашим разбивателем чар! Артас получит свидетельство того, что необходимо начать очищение Стратхольма.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9221', 'ruRU', '$N! Я слишком хорошо помню твое охотничье мастерство. Может, ты и не очень удачно $gначал:начала; в Тернистой долине, но серьезно $gвзялся:взялась; за дело и $gстал одним:стала одной; из лучших, когда $gодолел:одолела; старого Бангалаша.$B$BНо послушай, $gпарень:девица;, охотиться здесь, в Запределье, – серьезное дело. Мои шрамы – вот доказательство!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('9660', 'ruRU', 'Для тебя – все что угодно, $N! Я положил ключ к тебе в сумку.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3055', 'ruRU', 'Эй?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13267', 'ruRU', 'Круги наверху обороняют самые могущественные слуги Малигоса. В одиночку не стоит и надеяться их одолеть. Мы можем даровать тебе власть призывать драконов из наших выводков. Оседлав дракона, ты сможешь прилететь сюда, в Окулус, и повелевать могущественной магией драконов.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10423', 'ruRU', 'Ну да, типа того… добро пожаловать на мою станцию.$B$BИтак, что ты думаешь? Тут неплохо все обустроили, ведь правда?$B$BВ любом случае я очень рад, что мы избавились от этих скучных эльфов из Сильванаара.$B$BНайди, чем тут заняться, мы здесь проводим массу экспериментов, и почему бы тебе не помочь с ними.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('10889', 'ruRU', '', 'Если ищешь всякие штуки, штучки, штуковинки, штукенции, прибамбасы, навороты и загогулины, то ты $gпришел:пришла; по адресу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3984', 'ruRU', 'Шестнадцать с половиной в год – слишком мало за такую работу.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6877', 'ruRU', 'Хочешь поиграть, вонючка?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6878', 'ruRU', 'Храню я ключ к земле свободной! Ступай за мной, и ты увидишь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6879', 'ruRU', 'Все еще живы? Вы меня удивляете. Смотрите, как я бегу!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6880', 'ruRU', 'Повеселее лицо сделай!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6881', 'ruRU', 'Все, кончен бал, погасли свечи!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14071', 'ruRU', 'Мои парни готовы. Пустим немного врайкульской крови!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13921', 'ruRU', '', 'Ты еще не $gдоказал:доказала;, что $gдостоин:достойна; бросать вызов в Валхаласе, $N.$b$bВозвращайся, когда сумеешь прославить свое имя!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14076', 'ruRU', 'Поскольку ты $gсведущ:сведуща; в портняжном деле, могу тебе сказать, что мне известны кое-какие особые выкройки. Например тончайшего плаща и плаща смертельной стужи.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14084', 'ruRU', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Орды, я смогу открыть портал для всех, кто хочет туда попасть.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', 'Приветствую тебя, |3-6($c).$B$BКогда Крепость Ледяных Оков будет находиться под контролем Орды, я смогу открыть портал для всех, кто хочет туда попасть.$B$BСледующая битва начинается через $3975w:$3976w$3782w:$3784w$3785w.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14063', 'ruRU', 'Я могу указать тебе путь, $gюноша:девушка;?', 'Я могу указать тебе путь, $gюноша:девушка;?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13759', 'ruRU', 'Привет тебе, |3-6($r)! Чем могу помочь?', 'Привет тебе, |3-6($r)! Чем могу помочь?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12943', 'ruRU', 'Слушай, |3-6($r).$b$bЕсли ты ищешь крошшер, на котором можно прокатиться, тебе нужно идти в холмы к северу отсюда.$b$bТам есть один списанный.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14221', 'ruRU', 'Лок\'тар! Ты желаешь послужить Орде на поле битвы?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12944', 'ruRU', 'Сначала мы их отремонтировали, чтобы валить тут лес, но с тех пор, как сюда прибыли войска Альянса и развязалась битва, мы используем их в качестве военной техники.$b$bПоверь мне, |3-6($c). Если бы у тебя был ключ, было бы безумием его не использовать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12838', 'ruRU', 'Приветствую, |3-6($c).$b$bХочешь, чтобы Даррок отправил тебя в Бухту торговцев?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12945', 'ruRU', 'Они у Грекка.$b$bВозможно, он поделится с тобой, если ты его выручишь. В следующий раз, когда будешь на севере, на Лесозаготовках Синего Неба, обязательно поговори с ним.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14137', 'ruRU', '', 'Мы уже потеряли несколько хороших воинов. Я начинаю сомневаться в том, что это место стоит нашего внимания.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7639', 'ruRU', 'Спасибо за то, что вернули мою счастливую рулетку, $N! Клянусь, с ней я ловлю огромных рыб!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7640', 'ruRU', 'Чтобы поймать Газ\'ранку, нужна наживка на грязнотинников. Я сам такие делаю!$B$BНо одной наживкой тут не обойдешься – нужны и сами грязнотинники… Положи их в приманку, и она пропитается этим мерзким запахом.$B$BКогда управишься с грязнотинниками, бросай наживку со стоянки Пэгла в Зул\'Гурубе, бери бутыль и жди… Только выбирай бутыль поменьше – с такой приманкой Газ\'ранка вряд ли заставит себя ждать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13512', 'ruRU', 'Благодарение Вечному Свету и тебе, $N! Отныне Братство Справедливости больше не грозит нам. $B$BОднако отсюда до Западного Края долгий путь – и, если мы не победим в этом бою, нам, быть может, некуда будет вернуться.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14112', 'ruRU', '\"Нити судьбы\", что в западной части Даларана, – самый популярный магазин в городе, торгующий одеждой и доспехами из ткани.', '\"Нити судьбы\", что в западной части Даларана, – самый популярный магазин в городе, торгующий одеждой и доспехами из ткани.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14113', 'ruRU', 'Какого рода оружие?', 'Какого рода оружие?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14098', 'ruRU', 'В \"Полном арсенале\", что в юго-восточной части Даларана, продаются все виды оружия ближнего боя.', 'В \"Полном арсенале\", что в юго-восточной части Даларана, продаются все виды оружия ближнего боя.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14104', 'ruRU', 'Если тебе нужны реагенты и тому подобные товары, отправляйся в \"Ведовские сестрички\", что в западной части Даларана.$B$BТолько смотри, остерегайся черного кота семейки Мурхед!..', 'Если тебе нужны реагенты и тому подобные товары, отправляйся в \"Ведовские сестрички\", что в западной части Даларана.$B$BТолько смотри, остерегайся черного кота семейки Мурхед!..', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14074', 'ruRU', 'В разных темных местах побывал я и научился шить мертвенно-хладный плащ.$B$BЭтот эпический плащ для заклинателей увеличивает силу заклинаний, скорость их прочтения и вероятность нанесения критического урона. Я ищу достойного преемника, которого я мог бы обучить шитью этого плаща.$B$BТот, кто выполнил большинство заданий по всему Нордсколу и достиг звания \"Хранитель мудрости Нордскола\", будет самым подходящим кандидатом для того, чтобы научиться выкройке мертвенно-хладного плаща.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13682', 'ruRU', 'Вернись к работе, пока нас не заметил надсмотрщик.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13740', 'ruRU', 'Мы рады приветствовать тебя – друга и союзника земельников.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14203', 'ruRU', 'Молоты земельников, как один, на стороне Глыбоскала. Мы будем сражаться с прихвостнями Локена до конца!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13703', 'ruRU', 'Приветствую тебя, $N. Это – мастерская Хранителя Мимира.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13919', 'ruRU', 'Мне следовало бы убить тебя на месте, |3-6($r)!$b$bВыкладывай то, что собираешься сказать, и убирайся прочь!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13869', 'ruRU', 'Что-то в глубине ока будто бы затягивает вас внутрь, и у вас начинает кружиться голова. Вы видите, как в этой бездне клубится древняя магия, которая так и манит окунуться в нее и вечно падать сквозь бесконечность…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14176', 'ruRU', 'Мы это сделали! Ты можешь поверить своим ушам? Это просто невероятно!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14038', 'ruRU', 'Главная слабость Артаса – это его гордыня. И эта слабость растет вместе с его силой. Черный Клинок получит шанс нанести удар бывшему принцу – в этом я не сомневаюсь.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14160', 'ruRU', 'Вот и ты. $GГотов:Готова;? Когда мы начнем, пути назад уже не будет.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('14037', 'ruRU', 'Просто поверить не могу, что Культ был столь беспечен, что оставил их у себя за спиной!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13472', 'ruRU', '', 'Что ты здесь делаешь? Артас находится у входа в Стратхольм, и ты $gдолжен:должна; быть рядом с ним! Ступай!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('8354', 'ruRU', '', 'Какими бы беспомощными идеалистами ни были ребята из Серебряного Рассвета, даже их не самый умный лидер понял, что враг превосходит их количеством. Я приехала сюда в качестве посланницы Алого ордена. Скорее даже дипломата… Сомневаюсь, что мы сможем нанести какой-либо вред армиям Кел\'Тузада – объединенным или нет, – но мы сможем сдерживать их достаточно долго для того, чтобы произошло какое-нибудь чудо.$B$BЧто же касается меня, то я намерена умереть в бою…$B$B*Маржан пожимает плечами.*', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4447', 'ruRU', 'Ты не $gсосредоточен:сосредоточена;. Ты что-то от меня скрываешь?', '', 'Я чувствую вокруг вас какое-то беспокойство…', '', 'Твое будущее сокрыто. Сосредоточься и спроси еще раз.', '', 'Не похожи вы на студента…', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('13881', 'ruRU', 'Найди Катарину Стэнфорд на берегу канала к юго-западу от тюрьмы.', 'Найди Катарину Стэнфорд на берегу канала к юго-западу от тюрьмы.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7074', 'ruRU', 'Эх! Нет ничего лучше, чем смотреть, как народ дерется на арене! Если придете сюда в нужное время, может, тоже сможете подраться!', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1121', 'ruRU', 'Здорово у тебя получается, $N. Твои клинки и молоты – просто загляденье, даже лучше моих собственных. Пожалуй, только один мастер умеет ковать лучше – Боргус Сталерук. Не хочешь поучиться у него?', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1122', 'ruRU', 'Хочешь учиться у Боргуса? Ну, я скажу тебе, где его найти. Отправляйся через площадь на северо-запад от моей кузницы, а затем – по западной дороге. И найдешь его во втором здании справа.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12275', 'ruRU', 'Добро пожаловать на борт переправы Оперенной Луны. Мы ходим между крепостью и Забытым берегом, разыскивая тех, кто пропал в море. Для того, кто не готов, море может быть очень опасным.$b$bЕсли хочешь подготовиться как следует, поговори, пожалуйста, с нашими поставщиками. Они с огромной радостью тебе помогут.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12244', 'ruRU', 'Прошу извинить меня, |3-6($c), но мне нужно работать.', 'Прошу извинить меня, |3-6($c), но мне нужно работать.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('7116', 'ruRU', 'Ну, привет еще раз, $N! Уж не знаю, сколько я еще буду возиться с этой лавочкой у черта на рогах… Но, как бы то ни было, ты мне $gпомог:помогла;. Покажу-ка я тебе кое-что интересное.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12245', 'ruRU', 'Море унесло очень много жизней. Мы с духами разыскиваем тела погибших.', 'Море унесло очень много жизней. Мы с духами разыскиваем тела погибших.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('1136', 'ruRU', '$N, верно? Замечательная работа. Я возьму тебя к себе и выучу делать такие доспехи, чтобы и самому королю Магни носить не стыдно было.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('12276', 'ruRU', 'От имени команды \"Лунной пыли\" я приветствую тебя, |3-6($c). Надеюсь, ты воспользуешься услугами наших поставщиков продукции прежде, чем отправиться с нами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2316', 'ruRU', 'Выбора у меня не было – по крайней мере, я так думал. Я должен был оставаться с королем и народом несмотря на падение Тауриссана.$B$BПодчиняясь Рагнаросу, король Дагран Тауриссан вырвал сердце горы и воздвиг Пик Черной горы. Мы же неустанно трудились, возводя город, по своей мощи превосходящий все крепости прошлого… и будущего.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2317', 'ruRU', 'Я просто стоял и смотрел, как скверна Рагнароса просачивается через сердца и умы нации Черного Железа. Эта когда-то почтенная раса, доведенная до безумия… до зла.$B$BКогда Шпиль был построен, моя воля к жизни среди моего народа иссякла. Я умер как герой новой нации Черного Железа. Могилу устроили здесь – у входа в город, который я спланировал и построил, – в честь моих усилий.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2318', 'ruRU', 'Мой великий молот Железный Друг достался новому главному архитектору Финию Темнострою – амбициозной и коварной бездари. $B$BЗдесь-то ты и поможешь мне, $N. Я сам пытался остановить его. Ему самому не под силу совладать с Железным Другом, но если у него появится достаточно сильный союзник, их творения погубят весь мир.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2600', 'ruRU', '', 'Для вас проход закрыт. Вы не из наших, да и вызов вы нам не бросали.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2850', 'ruRU', 'Останки покрыты червями и мухами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('2997', 'ruRU', 'Да, редкая находка… $B$BМатредис кивает. $B$BДля продолжения мне потребуются ингредиенты.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3938', 'ruRU', 'Вы достигли исключительных успехов в кузнечном деле, $N, и вам пора принять решение – стать оружейником или бронником.$b$bНо помните, что сделав выбор, вы не сможете отказаться от него. Каждая профессия требует жесткой дисциплины и не оставляет места ничему другому.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6255', 'ruRU', 'Голодный солдат – плохой солдат. Чтобы одержать победу, необходимо обеспечить армию едой и припасами.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6733', 'ruRU', 'Припасов у нас почти нет. Придется хорошенько поработать, прежде чем мы сможем отправить штурмовой отряд на Поле брани.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6073', 'ruRU', 'У нас не хватает ресурсов на усовершенствование войск.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('6316', 'ruRU', '', 'Наша кавалерия нуждается в знаменитых верховых баранах Альтерака!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3999', 'ruRU', 'Мы побеждаем Плеть – измененная чума будет действовать на нежить как минимум еще один день! Но чтобы закрепить эффект, нужно как можно скорее вывести из строя следующий котел!', 'Мы побеждаем Плеть – измененная чума будет действовать на нежить как минимум еще один день! Но чтобы закрепить эффект, нужно как можно скорее вывести из строя следующий котел!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('3981', 'ruRU', 'Исходя из принесенного тобой образца яда я полагаю, что тебе потребуется следующее: чародейский ускоритель, пять соматических усилителей и четыре отреза рунической ткани. С помощью стеклянного фокусирующего устройства добудь усилители из плоти зомби или вурдалаков Чумных земель.$B$BКак только все это будет у тебя, ты сможешь залить противоядие в этот котел.', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `npc_text_localized` VALUES ('4401', 'ruRU', 'Если покажешь себя надежным союзником, я, может быть, и возьмусь тебя обучать…', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
