INSERT INTO `quests_localized` VALUES ('997', 'ruRU', 'Земля Деналана', 'Так ты идешь на юг? К озеру Аль\'Амет? Если да, то не выполнишь ли ты одну мою просьбу...$B$BМой собрат Деналан стоит лагерем у восточной оконечности озера, где изучает жизнь растений Тельдрассила и проводит свои эксперименты. Ему нужна редкая земля из Дарнаса, но ее доставили в Доланаар с опозданием – только что.$B$BНе $gмог бы:могла бы; ты отнести ему эту землю?', 'Отнесите горсть редкой земли Деналану к озеру Аль\'Амет.', 'Наконец-то! Я уже давно жду эту редкую землю. Надеюсь, она еще свежая...$B$BСпасибо тебе за доставку, $N. Ты – |3-6($r), не $Gжалеющий:жалеющая; времени на благие дела!', 'Ты что-то мне $Gпринес:принесла;?', '', '', '', '', '');
