INSERT INTO `quests_localized` VALUES ('1282', 'ruRU', 'Зовут его Улыбка Джим', 'Ничего не могу с собой поделать, мне так жаль старину Джима... Он не всегда был таким... Когда-то он держал таверну на болотах, дивное было местечко.$b$bСлушай, я не в настроении говорить об этом. Если тебе нужна информация, поговори с капитаном Ваймсом. Он сидит на верхнем этаже в Цитадели, знаешь, эти большие казармы, к востоку от башни леди Джайны.', 'Поговорите с капитаном Гарраном Ваймсом на верхнем этаже Цитадели.', '', '', '', '', '', '', '');
