INSERT INTO `quests_localized` VALUES ('1284', 'ruRU', 'Подозрительные следы копыт', 'Цепочка следов уходит от все еще дымящегося остова таверны \"Последний привал\". В отличие от других следов, еле заметных на мягкой болотистой почве, эти видны отчетливо.', 'Доложите о подозрительных следах возле таверны \"Последний привал\" капитану Гаррану Ваймсу из Терамора.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('1285', 'ruRU', 'Солдаты Даэлина', 'Реддон... Редпат... А! Вот он! Лейтененат Павал Рит.$b$bВсупил в ряды тераморской стражи вместе с остальными новобранцами, которые пришли из-за моря вместе с леди Праудмур. Зачислен в морские пехотинцы Кул-Тираса в возрасте шестнадцати лет. Отличная служба, поощрения...$b$bЧислится пропавшим без вести.', 'Вернитесь с информацией о Рите к капитану Гаррану Ваймсу в Терамор.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('1286', 'ruRU', 'Дезертиры', 'Удержаться здесь, на болотах, было нелегко. Но, благодаря решимости и отваге, мы смогли возвести несколько оборонительных сторожевых башен в округе.$b$bПо последним донесениям наших разведчиков, группа дезертиров засела в Разрушенной башне, к юго-востоку от таверны. $B$BЗа главного у них, кажется, Балос Хакен. Посмотрим, может удастся получить от него какие-то сведения. $B$BНо будь с ним осторожнее, $N, он – тертый вояка.', 'Расспросите о Рите Балоса Хакена и его дезертиров в Пылевых топях.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('1287', 'ruRU', 'Дезертиры', 'Рит? Этот трус?$b$bМы вышвырнули его из лагеря, потому что он настаивал на налете на деревню огров Гиблотопь, чтобы пограбить их запасы. Нас не так много, чтобы рассчитывать справиться с этими безмозглыми тварями. $b$bОн настолько часто зарывался, что мы решили, что с нас хватит, и прогнали его.$b$bВот, я помог тебе, почему бы теперь тебе не забыть о нашей встрече?', 'Поговорите с капитаном Гарраном Ваймсом в Тераморе.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('1288', 'ruRU', 'Донесение Ваймса', 'Хорошая работа, $n. Для любителя просто отличная. $b$bЛеди Праудмур просила, чтобы я держал ее в курсе, но я некоторое время не имел возможности с ней пообщаться.$b$bЯ собирался отправить ей рапорт позже, – но мне кажется уместным вместо этого послать к ней тебя. Ты куда лучше меня ответишь на все ее вопросы.', 'Отнесите донесение Ваймса леди Джайне Праудмур в Терамор.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('1289', 'ruRU', '<nyi> Доклад Ваймса', 'Итак, судя по докладу капитана Ваймса, ты весьма $gпомог:помогла; нам в этом расследовании, благодарю тебя.$b$bЯ с чистой совестью могу оставить дальнейшую судьбу этого дела в таких надежных руках. Поэтому прошу тебя вернуться к капитану Ваймсу и помочь ему в раскрытии этого преступления.', 'Вернитесь к капитану Ваймсу в Терамор.', '', '', '', '', '', '', '');
