INSERT INTO `quests_localized` VALUES ('11028', 'ruRU', 'Оставшееся время', 'В этом альманахе предсказано возвращение Терокка, самого могущественного и ужасного короля Скеттиса в истории араккоа.$B$BЯ всегда считал, что это только легенда, $N, однако возвращение жрецов когтя и вот этот том утверждают обратное.$B$BВозвращение Терокка близится с каждым днем, и наш единственный шанс – выдернуть его в этот мир неподготовленным. Возвращайтесь в Скеттис и поговорите с Адарисом – будем надеяться, у него хватит мудрости, чтобы не ошибиться в выборе.', 'Расскажите небесному капитану Адарису о возвращении Терокка. Вы сможете найти его в лагере Черного Ветра в лесу Тероккар.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('11029', 'ruRU', 'Потрепанная маскировка', 'Открывайте, $N. Это маскировочный костюм. Выглядит не очень-то убедительно, да?$B$BЯ шью их для астральных контрабандистов, которые ищут редкие книги. В центре Скеттиса, в местечке под названием Покой Терокка, живет писатель араккоа – совсем старый и почти слепой.$B$BНаденьте этот костюм и постарайтесь выманить у него книгу \"Кровная вражда\". Но будьте осторожны – других араккоа этот костюм не обманет.', 'Оденьтесь в потрепанный маскировочный костюм араккоа, возьмите книгу \"Кровная вражда\" у Сахаака и вернитесь к Хаззику.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('11030', 'ruRU', 'Наш мальчик хочет быть следопытом Стражи Небес', '$C, у нас есть одна проблема – у нас много сыновей. И поскольку рядом обосновалась Стража Небес, наши сыновья только и говорят, что хотят вступить в ее ряды.$B$BБудучи гордым отцом, мы хотим для них самого лушего. Потому не сходишь ли ты к хрустальной кузнице Скверны в Лагерь Легиона на север или юг, чтобы добыть магический настой для наших сыновей? Иди на зеленый дым – и найдешь лагерь.$B$BМы думаем, что на настой пойдет десять осколков апексита. Мы бы их тебе дали, да у нас нету.', 'Добудьте нестабильный настой Зверя.', '', '', '', '', '', '', '');
