INSERT INTO `quests_localized` VALUES ('11107', 'ruRU', 'Склонитесь перед верховным господином', 'Я недостоин даже принести вам это известие, $Gсэр:мадам;, умоляю о снисхождении. Властитель Мор\'гор хочет видеть вас у себя.', 'Поговорите с властителем Мор\'гором в лагере Драконьей Пасти в Долине Призрачной Луны.', '', '', '', '', '', '', '');
INSERT INTO `quests_localized` VALUES ('11108', 'ruRU', 'Владыка Иллидан Ярость Бури', 'Пойдем, сам Владыка Иллидан ждет нас!', 'Встретьтесь с Иллиданом Яростью Бури. Найдите Бартамуса в Нижнем Городе Шаттрата, если останетесь в живых.', '', '', 'Встреча с Иллиданом Яростью Бури.', '', '', '', '');
