INSERT INTO `quests_localized` VALUES ('11403', 'ruRU', 'Наконец, свободен!', 'Гнев был моей темницей, $N, а ты $Gосвободил:освободила; меня. Хотя я считал себя правым, душа моя ныне кровоточит от всех тех ужасов и смертей, которые обрушил я на жителей Азерота.$B$BБлагодарю тебя, что ты $gпривел:привела; меня к Свету. Да не сверну я с этого пути!$B$BОтнеси этот потрепанный молитвенник попечительнице сирот, чтобы дети узнали, что больше я их не потревожу.', 'Возьмите книгу Томаса Томсона и отнесите ее попечительнице сирот в маске в Дуротар, Брилл или на Площадь Соколиных Крыльев.', '', '', '', '', '', '', '');
